head.script:

/**
 * 화면 초기 로딩시 수행 스크립트 
 */
scwin.onpageload = function() {
	console.log("[/index_tabControl.xml] [onpageload()] ==> [TEST_01] [tabControl 로딩...@@@]");

	scwin.initMainLoad();
};

scwin.onpageunload = function() {

};

scwin.tac_layout_onbeforeselect = function(selectedIndex, index) {
	console.log("[/index_tabControl.xml] [tac_layout_onbeforeselect()] ==> [TEST_01] [selectedIndex]"+ selectedIndex +"[index]"+ index );

	try {
		//자체 개발 (반응형 탭에서 현재 열린 창 닫는 코딩)
		var isAdaptive = parseInt(this.getStyle("width"), 10) < 1024
				? true
				: false;
		if (isAdaptive) {
			if (selectedIndex == index) {
				//현재 선택된 탭이 선택될 경우 접는 동작.
				var classNm = "w2tabcontrol_contents_wrapper_selected";
				var selectTac = this.getFrame(selectedIndex);
				if (selectTac
						.hasClass("w2tabcontrol_contents_wrapper_selected")) {
					setTimeout(
							function(comp) {
								comp
										.removeClass("w2tabcontrol_contents_wrapper_selected");
								$("#" + comp.id)
										.prev()
										.removeClass(
												"w2tabcontrol_contents_bridge_selected");
							}, 200, selectTac);
					return;
				}
			}
			//새로운 탭을 선택 할 경우 현재 탭을 최상위로 스크롤 이동.
			var moveHeight = 0;
			for (var i = 0; i < index; i++) {
				moveHeight += parseFloat($("#" + this.getFrame(i).id)
						.prev().css("height"));
			}
			setTimeout(function(contentId, height) {
				$("#" + contentId).scrollTop(height);
			}, 200, this.parentControl.id, moveHeight);
		}
		//코딩 끝
	} catch (e) {
	}
};

scwin.tac_layout_ondrawcomplete = function(tabID, idx, userID) {
	console.log("[/index_tabControl.xml] [tac_layout_ondrawcomplete()] ==> [TEST_01] [tabID]"+ tabID +"[idx]"+ idx +"[userID]"+ userID );

	var isAdaptive = parseInt(this.getStyle("width"), 10) < 1024
			? true
			: false;
	//반응형일때 탭 생성시 탭이 접혀있는 상태로 표현됨. 이때 강제로 펼치도록 설정.
	if (isAdaptive) {
		this.getFrame(idx).addClass(
				"w2tabcontrol_contents_wrapper_selected");
	}
};

/**
 * TabControl의 모든 메뉴 닫기 버튼 이벤트
 * @lastUpdate 2016.08.28
 * @author InswaveSystems
 * @since 2016.08.28
 */
scwin.btn_closeAll_onclick = function() {
	var cnt = tac_layout.getTabCount();
	for (var i = cnt; i > 0; i--) {
		tac_layout.deleteTab(i);
	}
};
