head.script:
/**
 * 화면 초기 로딩시 수행 스크립트	==> 	// 로그인 정보 ==> 100001, 1234 
 */
scwin.onpageload = function() {
	console.log("[/login.xml] [onpageload()] ==> [TEST_01] [로그인  화면 로딩...@@@]");

	///////////////////// url 숨기기 - w2xPath 이하 경로 제거에서 사용/////////////////////////////////
	var initUrl = location.href;

	if (initUrl.indexOf("#") > -1) {
		com.goHome();
	}
	///////////////////////////////////////////////////////////////////////////////////////////////////

	ibx_empCd.focus();
};

scwin.onpageunload = function() {
};

/**
 * 아이디 입력 후 Keyup 이벤트 발생 시 실행
 */
scwin.ibx_empCd_onkeyup = function(e) {
	if (e.keyCode == 13) {
		sct_password.focus();
	}
};

/**
 * 패스워드 입력 후 Keyup 이벤트 발생 시 실행 - 로그인 트랜잭션 발생
 */
scwin.sct_password_onkeyup = function(e) {
	if (e.keyCode == 13) {
		scwin.btn_login_onclick();
	}
};

/**
 * 로그인 버튼 클릭시 실행 - 로그인 처리 트랜잭션 발생
 */
scwin.btn_login_onclick = function() {
	console.log("[/login.xml] [btn_login_onclick()] ==> [TEST_01] [로그인 버튼 클릭 이벤트]");

	com.executeSubmission(sbm_loginCheck);		// 정적 ubmission 처리
};

/**
 * 로그인 submitdone 스크립트 - 로그인 성공여부 판단, 성공시 메인페이지 이동, 실패시 메세지 처리
 */
scwin.sbm_Login_submitdone = function(e) {
	console.log("[/login.xml] [sbm_Login_submitdone()] ==> [TEST_01] [로그인 결과 콜백] [result..statusCode]"+ e.responseJSON.rsMsg.statusCode +"[result..message]"+ e.responseJSON.rsMsg.message );
	// alert("[/login.xml] [sbm_Login_submitdone()] ==> [TEST_01] [로그인 결과 콜백] [result..statusCode]"+ e.responseJSON.rsMsg.statusCode +"[result..message]"+ e.responseJSON.rsMsg.message );
	// [/LoginController.java] [login()] ==> [TEST_91] [result.getResult()]{rsMsg={message=로그인 성공, statusCode=S}}

	var resultData = e.responseJSON;
	var result = resultData.rsMsg;

	try {
		if (result.statusCode == "S") {
			com.goHome();		// 메인(/)으로 이동
		} else {
			com.alert(result.message);
		}
	} catch (ex) {
		com.alert("서버 통신 도중 오류가 발생하였습니다. 잠시후 다시 시도해주시기 바랍니다.");
	}
};

/* ■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■
+------------------------------------------------// script 종료 //------------------------------------------------------------+
