head.script:
    var gsSysDate;			// global 현재일자
	var gUserID; 			// global 사용자 ID
	var gUserEmpName; 	    // global 부서명

	//**********************************************************
	// 초기화
	//**********************************************************
	scwin.init = function() {
		gsSysDate 		= WebSquare.date.getCurrentServerDate("yyyyMMdd"); 	// global 현재일자
		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 부서명

	    //----- 버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
		gfn_btnEnable(false, false, false, false, false, false, false, true);

	    ic_dateFr.setValue(gsSysDate.substring(0, 6)+"01");
	    ic_dateTo.setValue(gsSysDate.substring(0, 6) + util.getLastDay(gsSysDate));	        

	    //발송내역조회
	    scwin.getBussActDataSendList();
	};			

	//**********************************************************
	// 
	//**********************************************************
	scwin.subCallBackfunction = function(e){
		// e.subID : 서브미션 ID
		// e.flag  : s성공;
		// e.msg   : 트렌젝션 처리후 메세지
		// e."받을데이타셋"
		switch(e.subID){

			//SMS이메일 발송
			case "retrieveBussActDataSendInfo":
				if(e.flag =="s"){
					dst_BussActDataSendListInfoVo.setJSON(e.result.bussActDataSendInfoVo);		

					if(dst_BussActDataSendListInfoVo.getRowCount()==0){
			        	wprom.alert("조회결과가 없습니다.");        	
			        	return;	
					}

				}else{
					wprom.alert(e.msg);
				}
			break;	
		}
	};


	//**********************************************************
	// 조회버튼클릭
	//**********************************************************
	scwin.btn_Search_onclick = function() {	
		var dateFr = ic_dateFr.getValue();
		var dateTo = ic_dateTo.getValue();
		var searchFlag = sel_searchFlag.getValue();		
		var cont = ib_cont.getValue();	

		if(util.isNull(dateFr)){			
        	wprom.alertF("조회 시작일자를 입력해 주세요.",{}, ic_dateFr);        	
        	return;		
		}		
		if(util.isNull(dateTo)){			
        	wprom.alertF("조회 종료일자를 입력해 주세요.",{}, ic_dateTo);        	
        	return;		
		}	
		if(parseInt(dateFr-dateTo)>0){
			wprom.alertF("조회시작일이 조회종료일보다 큽니다.",{},ic_dateFr);						
			return;
		}	
		if(WebSquare.date.dateDiff(dateFr,dateTo)>31){
			wprom.alertF("최대 31일까지 조회 가능합니다.",{},ic_dateTo);
			return;
		}		

	    //사원번호(발송자사원번호)
		if("01" == searchFlag){		
			if(cont.length>7){
				wprom.alertF("사원번호(7자리) 확인해주세요.",{},ib_cont);
				return;
			}		
		}//고객번호
		else if("02" == searchFlag){		
			if(cont.length!=13){
				wprom.alertF("고객번호(13자리) 확인해주세요.",{},ib_cont);
				return;
			}		

		//고객명
		}else if("03" == searchFlag){

		}	


		//발송내역조회
		scwin.getBussActDataSendList();
	};

//---------------------------------------------------------------------------
// function 명 : lfn_getRealNo (기간계B370)
// 내용        : 실주민번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getRealNo = function(sCustKeyNo) {
		DsWs_CustNo.removeAll();

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectRealCustNo", 0, 0);
		MethodObj.setId("selectRealCustNo");
		MethodObj.setParam("sCustKeyNo", sCustKeyNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sRealCustNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sRealCustNo");
		}else{
			return sCustKeyNo;
		}
	};

//---------------------------------------------------------------------------
// function 명 : lfn_getCustNo
// 내용        : 고객번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getCustNo = function(sRealCustNo) {
		DsWs_CustNo.removeAll();		

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectCustKeyNo", 0, 0);
		MethodObj.setId("selectCustKeyNo");
		MethodObj.setParam("sRealCustNo", sRealCustNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sCustKeyNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sCustKeyNo");
		}else{
			return "";
		}
	};

	//**********************************************************
	// 발송내역조회
	//**********************************************************
	scwin.getBussActDataSendList = function() {		
		dst_BussActDataSendInparamVO.set("dateFr",ic_dateFr.getValue());
		dst_BussActDataSendInparamVO.set("dateTo",ic_dateTo.getValue());

		dst_BussActDataSendInparamVO.set("cstFlgcd",sel_CstFlgcd.getValue());
		dst_BussActDataSendInparamVO.set("bussActDataFlag",sel_BussActDataFlag.getValue());
		dst_BussActDataSendInparamVO.set("sendFlgcd",sel_SendFlgcd.getValue());

		dst_BussActDataSendInparamVO.set("senrEmpno","");
		dst_BussActDataSendInparamVO.set("custNo","");
		dst_BussActDataSendInparamVO.set("name","");

		var searchFlag = sel_searchFlag.getValue();
		var cont = ib_cont.getValue();

		//사원번호(발송자사원번호)
		if("01" == searchFlag){
			dst_BussActDataSendInparamVO.set("senrEmpno",cont);			
		//고객번호
		}else if("02" == searchFlag){
			//고객 주민번호 -> 치환키
			var custKey = "";
			if(!util.isNull(cont)){
				//custKey = scwin.lfn_getCustNo(cont);
				custKey = cont;
				dst_BussActDataSendInparamVO.set("custNo", custKey);	//치환고객번호
			}else{
				dst_BussActDataSendInparamVO.set("custNo",cont);
			}			

		//고객명
		}else if("03" == searchFlag){
			dst_BussActDataSendInparamVO.set("name",cont);
		}	

		var trans = new transSub();                   // transaction 보낼 객체 생성
		trans.setId("retrieveBussActDataSendInfo");    // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0006.do");    // Controller 쪽으로 보낼 주소
		trans.setDs("dst_BussActDataSendInparamVO");					
		trans.targetDs("dst_BussActDataSendListInfoVo");			
		trans.callBack("scwin.subCallBackfunction");  // 받을 Dataset을 처리시 콜백함수
		trans.send();	
	};

	//**********************************************************
	// 발송구분코드에 따라 그리드컬럼 세팅 
	//**********************************************************
	scwin.setSendInfo = function(data, formattedData, rowIndex, colIndex) {
		// 발송구분코드
		var sendFlgcd = dst_BussActDataSendListInfoVo.getCellData(rowIndex, "sendFlgcd");
		// 핸드폰번호
		var cellPhone = dst_BussActDataSendListInfoVo.getCellData(rowIndex, "cellPhone");
		// 이메일
		var email = dst_BussActDataSendListInfoVo.getCellData(rowIndex, "email");

		if(sendFlgcd == "01") {
			formattedData = common.transPhone(cellPhone);
		} else if(sendFlgcd == "02") {
			formattedData = email;
		}

		//console.log("발송구분:"+sendFlgcd+"/핸드폰번호:"+cellPhone+"/이메일:"+email+"/formattedData:"+formattedData);

		return formattedData;	
	};

	scwin.ib_cont_onkeydown = function(e) {
		// 고객명 인풋박스 엔터키 키다운이벤트시 조회버튼 클릭이벤트와 연결  
		if(e.keyCode == 13) {
			scwin.btn_Search_onclick();
		}	
	}; 

	/* ■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■■
