head.script:
	/*
 	 *===================================File description================================================
 	 * Create Date : 2017/09/20
 	 * Creater     : KMJ
 	 *====================================================================================================
	 */

	var gsSysDate;			// global 현재일자
	var gUserID; 			// global 사용자 ID
	var gUserEmpName; 	    // global 부서명
	var pageSize = "20";
	var pageCount = "20";		

	//**********************************************************
	// 초기화
	//**********************************************************
	scwin.init = function() {			
		gfn_getServerTime();//서버시간
		gsSysDate 		= wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);	
		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 부서명
		gUserDuty		= util.getGdsValue("gUserDuty");					// global UserDuty

		var sDate = wconv.addDateSimple(gsSysDate, -1, "yyyyMMdd");	
		cal_fmdt.setValue(wconv.substr(sDate, 0, 6)+"01");
 		cal_todt.setValue(sDate);             
    	//  공통버튼 처리 (입력, 수정, 삭제, 결제, 취소, 인쇄, 엑셀, 닫기)
    	gfn_btnEnable(false, false, false, false, false, false, false, true);

		// 화면 초기화    	
    	scwin.init_div1_sel_list();
		scwin.init_div2_control_list();
		scwin.init_sel_agent();
		scwin.init_sel_agent2();
		scwin.init_sel_agent3();

    	// 전체 조회 권한여부 
    	scwin.checkDuty(gUserDuty);

	};

	//**********************************************************
	// 전체 조회 권한 여부
	//**********************************************************
	scwin.checkDuty = function(sDuty) {
		console.log("====checkDuty sDuty=="+sDuty);
		/*  0. 직책(sDuty)이 '01','02','03','04','05','06','07','09','10','11','20','21','22','55' 이면
			  - 전체 권한
			1. 직책(sDuty)이 64(직영 지점장), 66(대면총무)이면
			  - 조회구분 대면(5)으로 고정, 조회값 본인 지점코드(sTeamCode)로 고정
			2. 직책(sDuty)이 35(대리점사용인)이고 직무코드(sBdutCd)가 200206(대리점사용인관리자(TM))이면
			  2-1. 대리점코드가 A000235이면
			    - 조회구분 대리점(TM)(4)로 고정
			    - 대리점값 대리점코드로 고정
			  2-2. 기타
			    - 조회구분 대리점(대면)(6)로 고정
			    - 대리점값 대리점코드로 고정
			3. 직책(sDuty)이 49(DPM)이면
			  - 조회구분 신채널(3)로 고정
			4. 기타
			  - 조회구분 사번(2)으로 고정
			  - 조회값 본인 사번으로 고정 */			
	    if(sDuty == "01" || 
	       sDuty == "02" || 
	       sDuty == "03" || 
	       sDuty == "04" || 
	       sDuty == "05" || 
	       sDuty == "06" || 
	       sDuty == "07" || 
	       sDuty == "09" || 
	       sDuty == "10" || 
	       sDuty == "11" || 
	       sDuty == "20" || 
	       sDuty == "21" || 
	       sDuty == "22" || 
	       sDuty == "55") {
	       console.log("====checkDuty case0====");
		}
	    else {
	    	// 소속 에 대한 공통코드 조회	
			var MethodObj = new callService("com.inswave.ccc.task.RecTask", "getAgncyInfo","","");
			MethodObj.setId("getAgncyInfo");
			MethodObj.setParam("sUserCd", gUserID);
			MethodObj.setUrl("/sales/CBSB021MController.do");
			MethodObj.setXPath("//*/CCCBF42");
			MethodObj.addXPath("//WARNING");
			MethodObj.sync();
			MethodObj.send();

			wconv.clear(DsWs_Warning);
			util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
		    if (DsWs_Warning.getRowCount() != 0) {
		        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
		        return;
		    } 
		    wconv.clear(DsWs_AgncyInfo);
		    util.jSonDsCreate(_tranSync.getDsJson("CCCBF42"), "DsWs_AgncyInfo", "Y");	

	        var sBdutCd 	= DsWs_AgncyInfo.getCellData(0, "sBdutCd");
	        var sAgncyCd 	= DsWs_AgncyInfo.getCellData(0, "sAgncyCd"); 

			var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR930Task", "getAgentList","","");
			MethodObj.setId("getAgentList");
			MethodObj.setParam("sAgentCode", gUserID);
			MethodObj.setUrl("/sales/CBSB021MController.do");
			MethodObj.setXPath("//*/ADMAC03");
			MethodObj.addXPath("//WARNING");
			MethodObj.sync();
			MethodObj.send();

			wconv.clear(DsWs_Warning);
			util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
		    // 오류처리
		    if (DsWs_Warning.getRowCount() != 0) {
		        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
		        return;
		    } 
		    wconv.clear(DsWs_TEAM);
		    util.jSonDsCreate(_tranSync.getDsJson("ADMAC03"), "DsWs_TEAM", "Y");	

		    var sTeamCode = DsWs_TEAM.getCellData(0, "sTeamCode");
			var sCenterCode = DsWs_TEAM.getCellData(0, "sCenterCode");

			console.log("====checkDuty sBdutCd=="+sBdutCd);
			console.log("====checkDuty sAgncyCd=="+sAgncyCd);
			console.log("====checkDuty sTeamCode=="+sTeamCode);
			console.log("====checkDuty sCenterCode=="+sCenterCode);

	        // 장기직영 지점장 (설계사) : 64 장기직영 대면총무 (상담직) : 66
			if(sDuty == "64" || sDuty == "66" ) {
				console.log("====checkDuty case1====");
				sel_searchType.setValue("5");
				sel_searchType.setDisabled(true);

				scwin.init_div1_sel_list();
				scwin.init_div2_control_list();
				// Ds_Agent3
				scwin.init_sel_agent();
				scwin.init_sel_agent2();
				scwin.init_sel_agent3(0);

				sel_agent3.setValue(sTeamCode);		
				sel_agent3.setDisabled(true);				
			} 	
			// 대리점사용인 관리자 : 35 200206 권한 소유 
			else if( sDuty == "35" && sBdutCd == "200206") {			
				if(sAgncyCd == "A000235" ) {
					console.log("====checkDuty case2-1====");
					sel_searchType.setValue("4");
					sel_searchType.setDisabled(true);

					scwin.init_div1_sel_list();
					scwin.init_div2_control_list();
					// Ds_Agent
					scwin.init_sel_agent(0);
					scwin.init_sel_agent2();
					scwin.init_sel_agent3();

					sel_agent.setValue(sAgncyCd);	
					sel_agent.setDisabled(true);							
				} 
				else {
					console.log("====checkDuty case2-2====");
					sel_searchType.setValue("6");
					sel_searchType.setDisabled(true);

					scwin.init_div1_sel_list();
					scwin.init_div2_control_list(); 	
					// Ds_Agent2
					scwin.init_sel_agent();
					scwin.init_sel_agent2(0);
					scwin.init_sel_agent3();

					sel_agent2.setValue(sAgncyCd);	
					sel_agent2.setDisabled(true);		
				}			
			} 
			//직책 DPM일 경우 자기 조직만 조회
			else if (sDuty == "49" ) {
				console.log("====checkDuty case3====");
				sel_searchType.setValue("3");
				sel_searchType.setDisabled(true);

				scwin.init_div1_sel_list(0);	
	            scwin.init_div2_control_list();	

		      	scwin.init_sel_agent();
				scwin.init_sel_agent2();
				scwin.init_sel_agent3();	

				div1_sel_center.setNodeSet('data:Ds_center2','sCenterName','sCenterCode');   				
			} 
			else {
				console.log("====checkDuty case4====");
				sel_searchType.setValue("2");
				sel_searchType.setDisabled(true);

				scwin.init_div1_sel_list();
				scwin.init_div2_control_list(0);
				scwin.init_sel_agent();
				scwin.init_sel_agent2();
				scwin.init_sel_agent3();

				div2_ib_empNo.setValue(gUserID);
				div2_ib_empNo.setDisabled(true);
				div2_ib_empName.setValue(gUserEmpName);
				div2_ib_empName.setDisabled(true);
			}		
	    }	    

	};

	//**********************************************************
	// 조회서비스  Call
	//**********************************************************
	scwin.search_TPCallService = function(pageNo) {	
	    wconv.clear(DsWs_totCnt);
	    wconv.clear(DsWs_search);
	    wconv.clear(DsWs_SearchWarning);

		var sStatus 		= sel_statusDiv.getValue(); 	//상태
	    var sType 			= sel_typeDiv.getValue(); 		//구분(보종)
	    var sTypeDetail 	= sel_typeDetailDiv.getValue(); //구분(보종상세)
	    var sKey 			= sel_keyDiv.getValue(); 		//검색어 조건
	    var sWord 			= ib_word.getValue();			//검색어	    
	    var sIncompletGubun = "1"; 							//불완전사유 전체(1)로 세팅
	    var sEtcChk			= "";							//기타포함유무(대리점, 웹)	
	    var sFromDate 		= cal_fmdt.getValue(); 			//조회시작일
	    var sToDate 		= cal_todt.getValue(); 			//조회종료일
	    var sIncomChk 		= "Y";  						//불완전시 기타 포함 & 미포함 (불완전판매 기타 : 체크)

	    //조회주건 구분자가 전체가 아닐경우 검색문자 체크
	    if (sKey != "" && sKey != "all") {
	        if (sWord == "") {
	            wprom.alert("검색 문자를 입력 해주세요");
	            return;
	        }
	    }

	    var minusDays = minusDates(sFromDate, sToDate);
	    if (sStatus == "2") {
	        if (wconv.toNumber(minusDays) > 186) {
	            wprom.alert("6달 이내로 조회가능합니다.");
	            return;
	        }
	    } else {
	        if (wconv.toNumber(minusDays) > 186) {
	            wprom.alert("6달 이내로 조회가능합니다.");
	            return;
	        }
	    }
	    // 시작일자 종료일자 조회시 20112401부터 모니터링 변경되어 통계값을 변경전일자와 변경후 일자로 통계 할수 없습니다.
	    if (sFromDate < "20120401" && sToDate >= "20120401") {
	        wprom.alert("시작일자 종료일자 조회시[2012-04-01]부터 완판모니터링이 변경됨으로 변경전일자 와 변경후일자로 조회 할수 없습니다.");
	        return;
	    }		
	    // 시작일자 종료일자 조회시 20112401부터 모니터링 변경되어 통계값을 변경전일자와 변경후 일자로 통계 할수 없습니다.
	    if (sFromDate < "20120901" && sToDate >= "20120901") {
	        wprom.alert("시작일자 종료일자 조회시[2012-09-01]부터 완판모니터링이 변경됨으로 통계 건수가 변경전일자 와 변경후일자로 통계 할수 없습니다.");
	        return;
	    }

		var MethodObj = new callService("com.inswave.ccc.task.CSStatisticsTask", "getCompleteSalesList", pageSize, pageNo);		     
	    var sSearchType = sel_searchType.getValue();// 조직/사번 [타스크내에서 필요]
	    MethodObj.setParam("nKind", sSearchType);	   	   	       
	    if (sSearchType == "1" || sSearchType == "3") {
	        // 조직으로 조회시(다사부)
	        var sCenter = div1_sel_center.getValue();
	        var sTeam 	= div1_sel_team.getValue();
	        var sGroup 	= div1_sel_group.getValue();
	        var sPart 	= div1_sel_part.getValue();
	        var sAgent 	= div1_sel_agent.getValue();
	        var srchOrgName;
	        var srchOrgValue;
	        if (sCenter == "all") {
	            srchOrgName = "all";
	        } else if (sTeam == "all") {
	            srchOrgName = "center";
	            srchOrgValue = sCenter;
	        } else if (sGroup == "all") {
	            srchOrgName = "team";
	            srchOrgValue = sTeam;
	        } else if (sPart == "all") {
	            srchOrgName = "group";
	            srchOrgValue = sGroup;
	        } else if (sAgent == "all") {
	            srchOrgName = "part";
	            srchOrgValue = sPart;
	        } else {
	            srchOrgName = "agent";
	            srchOrgValue = sAgent;
	        }
	        MethodObj.setParam("srchOrgName", srchOrgName);
	        MethodObj.setParam("srchOrgValue", srchOrgValue);
	    } else if (sSearchType == "2") {
	        //사번으로 조회시
	        MethodObj.setParam("sEmpNo", div2_ib_empNo.getValue());
	    } else if (sSearchType == "4") {
	    	var sAgent = sel_agent.getValue();
	        // 조직으로 조회시(TM대리점)
	        if (sAgent != "all") {
	            MethodObj.setParam("sAgent", sAgent);
	        } else {
	            MethodObj.setParam("sAgent", "");
	        }
	    } else if (sSearchType == "6") {
	        // 조직으로 조회시(대면대리점)
	        var sAgent = sel_agent2.getValue();
	        if (sAgent != "all") {
	            MethodObj.setParam("sAgent", sAgent);
	        } else {
	            MethodObj.setParam("sAgent", "");
	        }
	    } else if (sSearchType == "5") {
	        // 대면
	        var sAgent = sel_agent3.getValue();
	        if (sAgent != "all") {
	            MethodObj.setParam("sAgent", sAgent);
	        } else {
	            MethodObj.setParam("sAgent", "");
	        }
	    }

        MethodObj.setParam("sStatus", sStatus);
        MethodObj.setParam("sType", sType);
        MethodObj.setParam("sTypeDetail", sTypeDetail);
        MethodObj.setParam("sKey", sKey);
        MethodObj.setParam("sWord", sWord);		
        MethodObj.setParam("sIncompletGubun", sIncompletGubun); //불완전사유 - 전체1
        MethodObj.setParam("sEtcChk", sEtcChk);  //기타유무 파라미터 추가(2011.10.27 이춘수 추가)
        MethodObj.setParam("sIncomChk", sIncomChk);// 불완전판매 조회시 기타 포함/미포함 여부
        MethodObj.setParam("sFromDate", sFromDate);
        MethodObj.setParam("sToDate", sToDate);
        MethodObj.setParam("more65YearsYN", ""); //조회조건에 65세이상 여부 추가
        MethodObj.setParam("sCustType", "");  // 고객유형(체크시 피보험자)
		var sPersonalInfo = chk_personalInfo.getValue();
        MethodObj.setParam("sPersonalInfo", sPersonalInfo);// 개인정보적법성

		MethodObj.setId("getCompleteSalesList");
		MethodObj.setUrl("/sales/CBSB021MController.do");
		MethodObj.setXPath("//*/hashtable/data/vector/data/CCCMA01Doc");
		MethodObj.addXPath("//*/vector");  
		MethodObj.addXPath("//*/WARNING");
		MethodObj.sync();
		MethodObj.send();

//		util.interfaceKey(_tranSync.result.serviceDs);
//		console.log(JSON.stringify(_tranSync.result));
		DsWs_totCnt.setJSON(_tranSync.getDsJson("vector")); 		// dataset 데이터
	    DsWs_search.setJSON(_tranSync.getDsJson("CCCMA01Doc")); 	// dataset 데이터
		DsWs_SearchWarning.setJSON(_tranSync.getDsJson("WARNING")); 	// dataset 데이터
	    // 오류처리
	    if (DsWs_SearchWarning.getRowCount() != 0) {
	        wprom.alert(DsWs_SearchWarning.getCellData(0, "msg"));
	        return;	        
	    }else {	    	
	    	if(DsWs_search.getRowCount() == 0){	    		
	        	wprom.alert("해당 데이터가 존재하지 않습니다.");
	        	wconv.clear(DsWs_search);	        	
	        	return;
	    	}	        
	    }

	};

	//**************************************************************************************************
	// 입력조건 - 모집조직 셀렉트박스 뷰체인지 이벤트  
	//**************************************************************************************************
	scwin.sel_searchType_onviewchange = function(info) {
		var sSearchType = sel_searchType.getValue();
		// 다사 선택
		if(sSearchType == "1") {
            scwin.init_div1_sel_list(0);	
            scwin.init_div2_control_list();

			scwin.init_sel_agent();
			scwin.init_sel_agent2();
			scwin.init_sel_agent3();

			div1_sel_center.setNodeSet('data:Ds_center1','sCenterName','sCenterCode'); 
		} 
		// 신채널 선택 
		else if(sSearchType == "3") {
			scwin.init_div1_sel_list(0);	
            scwin.init_div2_control_list();	

	      	scwin.init_sel_agent();
			scwin.init_sel_agent2();
			scwin.init_sel_agent3();	

			div1_sel_center.setNodeSet('data:Ds_center2','sCenterName','sCenterCode');   
      	} 
		// TM대리점 선택 
		else if(sSearchType == "4") {
			scwin.init_div1_sel_list();
			scwin.init_div2_control_list();
			// Ds_Agent
			scwin.init_sel_agent(0);
			scwin.init_sel_agent2();
			scwin.init_sel_agent3();
		}	
		// 대면대리점 선택 
		else if(sSearchType == "6") {
			scwin.init_div1_sel_list();
			scwin.init_div2_control_list(); 	
			// Ds_Agent2
			scwin.init_sel_agent();
			scwin.init_sel_agent2(0);
			scwin.init_sel_agent3();
		}	
 		// 사원 선택 
		else if(sSearchType == "2") {
			scwin.init_div1_sel_list();
			scwin.init_div2_control_list(0);
			scwin.init_sel_agent();
			scwin.init_sel_agent2();
			scwin.init_sel_agent3();
		}	
		// 대면 선택 
		else if(sSearchType == "5") {
			scwin.init_div1_sel_list();
			scwin.init_div2_control_list();
			// Ds_Agent3
			scwin.init_sel_agent();
			scwin.init_sel_agent2();
			scwin.init_sel_agent3(0);
		}	
		//  전체선택
		else {	
			scwin.init_div1_sel_list();
			scwin.init_div2_control_list();
			scwin.init_sel_agent();
			scwin.init_sel_agent2();
			scwin.init_sel_agent3();
		}

	};			

	//**************************************************************************************************
	// 조회 버튼 클릭 이벤트  
	//**************************************************************************************************
	scwin.btn_Search_onclick_Async = function() { //완료
		util.eventAsyncCall(this, scwin.btn_Search_onclick, arguments);	
	}

	scwin.btn_Search_onclick = function() {
		scwin.search_TPCallService("1");

        // 전체 건수 가져오기
//        gfn_MakePagingData(Tab0_tab1_Div1.Ds_Page, DsWs_totCnt.getCellData(0, "totalRows"), pageSize, pageCount);
//        Tab0_tab1_Div1_Cmb_Page.Value = "1";	
		gfn_MakePagingData(Ds_Page, DsWs_totCnt.getCellData(0, "totalRows"), pageSize, pageCount);
		Cmb_Page.setValue("1");

	};

	//**************************************************************************************************
	// 조회타입(다사,신채널 선택인 경우)에 따른 셀렉트박스들 초기화  
	//**************************************************************************************************
	scwin.init_div1_sel_list = function(nSelType) {
		if(nSelType == 0) {
			scwin.init_div1_sel_center();
			scwin.init_div1_sel_team();	
			scwin.init_div1_sel_group();
			scwin.init_div1_sel_part();
			scwin.init_div1_sel_agent();		
		}
		else if(nSelType == 1) { 
			scwin.init_div1_sel_team();	
			scwin.init_div1_sel_group();
			scwin.init_div1_sel_part();
			scwin.init_div1_sel_agent();
		}
		else if(nSelType == 2) { 
			scwin.init_div1_sel_group();
			scwin.init_div1_sel_part();
			scwin.init_div1_sel_agent();
