	/*	    if (WARNING.getRowCount() != 0) {
		        wprom.alert(WARNING.getCellData(0, "msg"));
		        return;
		    } else {
		    	;    
		    }*/

		    var sEmpNo = DsWs_empName.getCellData(0,"sEmpNo");
		    var sEmpName = DsWs_empName.getCellData(0,"sEmpName");

		    if (util.isNull(sEmpNo) ){
				alert("존재하지 않는 사원번호입니다.");
				div2_ib_empNo.setValue("");
				div2_ib_empName.setValue("");
