			div2_ib_empName.setValue("");
		}
	};

	//**************************************************************************************************
	// 그리드뷰 모집자 cltr
	//**************************************************************************************************	
	scwin.getCltr = function(data, formattedData, rowIndex, colIndex) {

		var sAgentCode = DsWs_search.getCellData(rowIndex, "sAgentCode");//모집상담원
		var cltr = "";

		if(sAgentCode != null && sAgentCode != "" && wconv.length(sAgentCode)>0 ){

		 	var cltrTypr = wconv.substr(sAgentCode,0,1);

		 	//설계사인 경우
		 	if(cltrTypr == "C"){		 			 	
		 		cltr =  DsWs_search.getCellData(rowIndex, "sAgentName2");

		 	//대리점인 경우
		 	}else if(cltrTypr == "A"){		 			 	
		 		cltr =  DsWs_search.getCellData(rowIndex, "sAgentName3");

		 	}else{
		 		cltr =  "";
		 	}
		}	
		return 	cltr;				
	};


	//**************************************************************************************************
	// 그리드뷰 모니터링일자(예정일) sMonitorDate+sMonitorTime
	//**************************************************************************************************	
	scwin.getMonitorDateTime = function(data, formattedData, rowIndex, colIndex) {
		var sMonitorDate = DsWs_search.getCellData(rowIndex, "sMonitorDate");
		var sMonitorTime = DsWs_search.getCellData(rowIndex, "sMonitorTime");

		// ####/##/## ##:##
		sMonitorDate = wconv.dateformat(sMonitorDate, 'yyyy/MM/dd');
		if(!util.isNull(sMonitorTime)) {
			sMonitorTime = sMonitorTime.substr(0,2) + ":" + sMonitorTime.substr(2,2);	
		}

		formattedData = sMonitorDate + " " + sMonitorTime;

		return formattedData;
	};

	//**************************************************************************************************
	// 그리드뷰 모니터링여부  
	//**************************************************************************************************	
	scwin.getStatusName = function(data, formattedData, rowIndex, colIndex) {
		// decode(sStatus,'1','완전','2','불완전','3','미결','4','기타','5','보완완료')
		var sStatus = DsWs_search.getCellData(rowIndex, "sStatus");
		var sStatusName = "";

		switch (sStatus) {
			case "1":
				sStatusName = "완전";
				break;
			case "2":
				sStatusName = "불완전";
				break;
			case "3":
				sStatusName = "미결";
				break;
			case "4":
				sStatusName = "기타";
				break;
			case "5":
				sStatusName = "보완완료";	
				break;		
			default:
				break;
		}

		formattedData = sStatusName;		
		return formattedData;
	};

