	//**************************************************************************************************
	scwin.grd_List_oncellclick = function(row,col) {
		var sColId = grd_List.getColumnID(col);
	    if (sColId == "sPolicyNo") {
	        var promise = Promise.resolve();	        
		    promise = promise.then(function(rtnVal) {	
				// 계약번호 클릭하는 경우 [완전판매모니터링(CBSB036P)]화면을 오픈.	
				var indata  = {};
					indata["sPolicyType"] = DsWs_search.getCellData(row, "sPolicyType");
					indata["sPolicyYM"] = DsWs_search.getCellData(row, "sPolicyYM");
			    	indata["sPolicySer"] = DsWs_search.getCellData(row, "sPolicySer");		
			    	indata["sCustType"] = DsWs_search.getCellData(row, "sCustType");		

				var popInfo = {
				    "width"     : 800,
				    "height"    : 700,
				    "top"       : 100,
				    "left"      : 200,
				    "indata"    : indata,
				    //"callback"  : scwin.popupCallback,
				    "title"     : "완전판매모니터링"
				};
				return wprom.popupOpen("/ui/cbsb/CBSB036P.xml", popInfo);
			});
	    }
	};

	//**************************************************************************************************
