        var sURL = gClaimUrl + "/ccc/monitering/CompleteSalesMonitering.html?sPopupYN=Y&sSupplement=Y&sRelKey=";
    	//alert(sURL);
    	scwin.gfn_Search(nRow);

    };

    //**************************************************************************************************
	// 기초셋팅 미사용 -> CBSB036P 화면 팝업으로 변경(2017.12.28)
	//**************************************************************************************************
	scwin.gfn_Search = function(nRow) {
		var sPolicyType = DsWs_search.getCellData(nRow, "sPolicyType");
	    var sPolicyYM = DsWs_search.getCellData(nRow, "sPolicyYM");
	    var sPolicySer = DsWs_search.getCellData(nRow, "sPolicySer");
	    var sCustType = DsWs_search.getCellData(nRow, "sCustType");

		var MethodObj = new callService("com.inswave.ccc.task.SalesMonitoringTask", "select", "", "");
		MethodObj.setId("select");
		MethodObj.setParam("sPolicyType", sPolicyType);
		MethodObj.setParam("sPolicyYM", sPolicyYM);
		MethodObj.setParam("sPolicySer", sPolicySer);
		MethodObj.setParam("sCustType", sCustType);

		MethodObj.setUrl("/sales/CBSB021MController.do");
		MethodObj.setXPath("//*/vector/data/ADMAB01");
		MethodObj.sync();
		MethodObj.send();

//		util.interfaceKey(_tranSync.result.serviceDs);
//		console.log(JSON.stringify(_tranSync.result));
	    DsWs_BBSAADetail.setJSON (_tranSync.getDsJson("ADMAB01")); // dataset 데이터

	    if (DsWs_BBSAADetail.getRowCount() < 1) {
	        wprom.alert("자료가 존재하지 않습니다.");
	        return;
	    } else {
	        //콜리시얼이 없을경우 버튼 숨김 
	        if (DsWs_BBSAADetail.getCellData(0, "nCallSerial") == null || 
	            DsWs_BBSAADetail.getCellData(0, "nCallSerial") == "") {
	    //        btn_Listen.hide();
	        } else {
	    //        btn_Listen.show();
	        }
	    }
	};

