	scwin.cm_btnExDown = function(){
		if(DsWs_search.getRowCount() == 0){
			alert("조회 후 사용하세요.");
			return;
		}

		var option={};
		var sFromDate 		= cal_fmdt.getValue(); 			//조회시작일
	    var sToDate 		= cal_todt.getValue(); 			//조회종료일		
		option.fileName= "완전판매모니터링명세_" + sFromDate + "_" + sToDate +".xlsx";
		var infoArr = {};
		common.downLoadExcel("grd_List",option,infoArr);
	};
