head.script:
	/*
 	 *===================================File description================================================
 	 * Create Date : 2017/09/22
 	 * Creater     : KMJ
 	 *====================================================================================================
	 */

	var gsSysDate;			// global 현재일자
	var gUserID; 			// global 사용자 ID
	var gUserEmpName; 	    // global 사용자명 
	var gUserDepartCode;	// global 소속부서코드
	var gCurrDiv;			// 현재활성화뷰 
	var gWeekFlag;			// 주구분		
	var ib_searchEmpNo_Event;
	//**********************************************************
	// 초기화
	//**********************************************************
	scwin.init = function() {
		gfn_getServerTime();//서버시간
		gsSysDate 		= wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);	
		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 사용자명
		gUserDepartCode	= util.getGdsValue("gUserDepartCode");    			// global 소속부서코드 

	    //----- 버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
		gfn_btnEnable(false, false, false, false, false, false, false, true);

		// 화면 오픈시 조회기준일은 현재일.
		cal_searchPeriod.setValue( gsSysDate );
		// 사번은 로그인자 사번 setting
	    var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{4}[0-9\s]{7}$/,
	    						/^[0-9\s]{6}$/,
	    							/^[0-9\s]{6}[0-9\s]{1}[A-Z0-9\s]{6}$/
	    								,/^[0-9A-Z\s]{7}$/
	    									,/^[ㄱ-ㅎㅏ-ㅣ가-힣A-Za-z0-9\s]{20}$/ ];
		var maxLength  = [12,6,13,7,20];
		var chkIndex   = 3;		
		ib_searchEmpNo_Event = new wMaskEditObj(ib_searchEmpNo, maskRegExp, maxLength, chkIndex);
	    ib_searchEmpNo.setDisplayFormat("#######");
        ib_searchEmpNo.setPlaceholder( "_______");
        ib_searchEmpNo_Event.setIndex(3);	    	    	    	    
		ib_searchEmpNo.setValue( gUserID );
		ib_searchEmpName.setValue(gUserEmpName);
		// 최초 화면로딩시 활동계획(월) 화면 display
		gCurrDiv  = 1;

		// 화면데이터 초기화 
		scwin.init_1stWeekData();
	    scwin.init_2ndWeekData();
	    scwin.init_3rdWeekData();
	    scwin.init_4thWeekData();
	    scwin.init_5thWeekData();	    

	    var promise = Promise.resolve();
	    promise = promise.then(function(rtnVal) {
	     	// /common/CBSO0015.do 로그인자 권한조회	 
			return scwin.getLginAuthInfo();	 			
	    });

	     promise = promise.then(function(rtnVal) {
		    // 월활동계획 클릭 이벤트 발생시킴  
		    scwin.btn_schdPlanMon_onclick();	  
	    });
	    return;	   	    	    	
	};

	//**********************************************************
	// 
	//**********************************************************
	scwin.subCallBackfunction = function(e){
		// e.subID : 서브미션 ID
		// e.flag  : s성공;
		// e.msg   : 트렌젝션 처리후 메세지
		// e."받을데이타셋"
		switch(e.subID){
			// 로그인ID권한조회
			case "retrieveLginIDAuthInqr":
				if(e.flag == "s") {
					dst_LginAuthInqrVo.setJSON(e.result);	
					// 권한에 따라 버튼및 게시조직 체크 
					scwin.setCtrlByAuth();

					var promise = Promise.resolve();
					promise = promise.then(function(rtnVal) {
						// /sales/support/CBSB0013.do 월고객일정계획정보조회
   			//			scwin.getMonCstSchdPlanInfo();	
					});

				}else {
					wprom.alert(e.msg);
				}															
			break;
			// 동일소속조회
			case "retrieveSamePstnAuthInqr":
				if(e.flag == "s") {
					dst_SamePstnAuthInqrVo.setJSON(e.result);	
					if( gUserDepartCode != dst_SamePstnAuthInqrVo.get("cmprPstnDeptCd") ) {
						wprom.alertF("부서코드불일치.동일소속사번만 입력가능합니다. 사번을 다시 입력하세요", {}, ib_searchEmpNo);
						ib_searchEmpNo.setValue("");	
						return;
					}

				}else {					
					wprom.alert(e.msg);
				}															
			break;
			// 월고객일정계획정보조회
			case "retrieveMonCstSchdPlanInfo":
				if(e.flag == "s") {
					dst_SchdPlanMonListInfoVo.setJSON(e.result.schdPlanMonInfoVo);	
					dst_SchdPlanInfoVo.setJSON(e.result);
					// 월고객일정계획 화면에 표시 
					scwin.setSchdPlanMonListInfo();
				} else {
					wprom.alert(e.msg);
				}
			break;
			// 주고객일정계획정보조회
			case "retrieveWeekCstSchdPlanInfo":
				if(e.flag == "s") {		
					dst_SchdPlanWeekListInfoVo.setJSON(e.result.vistMmdtVo);	
					dst_SchdPlanInfoVo.setJSON(e.result);
					// 주고객일정계획 화면에 표시 
					scwin.setSchdPlanWeekListInfo();
				} else {
					wprom.alert(e.msg);
				}
			break;
			// 일고객일정계획정보조회
			case "retrieveDayCstSchdPlanInfo":
				if(e.flag == "s") {
					dst_SchdPlanDayListInfoVo.setJSON(e.result.vistMmdtVo);	
					dst_SchdPlanInfoVo.setJSON(e.result);
					// 일고객일정 화면에 표시 
					scwin.setSchdPlanDayListInfo();	
				} else {
					wprom.alert(e.msg);
				}
			break;
			// 일정메모등록/수정
			case "registerSchdMemoInfo":
				if(e.flag == "s") {
					dst_SchdMemoInfoVo.setJSON(e.result);
					wprom.alert("등록/수정이 완료되었습니다.");
				} else {
					wprom.alert(e.msg);
				}
			break;	
		}
	};

	//**********************************************************
	// 로그인자 권한조회 /common/CBSO0015.do 로그인자 권한조회
	//**********************************************************
	scwin.getLginAuthInfo = function() {
		var promise = Promise.resolve();
		// 사번으로 로그인자 권한조회	
		dst_LginAuthInqrVo.set("empNo" , gUserID);

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveLginIDAuthInqr");        	// transaction 보낼 SubmissionID
		trans.setUrl("/common/CBSO0015.do");   			// Controller 쪽으로 보낼 주소
		trans.setDs("dst_LginAuthInqrVo");					
		trans.targetDs("dst_LginAuthInqrVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 동일소속조회 /common/CBSO0016.do 
	//**********************************************************
	scwin.getSamePstnAuthInqr = function() {
		// 사번
		dst_SamePstnAuthInqrVo.set("empno", gUserID);
		// 소속부서코드
		dst_SamePstnAuthInqrVo.set("pstnDeptCd", gUserDepartCode);
		// 비교사번
		dst_SamePstnAuthInqrVo.set("cmprEmpno", ib_searchEmpNo.getValue());

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveSamePstnAuthInqr");        // transaction 보낼 SubmissionID
		trans.setUrl("/common/CBSO0016.do");   			// Controller 쪽으로 보낼 주소
		trans.setDs("dst_SamePstnAuthInqrVo");					
		trans.targetDs("dst_SamePstnAuthInqrVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 권한에 따라 화면컨트롤 
	//**********************************************************
	scwin.setCtrlByAuth = function() {	
		// 본사 설계사 및 대리점 사용인은 본인 고객만 조회 가능하도록.
		ib_searchEmpNo.setDisabled(true);
		//∙ 조직 관리자 SuperAuthYn='Y' 는 본인 조직의 취급자 사번 입력 가능 - 권한코드 : 34024(대면총무권한) /21013(대리점사용인관리자)
		if(dst_LginAuthInqrVo.get("superAuthYn") == "Y")
  		{
  			ib_searchEmpNo.setDisabled(false);
  		}
		//∙ 본사 관리자 bizAdmrYn='Y'는 전체 취급자 사번 입력 가능 - 권한코드 : 4000103(장기보험부기본)
		if(dst_LginAuthInqrVo.get("bizAdmrYn") == "Y")
  		{
  			ib_searchEmpNo.setDisabled(false);
  		}		
	};	

	//**********************************************************
	// 활동계획(월) 버튼 클릭이벤트 
	//**********************************************************
	scwin.btn_schdPlanMon_onclick = function() {
		// 월별 캘린더만 보여주도록 함  sw_div1	
		switch_div.show();
        switch_div.setSelectedID("sw_div1");
        sw_div1.setStyle("display" , ""); 
        sw_div2.setStyle("display" , "none");  
        sw_div3.setStyle("display" , "none"); 
  //      btn_memo.hide();
        gCurrDiv  = 1;
        // 활동계획(월) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
 /*       btn_schdPlanMon.changeClass("btn_nml off","btn_nml on");
        btn_schdPlanWeek.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanDay.changeClass("btn_nml on","btn_nml off");*/

        // 조회기준일자 캘린더 포맷 년월 로 표시되도록 한다.
//        cal_searchPeriod.setCalendarValueType("yearMonth");  
     	// 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
     	cal_searchPeriod.setCalendarValueType("yearMonthDate");  

        // 월정보 조회
     	scwin.btn_Search_onclick();      
	};

	//**********************************************************
	// 활동계획(주) 버튼 클릭이벤트 
	//**********************************************************		
	scwin.btn_schdPlanWeek_onclick = function() {
		// 주별 캘린더만 보여주도록 함  sw_div2		
		switch_div.show();
        switch_div.setSelectedID("sw_div2");
        sw_div2.setStyle("display" , "");         
        sw_div1.setStyle("display" , "none");  
        sw_div3.setStyle("display" , "none"); 
        gCurrDiv  = 2;
   //     btn_memo.hide();
        // 활동계획(주) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
        /*btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml off","btn_nml on");
        btn_schdPlanDay.changeClass("btn_nml on","btn_nml off");*/

     	// 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
     	cal_searchPeriod.setCalendarValueType("yearMonthDate");  

     	// 주정보 조회
     	scwin.btn_Search_onclick();
     	//scwin.getWeekCstSchdPlanInfo();
	};

	//**********************************************************
	// 활동계획(일) 버튼 클릭이벤트 
	//**********************************************************
	scwin.btn_schdPlanDay_onclick = function() {
		// 일별 캘린더만 보여주도록 함  sw_div3	
		switch_div.show();
        switch_div.setSelectedID("sw_div3");
        sw_div3.setStyle("display" , "");        
        sw_div1.setStyle("display" , "none");  
        sw_div2.setStyle("display" , "none"); 
        gCurrDiv  = 3;
 //       btn_memo.show();
        // 활동계획(일) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
/*      btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanDay.changeClass("btn_nml off","btn_nml on");*/

        // 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
        cal_searchPeriod.setCalendarValueType("yearMonthDate");  

        // 일정보 조회
     	scwin.btn_Search_onclick();

	};

	//**********************************************************
	// 월고객일정계획정보조회
	//**********************************************************
	scwin.getMonCstSchdPlanInfo = function(sSearchDate) {
		// INPUT DS 세팅
		// 등록자사번 
		dst_SchdPlanMonInfoVo.set("rgtrEmpno", ib_searchEmpNo.getValue()); //
		// 조회기준일자  (월별 -> 기준일의 년월정보로 데이터 조회)
		dst_SchdPlanMonInfoVo.set("stndDate", sSearchDate.substr(0, 8)); //cal_searchPeriod.getValue());

//		dst_SchdPlanMonInfoListVo.removeAll();
		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveMonCstSchdPlanInfo");      // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0013.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_SchdPlanMonInfoVo");			// Input DS		
		trans.targetDs("dst_SchdPlanMonInfoListVo");	// Output DS	 	
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};				

	//**********************************************************
	// 주고객일정계획정보조회
	//**********************************************************
	scwin.getWeekCstSchdPlanInfo = function(weekFlag, sSearchDate, stdt, enddt) {

		// 등록자사번 
		dst_SchdPlanWeekInfoVo.set("doerNo", ib_searchEmpNo.getValue()); //"C000148"
		// 조회기준일자  
		dst_SchdPlanWeekInfoVo.set("stndYmd", sSearchDate);
		// 시작일자  
		dst_SchdPlanWeekInfoVo.set("stdt", stdt);
		// 종료일자   
		dst_SchdPlanWeekInfoVo.set("enddt", enddt);
		// 주구분   
		dst_SchdPlanWeekInfoVo.set("weekFlag", weekFlag);
		gWeekFlag = weekFlag;

//		dst_SchdPlanWeekListInfoVo.removeAll();
		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveWeekCstSchdPlanInfo");     // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0014.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_SchdPlanWeekInfoVo");			// Input DS		
		trans.targetDs("dst_SchdPlanWeekListInfoVo");	// Output DS	 	
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};		

	//**********************************************************
	// 일고객일정계획정보조회
	//**********************************************************
	scwin.getDayCstSchdPlanInfo = function(sDayOfWeek, sSearchDate) {			
		// INPUT DS 세팅
		// 등록자사번 
		dst_SchdPlanInfoVo.set("doerNo", ib_searchEmpNo.getValue()); //"C000148"
		// 조회기준일자  
		dst_SchdPlanInfoVo.set("stndYmd", sSearchDate);
		// 요일   
		dst_SchdPlanInfoVo.set("dayOfWeek", sDayOfWeek);

//		dst_SchdPlanListInfoVo.removeAll();
		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveDayCstSchdPlanInfo");      // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0015.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_SchdPlanInfoVo");				// Input DS		
		trans.targetDs("dst_SchdPlanListInfoVo");		// Output DS	 	
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};	

	//**********************************************************
	// 일정메모등록/수정
	//**********************************************************
	scwin.rgstSchdMemoInfo = function() {
		// 입력값 체크 
		var sCont 		= dst_SchdMemoInfoVoList.getCellData(0, "cont");
		var sRgtrEmpno	= ib_searchEmpNo.getValue();  //dst_SchdMemoInfoVoList.getCellData(0, "rgtrEmpno");
		var sRgstDate 	= cal_searchPeriod.getValue(); //dst_SchdMemoInfoVoList.getCellData(0, "rgstDate");

		// 메모내용
		if(util.isNull(sCont)){
			wprom.alertF("내용을 입력하세요", {}, txt_dayMemo);//grd_dayMemo);	
			return;	
		}

		// 등록자사번 
		dst_SchdMemoInfoVo.set("rgtrEmpno", sRgtrEmpno);
		// 등록일자 
		dst_SchdMemoInfoVo.set("rgstDate", sRgstDate);
		// 내용 
		dst_SchdMemoInfoVo.set("cont", sCont);
		// 삭제여부 				
	//	dst_SchdMemoInfoVo.set("delYn", "N");	   	

		var trans = new transSub();                  // transaction 보낼 객체 생성
		trans.setId("registerSchdMemoInfo");          // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0016.do");   // Controller 쪽으로 보낼 주소
		trans.setDs("dst_SchdMemoInfoVo");					
		trans.targetDs("dst_SchdMemoInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  // 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 조회버튼 클릭이벤트 
	//**********************************************************	
	scwin.btn_Search_onclick = function() {	
		// INPUT DS 세팅
		// 조회기준일자 
		var sSearchDate = cal_searchPeriod.getValue();
		if(wconv.length(sSearchDate) != 8) {
			wprom.alertF("조회일자를 정확히 입력하세요", {}, cal_searchPeriod);
			return;
		}

		var sSearchEmpNo = ib_searchEmpNo.getValue();
		if(wconv.length(sSearchEmpNo) != 7) {
			wprom.alertF("조회사번을 정확히 입력하세요", {}, ib_searchEmpNo);
			return;
		}

		// 월고객일정계획정보조회	
		// 화면데이터 초기화 
		scwin.init_1stWeekData();
	    scwin.init_2ndWeekData();
	    scwin.init_3rdWeekData();
	    scwin.init_4thWeekData();
	    scwin.init_5thWeekData();

		scwin.getMonCstSchdPlanInfo(sSearchDate);	

	};

	//**********************************************************
	// 월고객정보 화면에 표시  
	//**********************************************************	
	scwin.setSchdPlanMonListInfo = function() {
		var str ;
		var sEmpName = ib_searchEmpName.getValue(); 
		// 조회기준일자 
		var sSearchDate = cal_searchPeriod.getValue();
		var sYear = sSearchDate.substr(0,4);
		var sMon = sSearchDate.substr(4,2);
		var sDay = sSearchDate.substr(6,2);

		var empNm			= dst_SchdPlanInfoVo.get("empNm");		
		//취급자님 2017년 8월 일정
	 	str = empNm + " 님 " + sYear + "년 " + sMon + "월 일정"; 
		txt_search_info.setValue(str);	

		for(var i=0; i<dst_SchdPlanMonListInfoVo.getRowCount(); i++) {		

			var sDayOfWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "dayOfWeek");
			var sWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "week");
			var nDay = dst_SchdPlanMonListInfoVo.getCellData(i, "day");
			var sMemoYn = dst_SchdPlanMonListInfoVo.getCellData(i, "memoYn");
			var sMmdtCnt = dst_SchdPlanMonListInfoVo.getCellData(i, "mmdtCnt");
			var sVisitCnt = dst_SchdPlanMonListInfoVo.getCellData(i, "visitCnt");
			var sCallCnt = dst_SchdPlanMonListInfoVo.getCellData(i, "callCnt");

			switch (sDayOfWeek) {			
