							txt_1stSat_mmdtCnt.show();
							txt_1stSat_mmdtCnt.setValue("기념일("+ sMmdtCnt +")");

						}
						// 방문건수 
						if(util.isNull(sVisitCnt)) {
					      txt_1stSat_visitCnt.hide(); 
						} else {
							txt_1stSat_visitCnt.show();
							txt_1stSat_visitCnt.setValue("방문("+ sVisitCnt +")");
