							txt_5thSat_visitCnt.setValue("방문("+ sVisitCnt +")");
						}
						// 콜건수 
						if(util.isNull(sCallCnt)) {
					      txt_5thSat_callCnt.hide(); 
						} else {
							txt_5thSat_callCnt.show();
							txt_5thSat_callCnt.setValue("전화("+ sCallCnt +")");
						}

					}
					break;						
				default:		
				break;
			}

		}

		// 조회기준일자 
		var sSearchDate = cal_searchPeriod.getValue();
		var sYear = sSearchDate.substr(0,4);
		var sMon = sSearchDate.substr(4,2);
		var sDay = sSearchDate.substr(6,2);		
		var stdt;		//시작일자
		var enddt;		//종료일자		
		var weekFlag;  	//주구분 - 1 :1주, 2:2주, 3:3주, 4:4주, 5:5주
		var sDayOfWeek;
		// 조회기준일자의 시작일자, 종료일자, 주정보를 가져온다. 
		for(var i=0; i<dst_SchdPlanMonListInfoVo.getRowCount(); i++) {			
			var stndDate = dst_SchdPlanMonListInfoVo.getCellData(i, "stndDate");
			if(sSearchDate == stndDate) {
				sDayOfWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "dayOfWeek");
				weekFlag = dst_SchdPlanMonListInfoVo.getCellData(i, "week");
				break;
			}
		}	

		if(gCurrDiv == 2) {
			scwin.getWeekCstSchdPlanInfo(weekFlag, sSearchDate, "", "");
		}else if(gCurrDiv == 3) {
			scwin.getDayCstSchdPlanInfo(sDayOfWeek, sSearchDate);
		}
	};

	//**********************************************************
	// 주고객정보 화면에 표시  
	//**********************************************************	
	scwin.setSchdPlanWeekListInfo = function() {
		// 주정보 라벨 설정 
		// 7 (월)
		var str ;
		var sEmpName = ib_searchEmpName.getValue(); 
		// 조회기준일자 
		var sSearchDate = dst_SchdPlanWeekInfoVo.get("stndYmd");
		var sWeekFlag = dst_SchdPlanWeekInfoVo.get("weekFlag");
		var sYear = sSearchDate.substr(0,4);
		var sMon = sSearchDate.substr(4,2);
		var sDay = sSearchDate.substr(6,2);

		var empNm			= dst_SchdPlanInfoVo.get("empNm");

		//취급자님 2017년 8월 일정
	 	str = empNm + " 님 " + sYear + "년 " + sMon + "월 " + sWeekFlag + "째주 일정"; 
		txt_search_info.setValue(str);

		// 헤더 초기화 
		grd_weekSun.setHeaderValue("col_Sun" , ""); 
		grd_weekMon.setHeaderValue("col_Mon" , ""); 
		grd_weekTue.setHeaderValue("col_Tue" , "");
		grd_weekWed.setHeaderValue("col_Wed" , ""); 
		grd_weekThu.setHeaderValue("col_Thu" , "");
		grd_weekFri.setHeaderValue("col_Fri" , ""); 
		grd_weekSat.setHeaderValue("col_Sat" , ""); 

		// 주정보내용 그리드 추가 
		dsl_grdWeekSun.removeAll();
		dsl_grdWeekMon.removeAll();
		dsl_grdWeekTue.removeAll();
		dsl_grdWeekWed.removeAll();
		dsl_grdWeekThu.removeAll();
		dsl_grdWeekFri.removeAll();
		dsl_grdWeekSat.removeAll();
		for(var i=0; i<dst_SchdPlanMonListInfoVo.getRowCount(); i++) {				

			var sWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "week");
			if(sWeek == sWeekFlag) {
				var sDayOfWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "dayOfWeek");
				var nDay = dst_SchdPlanMonListInfoVo.getCellData(i, "day");
				var sWeekLabel = "";
				sWeekLabel = nDay + " (" + sDayOfWeek + ")";

				if(sDayOfWeek == "일") {
//					txt_week_Sun.setValue(sWeekLabel);
					grd_weekSun.setHeaderValue("col_Sun" , sWeekLabel); 
					//grd_weekSun.setUserData1("userData1", sRgstDate);
				} else if(sDayOfWeek == "월") {
					grd_weekMon.setHeaderValue("col_Mon" , sWeekLabel); 
//					txt_week_Mon.setUserData1("userData1", sRgstDate);
//					txt_week_Mon.setValue(sWeekLabel);
				} else if(sDayOfWeek == "화") {
					grd_weekTue.setHeaderValue("col_Tue" , sWeekLabel); 
//					txt_week_Tue.setValue(sWeekLabel);
				} else if(sDayOfWeek == "수") {
					grd_weekWed.setHeaderValue("col_Wed" , sWeekLabel); 
//					txt_week_Wed.setValue(sWeekLabel);
				} else if(sDayOfWeek == "목") {
					grd_weekThu.setHeaderValue("col_Thu" , sWeekLabel); 
//					txt_week_Thu.setValue(sWeekLabel);
				} else if(sDayOfWeek == "금") {
					grd_weekFri.setHeaderValue("col_Fri" , sWeekLabel); 
//					txt_week_Fri.setValue(sWeekLabel);
				} else if(sDayOfWeek == "토") {
					grd_weekSat.setHeaderValue("col_Sat" , sWeekLabel); 
//					txt_week_Sat.setValue(sWeekLabel);
				}

			}
		}


		for(var j=0; j<dst_SchdPlanWeekListInfoVo.getRowCount(); j++) {				
			// 요일
			var sDayOfWeek = dst_SchdPlanWeekListInfoVo.getCellData(j, "dayOfWeek");
			// 기준일자
			var stndDate = dst_SchdPlanWeekListInfoVo.getCellData(j, "stndDate");
			// 일정구분(01:메모 02:기념일, 03:방문, 04:전화)
			var schdFlgcd = dst_SchdPlanWeekListInfoVo.getCellData(j, "schdFlgcd");
			var schdFlgNm = dst_SchdPlanWeekListInfoVo.getCellData(j, "schdFlgNm");	
			// 기념일구분(일정구분이 02인 경우 기념일구분을 보여줌 
			// 01:생일 , 02:결혼, 03:기타, 04:음력생일
			var admMmdtFlgcd = dst_SchdPlanWeekListInfoVo.getCellData(j, "admMmdtFlgcd");
			var admMmdtFlgNm = dst_SchdPlanWeekListInfoVo.getCellData(j, "admMmdtFlgNm");
			var custNm	= dst_SchdPlanWeekListInfoVo.getCellData(j, "custNm");
			// 방문인 경우 시간 표시 
			var schdTime = dst_SchdPlanWeekListInfoVo.getCellData(j, "schdTime");
			// 전화인 경우 핸드폰번호 표시
			var hpNo = dst_SchdPlanWeekListInfoVo.getCellData(j, "hpNo");
			if(sDayOfWeek == "일") {
				util.appendRow(dsl_grdWeekSun); 
				var nRow = dsl_grdWeekSun.getRowPosition();
				dsl_grdWeekSun.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekSun.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekSun.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekSun.setCellData(nRow, "schdFlgNm", schdFlgNm);  
				// 내용   
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}	
				dsl_grdWeekSun.setCellData(nRow, "cont", sCont);  
			} else if(sDayOfWeek == "월") {
				util.appendRow(dsl_grdWeekMon); 
				var nRow = dsl_grdWeekMon.getRowPosition();
				dsl_grdWeekMon.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekMon.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekMon.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekMon.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}	
				dsl_grdWeekMon.setCellData(nRow, "cont", sCont);  
			} else if(sDayOfWeek == "화") {			
				util.appendRow(dsl_grdWeekTue); 
				var nRow = dsl_grdWeekTue.getRowPosition();
				dsl_grdWeekTue.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekTue.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekTue.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekTue.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";					
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}				
				dsl_grdWeekTue.setCellData(nRow, "cont", sCont);    
			} else if(sDayOfWeek == "수") {
				util.appendRow(dsl_grdWeekWed); 
				var nRow = dsl_grdWeekWed.getRowPosition();
				dsl_grdWeekWed.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekWed.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekWed.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekWed.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}	
				dsl_grdWeekWed.setCellData(nRow, "cont", sCont);   
			} else if(sDayOfWeek == "목") {
				util.appendRow(dsl_grdWeekThu); 
				var nRow = dsl_grdWeekThu.getRowPosition();
				dsl_grdWeekThu.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekThu.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekThu.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekThu.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}			
				dsl_grdWeekThu.setCellData(nRow, "cont", sCont);   
			} else if(sDayOfWeek == "금") {
				util.appendRow(dsl_grdWeekFri); 
				var nRow = dsl_grdWeekFri.getRowPosition();
				dsl_grdWeekFri.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekFri.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekFri.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekFri.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}		
				dsl_grdWeekFri.setCellData(nRow, "cont", sCont);   
			} else if(sDayOfWeek == "토") {
				util.appendRow(dsl_grdWeekSat); 
				var nRow = dsl_grdWeekSat.getRowPosition();
				dsl_grdWeekSat.setCellData(nRow, "stndDate", stndDate);
				dsl_grdWeekSat.setCellData(nRow, "dayOfWeek", sDayOfWeek);  
				dsl_grdWeekSat.setCellData(nRow, "schdFlgcd", schdFlgcd);
				dsl_grdWeekSat.setCellData(nRow, "schdFlgNm", schdFlgNm);     
				var sCont;
				if(schdFlgcd == "01")	// 메모 
				{
					sCont = "<span class='memo2'> ";
					sCont += schdFlgNm;
				}
				else if(schdFlgcd == "02")	// 기념일
				{
					sCont = "<span class='birth'> ";
					sCont += admMmdtFlgNm;
					sCont += " - ";
					sCont += custNm;
				}
				else if(schdFlgcd == "03")	// 방문
				{
					sCont = "<span class='visit2'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
					sCont += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";
				}
				else if(schdFlgcd == "04")	// 전화
				{
					sCont = "<span class='call'> ";
					sCont += schdFlgNm;
					sCont += " - ";
					sCont += custNm;
				}					
				dsl_grdWeekSat.setCellData(nRow, "cont", sCont);   
			}	
		}
		// 일정구분코드로 sort
		dsl_grdWeekSun.sort('schdFlgcd', 0);
		dsl_grdWeekMon.sort('schdFlgcd', 0);
		dsl_grdWeekTue.sort('schdFlgcd', 0);
		dsl_grdWeekWed.sort('schdFlgcd', 0);
		dsl_grdWeekThu.sort('schdFlgcd', 0);
		dsl_grdWeekFri.sort('schdFlgcd', 0);
		dsl_grdWeekSat.sort('schdFlgcd', 0);
	};

	//**********************************************************
	// 일고객정보 화면에 표시  
	//**********************************************************	
	scwin.setSchdPlanDayListInfo = function() {
		var str ;
		var sEmpName = ib_searchEmpName.getValue(); 
		// 조회기준일자 
		var sDayOfWeek 		= dst_SchdPlanInfoVo.get("dayOfWeek");
		var sSearchDate 	= dst_SchdPlanInfoVo.get("stndYmd");
		var doerNo 			= dst_SchdPlanInfoVo.get("doerNo");
		var empNm			= dst_SchdPlanInfoVo.get("empNm");
		var sYear = sSearchDate.substr(0,4);
		var sMon = sSearchDate.substr(4,2);
		var sDay = sSearchDate.substr(6,2);
		//취급자님 2017년 8월 일정
	 	str = empNm + " 님 " + sYear + "년 " + sMon + "월 " + sDay + "일 일정"; 
		txt_search_info.setValue(str);

		// 일정보 라벨 설정 
		var sDayLabel;
		sDayLabel = sDay + " (" + sDayOfWeek + ")";
		txt_day_info.setValue(sDayLabel);
		// 일정보내용 그리드 추가 
		dsl_grdDay.removeAll();
		dst_SchdMemoInfoVoList.removeAll();
		// 메모는 무조건 한 로우 추가해두기 		
		util.appendRow(dst_SchdMemoInfoVoList);
		for(var j=0; j<dst_SchdPlanDayListInfoVo.getRowCount(); j++) {				
			// 요일
			var sDayOfWeek = dst_SchdPlanDayListInfoVo.getCellData(j, "dayOfWeek");
			// 일정보 라벨 설정 
			var sDayLabel;
			sDayLabel = sDay + " (" + sDayOfWeek + ")";
	//		txt_day_info.setValue(sDayLabel);			
	//		grd_day.setHeaderValue("col_day" , sDayLabel); 
			//일정ID
			var schdPlanId = dst_SchdPlanDayListInfoVo.getCellData(j, "schdPlanId");	
			// 일정구분(01:메모 02:기념일, 03:방문, 04:전화)
			var schdFlgcd = dst_SchdPlanDayListInfoVo.getCellData(j, "schdFlgcd");
			var schdFlgNm = dst_SchdPlanDayListInfoVo.getCellData(j, "schdFlgNm");	
			// 기념일구분(일정구분이 02인 경우 기념일구분을 보여줌 
			// 01:생일 , 02:결혼, 03:기타, 04:음력생일
			var admMmdtFlgcd = dst_SchdPlanDayListInfoVo.getCellData(j, "admMmdtFlgcd");
			var admMmdtFlgNm = dst_SchdPlanDayListInfoVo.getCellData(j, "admMmdtFlgNm");
			var custNm	= dst_SchdPlanDayListInfoVo.getCellData(j, "custNm");
			var custNo = dst_SchdPlanDayListInfoVo.getCellData(j, "custNo");
			// 방문인 경우 시간 표시 
			var schdTime = dst_SchdPlanDayListInfoVo.getCellData(j, "schdTime");
			// 방문인 경우 일정구분명 표시 
			var cstSchdFlgNm = dst_SchdPlanDayListInfoVo.getCellData(j, "cstSchdFlgNm");
			//방문결과 01 :완료
			var visitResult = dst_SchdPlanDayListInfoVo.getCellData(j, "visitResult");
			// 전화인 경우 핸드폰번호 표시
			var hpNo = dst_SchdPlanDayListInfoVo.getCellData(j, "hpNo");
			// 기념일인 경우 기념일자 표시 
			var mmdt = dst_SchdPlanDayListInfoVo.getCellData(j, "mmdt");
			var memoYn = dst_SchdPlanDayListInfoVo.getCellData(j, "memoYn");
			// 방문메모내용 
			var cstSchdMemo = dst_SchdPlanDayListInfoVo.getCellData(j, "cstSchdMemo");
			if(schdFlgcd == "01")	// 메모 
			{			
				var memo;
				if(memoYn == "Y") {
	//				memo = "<img src='/cm/images/contents/ico_tit_date.png'> ";
					memo = dst_SchdPlanInfoVo.get("memo");
//					txt_day_memo.setValue(memo);
//					txt_day_memo.setReadOnly(false);
					dst_SchdMemoInfoVoList.setCellData(0, "cont", memo);
					dst_SchdMemoInfoVoList.setCellData(0, "rgtrEmpno", doerNo);  
					dst_SchdMemoInfoVoList.setCellData(0, "rgstDate", sSearchDate);     					
				}
			}
			else
			{	
				util.appendRow(dsl_grdDay); 
				var nRow = dsl_grdDay.getRowPosition();
				dsl_grdDay.setCellData(nRow, "schdPlanId", schdPlanId);
				dsl_grdDay.setCellData(nRow, "schdFlgcd", schdFlgcd);
				if(schdFlgcd == "02")	// 기념일 
				{
					schdFlgNm = "<span class='birth'> " + schdFlgNm;
					dsl_grdDay.setCellData(nRow, "schdFlgNm", schdFlgNm);  
					// 생일/결혼/기타
					dsl_grdDay.setCellData(nRow, "cstSchdFlgNm", admMmdtFlgNm);  
					// 고객명 	
					dsl_grdDay.setCellData(nRow, "custNm", custNm);  
					// 내용 
					dsl_grdDay.setCellData(nRow, "cstSchdMemo", cstSchdMemo);  
				}
				else if(schdFlgcd == "03")	// 방문 
				{
					schdFlgNm = "<span class='visit2'> " + schdFlgNm;
					dsl_grdDay.setCellData(nRow, "schdFlgNm", schdFlgNm);    
					// 증권전달(자)/계약관리
					dsl_grdDay.setCellData(nRow, "cstSchdFlgNm", cstSchdFlgNm);  
					// 고객명 + (방문시간)	
					custNm += " ("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")";					
					dsl_grdDay.setCellData(nRow, "custNm", custNm);  
					// 방문시간 
//					dsl_grdDay.setCellData(nRow, "schdTime", "("+schdTime.substring(0,2)+":"+schdTime.substring(2,4)+")");
					// 내용 
					dsl_grdDay.setCellData(nRow, "cstSchdMemo", cstSchdMemo);  
				}
				else if(schdFlgcd == "04")	// 전화
				{
					schdFlgNm = "<span class='call'> " + schdFlgNm;
					dsl_grdDay.setCellData(nRow, "schdFlgNm", schdFlgNm); 

					// 고객명 	
					dsl_grdDay.setCellData(nRow, "custNm", custNm);  
					// 내용 
					dsl_grdDay.setCellData(nRow, "cstSchdMemo", cstSchdMemo);
				}
			}			
		}
		// 일정구분코드로 sort
		dsl_grdDay.sort('schdFlgcd', 0);
	};

