	//**********************************************************
	// 일정메모등록/수정
	//**********************************************************		
	scwin.btn_memo_onclick = function() {
		scwin.rgstSchdMemoInfo();	
	};

	//**********************************************************
	// 방문일정등록 버튼 클릭이벤트
	//**********************************************************	
	scwin.btn_openPop_CBSB003P_onclick = function(schdPlanId) {
		var promise = Promise.resolve();	        
	    promise = promise.then(function(rtnVal) {	
			// [고객방문관리(CBSB003P)]화면을 오픈.	
			var indata  = {};
			indata["pCstSchdAdmId"] = schdPlanId;
			indata["empNo"] = ib_searchEmpNo.getValue();
	    	indata["empNm"] = dst_SchdPlanInfoVo.get("empNm");		

			var popInfo = {
			    "width"     : 720,
			    "height"    : 300,
			    "top"       : 100,
			    "left"      : 200,
			    "indata"    : indata,
			    //"callback"  : scwin.popupCallback,
			    "title"     : "고객방문관리"
			};
			return wprom.popupOpen("/ui/cbsb/CBSB003P.xml", popInfo);
		});

		// 등록완료 되면 재조회 
		promise = promise.then(function(rtnVal) {	
			var rtnValue = wprom.getReturnData();
			if (rtnValue != "") {
				if(rtnValue == "I") {
	            	// 재조회 
	            	scwin.btn_Search_onclick();
	            }		            
			}
		});

	};

	//**********************************************************
	// 사번 인풋박스 뷰체인지 이벤트 발생 
	//**********************************************************		
	scwin.ib_searchEmpNo_onviewchange = function(info) {

		var empNo = ib_searchEmpNo.getValue();
		if(wconv.length(empNo) == 7) {
			//∙ 조직 관리자 SuperAuthYn='Y' 는 본인 조직의 취급자 사번 입력 가능 
			// 동일소속여부 조회 (슈퍼권한자이면서 업무권한자가 아닌경우에만 동일소속조회)
			if(dst_LginAuthInqrVo.get("superAuthYn") == "Y" && dst_LginAuthInqrVo.get("bizAdmrYn") != "Y")
	  		{
	  			scwin.getSamePstnAuthInqr();
	  		}	
		}	
	};


	//**********************************************************
	//  주 클릭 이벤트 발생 
	//**********************************************************			
	scwin.txt_1stWeek_onclick = function(e) {
		// INPUT DS 세팅
		// 조회기준일자 
		var stdt;		//시작일자
		var enddt;		//종료일자		
		var weekFlag;  	//주구분 - 1 :1주, 2:2주, 3:3주, 4:4주, 5:5주
		var stndDate;
		// 조회기준일자의 시작일자, 종료일자, 주정보를 가져온다. 
		for(var i=0; i<dst_SchdPlanMonListInfoVo.getRowCount(); i++) {			
			weekFlag = dst_SchdPlanMonListInfoVo.getCellData(i, "week");
			if(weekFlag == 1) {
				stndDate = dst_SchdPlanMonListInfoVo.getCellData(i, "stndDate");	
	//			stdt = stndDate;
	//			enddt = stndDate;
				break;
			}
		}	

		// 주별 캘린더만 보여주도록 함  sw_div2		
		switch_div.show();
        switch_div.setSelectedID("sw_div2");
        sw_div2.setStyle("display" , "");         
        sw_div1.setStyle("display" , "none");  
        sw_div3.setStyle("display" , "none"); 
        gCurrDiv  = 2;

        // 활동계획(주) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
/*        btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml off","btn_nml on");
        btn_schdPlanDay.changeClass("btn_nml on","btn_nml off");*/

     	// 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
     	cal_searchPeriod.setCalendarValueType("yearMonthDate");  	
     	cal_searchPeriod.setValue(stndDate);

		scwin.getWeekCstSchdPlanInfo(1, stndDate, stdt, enddt);
	};

	scwin.txt_2ndWeek_onclick = function(e) {
		// INPUT DS 세팅
		// 조회기준일자 
		var stdt;		//시작일자
