	//**********************************************************
	// 월활동계획에서 일정보 클릭 이벤트 발생 
	//**********************************************************				
	scwin.MonPlanList_onclick = function() {
		// 일별 캘린더만 보여주도록 함  sw_div3	
		switch_div.show();
        switch_div.setSelectedID("sw_div3");
        sw_div3.setStyle("display" , "");        
        sw_div1.setStyle("display" , "none");  
        sw_div2.setStyle("display" , "none"); 
        gCurrDiv  = 3;

        // 활동계획(일) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
/*        btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanDay.changeClass("btn_nml off","btn_nml on");*/

        // 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
        cal_searchPeriod.setCalendarValueType("yearMonthDate");  

        var userData1 = this.getUserData("userData1")	// 요일
        var userData2 = this.getUserData("userData2")	// 주

        var sDayOfWeek;
        var sWeek;
        var stndDate
        for(var i=0; i<dst_SchdPlanMonListInfoVo.getRowCount(); i++) {		

			sDayOfWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "dayOfWeek");
			sWeek = dst_SchdPlanMonListInfoVo.getCellData(i, "week");
			if(sDayOfWeek == userData1 && sWeek == userData2)
			{ 
				stndDate = dst_SchdPlanMonListInfoVo.getCellData(i, "stndDate");
				break;
			}		
        }
        // 일정보 조회
     	cal_searchPeriod.setValue(stndDate);
     	scwin.getDayCstSchdPlanInfo(sDayOfWeek, stndDate);

	};

