/*        btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanDay.changeClass("btn_nml off","btn_nml on");*/

        // 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
        cal_searchPeriod.setCalendarValueType("yearMonthDate");  
     	cal_searchPeriod.setValue(stndDate);
        // 일정보 조회
     	scwin.getDayCstSchdPlanInfo(dayOfWeek, stndDate);	
	};

	scwin.grd_weekSat_oncellclick = function(row,col) {
		var stndDate =	dsl_grdWeekSat.getCellData(row , "stndDate");
		var dayOfWeek = dsl_grdWeekSat.getCellData(row , "dayOfWeek");

		// 일별 캘린더만 보여주도록 함  sw_div3	
		switch_div.show();
        switch_div.setSelectedID("sw_div3");
        sw_div3.setStyle("display" , "");        
        sw_div1.setStyle("display" , "none");  
        sw_div2.setStyle("display" , "none"); 
        gCurrDiv  = 3;

        // 활동계획(일) 버튼  on 
        // 2017.11.17 다 on 시키도록 아래주석처리
/*        btn_schdPlanMon.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanWeek.changeClass("btn_nml on","btn_nml off");
        btn_schdPlanDay.changeClass("btn_nml off","btn_nml on");*/

        // 조회기준일자 캘린더 포맷 년월일 로 표시되도록 한다.
        cal_searchPeriod.setCalendarValueType("yearMonthDate");  
     	cal_searchPeriod.setValue(stndDate);        
        // 일정보 조회
     	scwin.getDayCstSchdPlanInfo(dayOfWeek, stndDate);	
	};

	//**********************************************************
	//  일활동계획 그리드 클릭이벤트 
	//**********************************************************	
	scwin.grd_day_oncellclick = function(row,col) {
		var schdFlgcd =	dsl_grdDay.getCellData(row , "schdFlgcd");	
		var schdPlanId = dsl_grdDay.getCellData(row, "schdPlanId");
		// 일정구분(01:메모 02:기념일, 03:방문, 04:전화) 
		// 방문이나 전화인 경우
		if(schdFlgcd == "03" || schdFlgcd == "04") {
			// [고객방문관리(CBSB003P)]화면을 오픈
			scwin.btn_openPop_CBSB003P_onclick(schdPlanId);
		}
	};

	//**********************************************************
	//  조회일자 키다운이벤트  
	//**********************************************************	
	scwin.cal_searchPeriod_onkeydown = function(e) {
		if(e.keyCode == 13) {
			scwin.btn_Search_onclick();
		}	
	};

	//**********************************************************
	// 권한자 인 경우 사번입력후 키다운이벤트
	//**********************************************************	
	scwin.ib_searchEmpNo_onkeydown = function(e) {
		if(e.keyCode == 13) {
			scwin.btn_Search_onclick();
		}			
	};
