head.script:
	var gsSysDate;			// global 현재일자
	var gUserID; 			// global 사용자 ID
	var gUserEmpName; 	    // global 사용자명
	var gDptOfficeCode; 	    // global 부서코드
	var gDptOfficeName; 	    // global 부서명
	var ib_custNo_Event;
	var ib_offceCd_Event;
	var ib_inputCustNo_Event;
	var ib_inputDepartCode_Event;


	//**********************************************************
	// 초기화
	//**********************************************************
	scwin.init = function() {

		gsSysDate 		= WebSquare.date.getCurrentServerDate("yyyyMMdd"); 	// global 현재일자
		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 사용자명					
		gDptOfficeCode	= util.getGdsValue("gDptOfficeCode");    			// global 부서코드
		gDptOfficeName	= util.getGdsValue("gDptOfficeName");    			// global 부서명

		ic_dateFr.setValue(gsSysDate);
		ic_dateTo.setValue(gsSysDate);	
		ib_stndYmd.setValue(gsSysDate);	


	    var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{4}[0-9\s]{7}$/,
	    						/^[0-9\s]{6}$/,
	    							/^[0-9\s]{6}[0-9\s]{1}[A-Z0-9\s]{6}$/
	    								,/^[0-9A-Z\s]{7}$/
	    									,/^[ㄱ-ㅎㅏ-ㅣ가-힣A-Za-z0-9\s]{20}$/ ];
		var maxLength  = [12,6,13,7,20];
		var chkIndex   = 0;	
		ib_custNo.setPlaceholder( "");
		ib_custNo_Event = new wMaskEditObj(ib_custNo, maskRegExp, maxLength, chkIndex);	
		ib_custNo.setPlaceholder("______-_______");
		ib_custNo.setDisplayFormat("######-#######");
		ib_custNo_Event.setIndex(2);

		ib_inputCustNo.setPlaceholder( "");
		ib_inputCustNo_Event = new wMaskEditObj(ib_inputCustNo, maskRegExp, maxLength, chkIndex);	
		ib_inputCustNo.setPlaceholder("______-_______");
		ib_inputCustNo.setDisplayFormat("######-#######");
		ib_inputCustNo_Event.setIndex(2);

		ib_offceCd.setPlaceholder( "");
		ib_offceCd_Event = new wMaskEditObj(ib_offceCd, maskRegExp, maxLength, chkIndex);	
		ib_offceCd.setPlaceholder("_______");
		ib_offceCd.setDisplayFormat("#######");
		ib_offceCd_Event.setIndex(3);

		ib_inputDepartCode.setPlaceholder( "");
		ib_inputDepartCode_Event = new wMaskEditObj(ib_offceCd, maskRegExp, maxLength, chkIndex);
		ib_inputDepartCode.setPlaceholder("_______");
		ib_inputDepartCode.setDisplayFormat("#######");
		ib_inputDepartCode_Event.setIndex(3);

		//사용인일괄등록버튼 업무확인
		//btn_excelDown1.visible(false);

		//사용인엑셀다운 그리드 hide
		grd_SDataList.setStyle("display" , "none");

	    //----- 버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
		gfn_btnEnable(false, false, false, false, false, false, false, true);


	    var promise = Promise.resolve();
	    promise = promise.then(function(rtnVal) {
	     	// /common/CBSO0015.do 로그인자 권한조회	 
			scwin.getLginAuthInfo();	  			
	    });

	    return promise;	

	};

	//**********************************************************
	// 로그인자 권한조회 /common/CBSO0015.do 로그인자 권한조회
	//**********************************************************
	scwin.getLginAuthInfo = function() {
		var promise = Promise.resolve();
		// 사번으로 로그인자 권한조회	
		dst_LginAuthInqrVo.set("empNo" , gUserID);

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveLginIDAuthInqr");        	// transaction 보낼 SubmissionID
		trans.setUrl("/common/CBSO0015.do");   			// Controller 쪽으로 보낼 주소
		trans.setDs("dst_LginAuthInqrVo");					
		trans.targetDs("dst_LginAuthInqrVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 
	//**********************************************************
	scwin.subCallBackfunction = function(e){
		// e.subID : 서브미션 ID
		// e.flag  : S성공;
		// e.msg   : 트렌젝션 처리후 메세지
		// e."받을데이타셋"
		switch(e.subID){
			// 로그인ID권한조회
			case "retrieveLginIDAuthInqr":			
				if(e.flag == "s") {
					dst_LginAuthInqrVo.setJSON(e.result);	

					//장기업무부 - 4000103 bizAdmrYn
					//모집인관리자 - 34025 clprAdmrYn
					//대면총무/지점장 - 34024 geafAdmrYn
					//대리점사용인관리자 - 21013 agncyAdmrYn
					if(dst_LginAuthInqrVo.get("bizAdmrYn") =="Y"
						|| dst_LginAuthInqrVo.get("clprAdmrYn") =="Y"
						|| dst_LginAuthInqrVo.get("geafAdmrYn") =="Y"
						|| dst_LginAuthInqrVo.get("agncyAdmrYn") =="Y"						
						){											
						btn_Search.setDisabled(false);

						//전체조회
						scwin.btn_Search_onclick();

					//화면조회권한이 없습니다.	
					}else{
						btn_Search.setDisabled(true);
						btn_dataInit.setDisabled(true); //초기화버튼
						btn_dataSave.setDisabled(true);	//저장버튼
						btn_dataDel.setDisabled(true);	//삭제버튼						
        				wprom.alert("화면조회권한이 없습니다.");
        				return;
					}							
				} else {
					wprom.alert(e.msg);
				}														
			break;							
			// 모집인정보 등록
			case "registerClprRgstRqst":
				if(e.flag == "s") {	
					//전체조회
					scwin.btn_Search_onclick();
					//등록정보 초기화
					scwin.btn_dataInit_onclick();
				} else {
					wprom.alert(e.msg);
				}														
			break;				
			// 모집인정보 삭제
			case "modifyClprRgstRqstDelete":
				if(e.flag == "s") {					
					//전체조회
					scwin.btn_Search_onclick();
					//등록정보 초기화
					scwin.btn_dataInit_onclick();
				} else {
					wprom.alert(e.msg);
				}														
			break;	
			// 모집인신청정보조회
			case "retrieveClprRgstRqstList":				
				if(e.flag == "s") {						
					dst_ClprRgstRqstInfoListVo.removeAll();			
					dst_ClprRgstRqstInfoListVo.setJSON(e.result.clprRgstRqstInfoVo);

					if(dst_ClprRgstRqstInfoListVo.getRowCount()==0){
			        	wprom.alert("조회결과가 없습니다.");        	
			        	return;	
					}else{
						//등록정보 초기화
						scwin.btn_dataInit_onclick();
					}

				} else {
					wprom.alert(e.msg);
				}														
			break;					
			// 모집인다건저장
			case "registerClprRgstRqstList":
				if(e.flag == "s") {		

					//등록정보 초기화
					scwin.btn_dataInit_onclick();
				} else {
					wprom.alert(e.msg);
				}														
			break;
		}
	};

	//**********************************************************
	// 모집인등록등록정보 setting 
	//**********************************************************
	scwin.setClprRgstRqstInfo = function() {

		dst_ClprRgstRqstInfoVo.set("stndYmd",ib_stndYmd.getValue());
		dst_ClprRgstRqstInfoVo.set("clltFlagCd",sel_clltFlagCd.getValue());
		dst_ClprRgstRqstInfoVo.set("carerCd",sel_CarerCd.getValue());

		var custKey = scwin.lfn_getCustNo(ib_custNo.getValue());
		if(!util.isNull(custKey)){						
			dst_ClprRgstRqstInfoVo.set("custNo", custKey);//주민등록번호
		}else{
			dst_ClprRgstRqstInfoVo.set("custNo", ib_custNo.getValue());//주민등록번호
		}	


		dst_ClprRgstRqstInfoVo.set("name",ib_name.getValue());		
		dst_ClprRgstRqstInfoVo.set("demagInsurRqstYn",sel_demagInsurYn.getValue());
		dst_ClprRgstRqstInfoVo.set("thrdInsurRqstYn",sel_thrdInsurYn.getValue());		
		dst_ClprRgstRqstInfoVo.set("offcCd",ib_offceCd.getValue());
		dst_ClprRgstRqstInfoVo.set("offcName",ib_offceNm.getValue());
		dst_ClprRgstRqstInfoVo.set("psnInfoWrcsRvYn",chk_psnInfoWrcsRvYn.getValue());
		dst_ClprRgstRqstInfoVo.set("notcMattRvYn",chk_notcMattRvYn.getValue());
		dst_ClprRgstRqstInfoVo.set("lfinrCmpnCd",sel_lfinrCmpnCd.getValue());
		dst_ClprRgstRqstInfoVo.set("crossLfinrQualfYn",chk_crossLfinrQualfYn.getValue());
		dst_ClprRgstRqstInfoVo.set("crossThrdInsurQualfYn",chk_crossThrdInsurQualfYn.getValue());
		dst_ClprRgstRqstInfoVo.set("post",ib_post.getValue());
		dst_ClprRgstRqstInfoVo.set("dongBelo",ib_dongBelo.getValue());

	};

	//**********************************************************
	// 모집인정보 setting 
	//**********************************************************
	scwin.setClprErsrInfo = function() {

		btn_dataInit.setDisabled(false); //초기화버튼
		btn_dataSave.setDisabled(false); //저장버튼
		btn_dataDel.setDisabled(true);	//삭제버튼		

		//심사요청(01)이후에는 등록시 기준년월처리 확인필요 - Todo		

		ib_stndYmd.setValue(gsSysDate);
		sel_clltFlagCd.setValue(dst_ClprRgstRqstInfoVo.get("clltFlagCd"));
		sel_CarerCd.setValue(dst_ClprRgstRqstInfoVo.get("carerCd"));
		ib_custNo.setValue(dst_ClprRgstRqstInfoVo.get("custNo"));
		ib_name.setValue(dst_ClprRgstRqstInfoVo.get("name"));
		sel_demagInsurYn.setValue(dst_ClprRgstRqstInfoVo.get("demagInsurRqstYn"));
		sel_thrdInsurYn.setValue( dst_ClprRgstRqstInfoVo.get("thrdInsurRqstYn"));
		ib_offceCd.setValue(dst_ClprRgstRqstInfoVo.get("offcCd"));
		ib_offceNm.setValue(dst_ClprRgstRqstInfoVo.get("offcName"));
		chk_psnInfoWrcsRvYn.setValue(dst_ClprRgstRqstInfoVo.get("psnInfoWrcsRvYn"));
		chk_notcMattRvYn.setValue(dst_ClprRgstRqstInfoVo.get("notcMattRvYn"));
		sel_lfinrCmpnCd.setValue(dst_ClprRgstRqstInfoVo.get("lfinrCmpnCd"));
		chk_crossLfinrQualfYn.setValue(dst_ClprRgstRqstInfoVo.get("crossLfinrQualfYn"));
		chk_crossThrdInsurQualfYn.setValue(dst_ClprRgstRqstInfoVo.get("crossThrdInsurQualfYn"));
		ib_post.setValue(dst_ClprRgstRqstInfoVo.get("post"));
		ib_dongBelo.setValue(dst_ClprRgstRqstInfoVo.get("dongBelo"));

	};			


	//**********************************************************
	// 초기화 버튼 클릭시
	//**********************************************************
	scwin.btn_dataInit_onclick = function() {		

		//ib_stndYmd.setValue("");
		sel_clltFlagCd.setValue("");
		sel_CarerCd.setValue("");
		ib_custNo.setValue("");
		ib_name.setValue("");
		sel_demagInsurYn.setValue("");
		sel_thrdInsurYn.setValue("");
		ib_offceCd.setValue("");
		ib_offceNm.setValue("");
		chk_psnInfoWrcsRvYn.setValue("");
		chk_notcMattRvYn.setValue("");
		sel_lfinrCmpnCd.setValue("");
		chk_crossLfinrQualfYn.setValue("");
		chk_crossThrdInsurQualfYn.setValue("");
		ib_post.setValue("");		
		ib_dongBelo.setValue("");

	};

	//**********************************************************
	// 저장 버튼시 - /sales/support/CBSB0025.do
	//**********************************************************
	scwin.btn_dataSave_onclick = function() {

		if(util.isNull(ib_stndYmd.getValue())){			
        	wprom.alertF("기준일자를 입력해 주세요.",{},ib_stndYmd);
        	return;
		}
		if(util.isNull(sel_clltFlagCd.getValue())){			
        	wprom.alertF("소속구분을 입력해 주세요.",{},sel_clltFlagCd);
        	return;
		}		
		if(util.isNull(sel_CarerCd.getValue())){			
        	wprom.alertF("경력을 입력해 주세요.",{},sel_CarerCd);
        	return;
		}
		if(util.isNull(ib_custNo.getValue())){			
        	wprom.alertF("주민등록번호를 입력해 주세요.",{},ib_custNo);
        	return;
		}
		if(ib_custNo.getValue().length<13){			
        	wprom.alertF("주민등록번호는 13자리 입니다.",{},ib_custNo);
        	return;
		}				
		if(util.isNull(ib_name.getValue())){			
        	wprom.alertF("이름을 입력해 주세요.",{},ib_name);
        	return;
		}
		if(util.isNull(sel_demagInsurYn.getValue())){			
        	wprom.alertF("영업범위(손보)를 입력해 주세요.",{},sel_demagInsurYn);
        	return;
		}
		if(util.isNull(sel_thrdInsurYn.getValue())){			
        	wprom.alertF("영업범위(제3보)를 입력해 주세요.",{},sel_thrdInsurYn);
        	return;
		}
		if(util.isNull(ib_offceCd.getValue())){			
        	wprom.alertF("점포(대리점)코드를 입력해 주세요.",{},ib_offceCd);
        	return;
		}
		if(util.isNull(ib_offceNm.getValue())){			
        	wprom.alertF("점포(대리점)이름을 입력해 주세요.",{},ib_offceNm);
        	return;
		}
		if(util.isNull(chk_psnInfoWrcsRvYn.getValue())){			
        	wprom.alertF("개인정보활용동의를 입력해 주세요.",{},chk_psnInfoWrcsRvYn);
        	return;
		}		
		if(!util.isNull(sel_lfinrCmpnCd.getValue())){	
			if(util.isNull(chk_crossLfinrQualfYn.getValue())
				&& util.isNull(chk_crossThrdInsurQualfYn.getValue())){
        		wprom.alertF("교차 영업범위를 입력해 주세요.",{},chk_crossLfinrQualfYn);
        		return;			
			}		
		}else{
			if(!util.isNull(chk_crossLfinrQualfYn.getValue())
				|| !util.isNull(chk_crossThrdInsurQualfYn.getValue())){
        		wprom.alertF("교차보험회사를 입력해 주세요.",{},sel_lfinrCmpnCd);
        		return;			
			}
		}
		if(util.isNull(chk_notcMattRvYn.getValue())){			
        	wprom.alertF("고지사항 수령여부를 입력해 주세요.",{},chk_psnInfoWrcsRvYn);
        	return;
		}

		if(util.isNull(ib_post.getValue())){			
        	wprom.alertF("우편번호를 입력해 주세요.",{},btn_postNo);
        	return;
		}	
		if(util.isNull(ib_dongBelo.getValue())){			
        	wprom.alertF("동이하 주소를 입력해 주세요.",{},ib_dongBelo);
        	return;
		}

		if(sel_demagInsurYn.getValue() == "0"
			&& sel_thrdInsurYn.getValue() == "0"){			
        	wprom.alertF("영업범위는 손보/제3보중 하나는 꼭 신청되어야 합니다.",{},sel_demagInsurYn);
        	return;
		}

		//저장데이터 setting
		scwin.setClprRgstRqstInfo();

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("registerClprRgstRqst");        		// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0021.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprRgstRqstInfoVo");					
		trans.targetDs("dst_ClprRgstRqstInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 삭제 버튼시 - /sales/support/CBSB0026.do
	//**********************************************************
	scwin.btn_dataDel_onclick = function() {

		//삭제데이터 setting
		scwin.setClprRgstRqstInfo();

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("modifyClprRgstRqstDelete");        	// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0022.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprRgstRqstInfoVo");					
		trans.targetDs("dst_ClprRgstRqstInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	//**********************************************************
	// 모집인 리스트 조회 - /sales/support/CBSB0024.do
	//**********************************************************
	scwin.btn_Search_onclick = function() {		
		//모집인 리스트 조회 키 setting
		scwin.setClprErsrSerchInparam();		
		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveClprRgstRqstList");        	// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0019.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprRgstRqstInfoVo");					
		trans.targetDs("dst_ClprRgstRqstInfoListVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	//**********************************************************
	// 모집인조회KEY정보 setting 
	//**********************************************************
	scwin.setClprErsrSerchInparam = function() {		
		dst_ClprRgstRqstInfoVo.set("dateFr",ic_dateFr.getValue());//조회시작일자
		dst_ClprRgstRqstInfoVo.set("dateTo",ic_dateTo.getValue());//조회종료일자
		dst_ClprRgstRqstInfoVo.set("clltFlagCd",sel_inputClltFlagCd.getValue());//소속구분
		dst_ClprRgstRqstInfoVo.set("departCode",ib_inputDepartCode.getValue());//부서코드
		var custKey = scwin.lfn_getCustNo(ib_inputCustNo.getValue());		
		if(!util.isNull(custKey)){						
			dst_ClprRgstRqstInfoVo.set("custNo", custKey);//주민등록번호
		}else{
			dst_ClprRgstRqstInfoVo.set("custNo", ib_inputCustNo.getValue());//주민등록번호
		}		
		dst_ClprRgstRqstInfoVo.set("name",ib_inputName.getValue());//이름						
	};

	//**********************************************************
	// 그리드 정보로 업무 setting 
	//**********************************************************
	scwin.grd_dataList_oncellclick = function(row,col) {	

		var colId = grd_dataList.getColumnID(col);	

		//설계사/사용인 관리화면 오픈
		if(colId == "rgstRqstAvaYn"){			
			var rgstRqstAvaYn = grd_dataList.getCellData(row, "rgstRqstAvaYn");
			var clltFlagCd = grd_dataList.getCellData(row, "clltFlagCd");

			//채번가능상태인 경우
			if(rgstRqstAvaYn == "Y"){
				var promise = Promise.resolve();
			    promise = promise.then(function(rtnVal) {

			        //추후, 화면 오픈시 데이터 값 setting -- Todo
					//설계사 화면 오픈
					if(clltFlagCd == "M"){
						scwin.openCBSB014M(grd_dataList.getCellData(row, "custNo"));				

					//사용인 화면 오픈
					}else if(clltFlagCd == "S"){
						scwin.openCBSB010M(grd_dataList.getCellData(row, "custNo"));								
					}
				});				

				//등록정보 초기화
				scwin.btn_dataInit_onclick();
			}					

		//하단 데이터 setting 	
		}else{		
			ib_stndYmd.setValue(grd_dataList.getCellData(row,"stndYmd"));
			sel_clltFlagCd.setValue(grd_dataList.getCellData(row,"clltFlagCd"));
			sel_CarerCd.setValue(grd_dataList.getCellData(row,"carerCd"));

			var custKey = scwin.lfn_getRealNo(grd_dataList.getCellData(row,"custNo"));
			if(!util.isNull(custKey)){						
				ib_custNo.setValue(custKey);
			}else{
				ib_custNo.setValue(grd_dataList.getCellData(row,"custNo"));
			}

			//ib_custNo.setValue(grd_dataList.getCellData(row,"custNo"));
			ib_name.setValue(grd_dataList.getCellData(row,"name"));
			sel_demagInsurYn.setValue(grd_dataList.getCellData(row,"demagInsurRqstYn"));
			sel_thrdInsurYn.setValue( grd_dataList.getCellData(row,"thrdInsurRqstYn"));
			ib_offceCd.setValue(grd_dataList.getCellData(row,"offcCd"));
			ib_offceNm.setValue(grd_dataList.getCellData(row,"offcName"));
			chk_psnInfoWrcsRvYn.setValue(grd_dataList.getCellData(row,"psnInfoWrcsRvYn"));
			chk_notcMattRvYn.setValue(grd_dataList.getCellData(row,"notcMattRvYn"));
			sel_lfinrCmpnCd.setValue(grd_dataList.getCellData(row,"lfinrCmpnCd"));
			chk_crossLfinrQualfYn.setValue(grd_dataList.getCellData(row,"crossLfinrQualfYn"));
			chk_crossThrdInsurQualfYn.setValue(grd_dataList.getCellData(row,"crossThrdInsurQualfYn"));
			ib_post.setValue(grd_dataList.getCellData(row,"post"));
			ib_dongBelo.setValue(grd_dataList.getCellData(row,"dongBelo"));				

			//처리상태에 따라 초기화, 저장, 삭제버튼 활성/비활성 처리
			//상태 - 00: 등록, 01: 신청, 02:심사결과수신, 03:등록결과수신, 04:처리완료
			var dlngStat = grd_dataList.getCellData(row,"dlngStat");//처리상태

			btn_dataInit.setDisabled(false);//초기화버튼									
			//저장
			if(dlngStat == "00"){									
				btn_dataDel.setDisabled(false);//삭제버튼
				btn_dataSave.setDisabled(false);//저장버튼
				ib_stndYmd.setDisabled(false);
				sel_clltFlagCd.setDisabled(false);
				sel_CarerCd.setDisabled(false);
				ib_custNo.setDisabled(false);
				ib_name.setDisabled(false);
				sel_demagInsurYn.setDisabled(false);
				sel_thrdInsurYn.setDisabled(false);
				ib_offceCd.setDisabled(false);
				ib_offceNm.setDisabled(false);
				chk_psnInfoWrcsRvYn.setDisabled(false);
				sel_lfinrCmpnCd.setDisabled(false);
				chk_crossLfinrQualfYn.setDisabled(false);
				chk_crossThrdInsurQualfYn.setDisabled(false);
				chk_notcMattRvYn.setDisabled(false);
				ib_post.setDisabled(false);
				ib_dongBelo.setDisabled(false);
				btn_postNo.setDisabled(false);

			}else{
				btn_dataDel.setDisabled(true);//삭제버튼
				btn_dataSave.setDisabled(true);//저장버튼
				ib_stndYmd.setDisabled(true);
				sel_clltFlagCd.setDisabled(true);
				sel_CarerCd.setDisabled(true);
				ib_custNo.setDisabled(true);
				ib_name.setDisabled(true);
				sel_demagInsurYn.setDisabled(true);
				sel_thrdInsurYn.setDisabled(true);
				ib_offceCd.setDisabled(true);
				ib_offceNm.setDisabled(true);
				chk_psnInfoWrcsRvYn.setDisabled(true);
				sel_lfinrCmpnCd.setDisabled(true);
				chk_crossLfinrQualfYn.setDisabled(true);
				chk_crossThrdInsurQualfYn.setDisabled(true);
				chk_notcMattRvYn.setDisabled(true);
				ib_post.setDisabled(true);
				ib_dongBelo.setDisabled(true);
				btn_postNo.setDisabled(true);
			}
		}	
	};

