	// 등록처리 위한 화면이동버튼 setting 
	//**********************************************************
	scwin.setRgstRqstAvaYn = function(data, formattedData, rowIndex, colIndex) {
		var rgstRqstAvaYn = grd_dataList.getCellData(rowIndex, "rgstRqstAvaYn");
		// 첨부파일명이 존재하면 이미지 표시
		if(rgstRqstAvaYn == "Y"){
			formattedData = "<img src='/cm/images/content/detail_link_ico.png'>";			
		//등록된 경우에 처리완료 표시	
		}else if(rgstRqstAvaYn == "S"){		
			formattedData = "등록";					
		// 첨부파일명이 널이면 '-'표시 
		}else {
			formattedData = "-";
		}	
		return formattedData;	
	};

