    //**********************************************************
	// 모집인 등록신청 엑셀 업로드 저장
	//**********************************************************
	scwin.btn_excelUpSave_onclick = function() {
		dst_ClprRgstRqstListRequestVo.removeAll();		

		alert("기준일자는 당일로 처리됩니다.");

		if(grd_dataList.getRowCount()>100){			
        	wprom.alertF("한번에 최대 100건 등록 가능합니다.",{},btn_excelUpSave);
        	return;
		}

		//발송대상 고객리스트 			
		for(var i=0; i<grd_dataList.getRowCount(); i++) {			

			util.appendRow(dst_ClprRgstRqstListRequestVo); 
			var nRow = dst_ClprRgstRqstListRequestVo.getRowPosition();

			var custKey = scwin.lfn_getCustNo(grd_dataList.getCellData(i, "custNo"));
			if(!util.isNull(custKey)){						
				dst_ClprRgstRqstListRequestVo.setCellData( nRow , "custNo" ,  custKey);
			}else{		
				dst_ClprRgstRqstListRequestVo.setCellData( nRow , "custNo" ,  grd_dataList.getCellData(i, "custNo"));
			}	

			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "name" , grd_dataList.getCellData(i, "name") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "clltFlagCd" , grd_dataList.getCellData(i, "clltFlagCd") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "demagInsurRqstYn" , grd_dataList.getCellData(i, "demagInsurRqstYn") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "thrdInsurRqstYn" , grd_dataList.getCellData(i, "thrdInsurRqstYn") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "offcCd" , grd_dataList.getCellData(i, "offcCd") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "offcName" , grd_dataList.getCellData(i, "offcName") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "post" , grd_dataList.getCellData(i, "post") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "dongBelo" , grd_dataList.getCellData(i, "dongBelo") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "lfinrCmpnCd" , grd_dataList.getCellData(i, "lfinrCmpnCd") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "crossLfinrQualfYn" , grd_dataList.getCellData(i, "crossLfinrQualfYn") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "crossThrdInsurQualfYn" , grd_dataList.getCellData(i, "crossThrdInsurQualfYn") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "notcMattRvYn" , grd_dataList.getCellData(i, "notcMattRvYn") );
			dst_ClprRgstRqstListRequestVo.setCellData( nRow , "psnInfoWrcsRvYn" , grd_dataList.getCellData(i, "psnInfoWrcsRvYn") );										
		}

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("registerClprRgstRqstList");        // transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0020.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprRgstRqstListRequestVo:I:clprRgstRqstInfoVo");
		trans.targetDs("dst_ClprRgstRqstInfoListVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

