head.script:
	var gsSysDate;			// global 현재일자
	var gUserID; 			// global 사용자 ID
	var gUserEmpName; 	    // global 사용자명
	var gDptOfficeCode; 	    // global 부서코드
	var gDptOfficeName; 	    // global 부서명
	var ib_inputCustNo_Event;
	var ib_inputDepartCode_Event;
	var ib_empNo_Event;


	//**********************************************************
	// 초기화
	//**********************************************************
	scwin.init = function() {
		gsSysDate 		= WebSquare.date.getCurrentServerDate("yyyyMMdd"); 	// global 현재일자
		gUserID 		= util.getGdsValue("gUserID");    					// global 사용자 ID
		gUserEmpName 	= util.getGdsValue("gUserEmpName");    				// global 사용자명					
		gDptOfficeCode	= util.getGdsValue("gDptOfficeCode");    				// global 부서코드
		gDptOfficeName	= util.getGdsValue("gDptOfficeName");    				// global 부서명

		ic_dateFr.setValue(gsSysDate);
		ic_dateTo.setValue(gsSysDate);	

	    var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{4}[0-9\s]{7}$/,
	    						/^[0-9\s]{6}$/,
	    							/^[0-9\s]{6}[0-9\s]{1}[A-Z0-9\s]{6}$/
	    								,/^[0-9A-Z\s]{7}$/
	    									,/^[ㄱ-ㅎㅏ-ㅣ가-힣A-Za-z0-9\s]{20}$/ ];
		var maxLength  = [12,6,13,7,20];
		var chkIndex   = 0;	
		ib_inputCustNo.setPlaceholder( "");
		ib_inputCustNo_Event = new wMaskEditObj(ib_inputCustNo, maskRegExp, maxLength, chkIndex);	
		ib_inputCustNo.setPlaceholder("______-_______");
		ib_inputCustNo.setDisplayFormat("######-#######");
		ib_inputCustNo_Event.setIndex(2);		

		ib_inputDepartCode.setPlaceholder( "");
		ib_inputDepartCode_Event = new wMaskEditObj(ib_inputDepartCode, maskRegExp, maxLength, chkIndex);
		ib_inputDepartCode.setPlaceholder("_______");
		ib_inputDepartCode.setDisplayFormat("#######");
		ib_inputDepartCode_Event.setIndex(3);

		ib_empNo.setPlaceholder( "");
		ib_empNo_Event = new wMaskEditObj(ib_empNo, maskRegExp, maxLength, chkIndex);
		ib_empNo.setPlaceholder("_______");
		ib_empNo.setDisplayFormat("#######");
		ib_empNo_Event.setIndex(3);


		chk_demagInsurYn.setDisabled(true);	//손해보험여부
		chk_thrdInsurYn.setDisabled(true);	//제3보험여부
		sel_clltFlagCd.setDisabled(true);	//소속구분

	    //----- 버튼 처리(입력, 수정, 삭제, 결재, 취소, 인쇄, 엑셀, 닫기)
		gfn_btnEnable(false, false, false, false, false, false, false, true);

	    var promise = Promise.resolve();
	    promise = promise.then(function(rtnVal) {
	     	// /common/CBSO0015.do 로그인자 권한조회	 
			scwin.getLginAuthInfo();	  			
	    });

	    return promise;	

	};

	//**********************************************************
	// 로그인자 권한조회 /common/CBSO0015.do 로그인자 권한조회
	//**********************************************************
	scwin.getLginAuthInfo = function() {
		var promise = Promise.resolve();
		// 사번으로 로그인자 권한조회	
		dst_LginAuthInqrVo.set("empNo" , gUserID);

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveLginIDAuthInqr");        	// transaction 보낼 SubmissionID
		trans.setUrl("/common/CBSO0015.do");   			// Controller 쪽으로 보낼 주소
		trans.setDs("dst_LginAuthInqrVo");					
		trans.targetDs("dst_LginAuthInqrVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 
	//**********************************************************
	scwin.subCallBackfunction = function(e){
		// e.subID : 서브미션 ID
		// e.flag  : S성공;
		// e.msg   : 트렌젝션 처리후 메세지
		// e."받을데이타셋"
		switch(e.subID){
			// 로그인ID권한조회
			case "retrieveLginIDAuthInqr":
				if(e.flag == "s") {
					dst_LginAuthInqrVo.setJSON(e.result);	

					//장기업무부 or 모집인관리자
					if(dst_LginAuthInqrVo.get("bizAdmrYn") =="Y"
						|| dst_LginAuthInqrVo.get("clprAdmrYn") =="Y"){
						ib_inputDepartCode.setValue("");
						tb_inputDepartNm.setValue("");
						ib_inputDepartCode.setDisabled(false);						
						btn_Search.setDisabled(false);

						//전체조회
						scwin.btn_Search_onclick();

					//화면조회권한이 없습니다.	
					}else{
						btn_Search.setDisabled(true);
						btn_dataInit.setDisabled(true); //초기화버튼
						btn_dataSave.setDisabled(true);	//저장버튼
						btn_dataDel.setDisabled(true);	//삭제버튼						
        				wprom.alert("화면조회권한이 없습니다.");
        				return;
					}							
				} else {
					wprom.alert(e.msg);
				}														
			break;			
			// 사번으로 모집인 정보 조회
			case "retrieveClprErsrByEmpNo":
				if(e.flag == "s") {
					dst_ClprErsrInfoVo.setJSON(e.result);

					if(util.isNull(dst_ClprErsrInfoVo.get("custNo"))|| dst_ClprErsrInfoVo.get("custNo") == ""){

        				wprom.alertF("사번을 확인해 주세요.\n모집인 번호만 입력 가능합니다.",{},ib_empNo);
        				return;

					}else{
						// 조회데이터로 화면세팅 		
						scwin.setClprErsrInfo();							
					} 					

				} else {
					wprom.alert(e.msg);
				}														
			break;					
			// 모집인말소정보 등록
			case "registerClprErsr":
				if(e.flag == "s") {	
					//전체조회
					scwin.btn_Search_onclick();
					//등록정보 초기화
					scwin.btn_dataInit_onclick();
				} else {
					wprom.alert(e.msg);
				}														
			break;				
			// 모집인말소정보 삭제
			case "modifyClprErsrDelete":
				if(e.flag == "s") {					
					//전체조회
					scwin.btn_Search_onclick();
					//등록정보 초기화
					scwin.btn_dataInit_onclick();
				} else {
					wprom.alert(e.msg);
				}														
			break;	
			// 모집인말소신청정보조회
			case "retrieveClprErsrList":
				if(e.flag == "s") {		
					dst_ClprErsrInfoListVo.removeAll();			
					dst_ClprErsrInfoListVo.setJSON(e.result.clprErsrInfoVo);

					if(dst_ClprErsrInfoListVo.getRowCount() >0){
						//등록정보 초기화
						scwin.btn_dataInit_onclick();					
					}else{
        				wprom.alert("조회결과가 없습니다.");
        				return;
					}

				} else {
					wprom.alert(e.msg);
				}														
			break;													
		}
	};

	//**********************************************************
	// 모집인말소등록정보 setting 
	//**********************************************************
	scwin.setClprErsrRegisterInfo = function() {

		dst_ClprErsrInfoVo.set("stndYmd", ib_stndYmd.getValue());//기준년월일
		dst_ClprErsrInfoVo.set("empNo", ib_empNo.getValue());//사번
		dst_ClprErsrInfoVo.set("custNo", ib_custNo.getValue());//고객번호		
		dst_ClprErsrInfoVo.set("demagInsurRgstNo", ib_demagInsurRgstNo.getValue());//손해보험번호
		dst_ClprErsrInfoVo.set("thrdInsurRgstNo", ib_thrdInsurRgstNo.getValue());//제3보험번호
		dst_ClprErsrInfoVo.set("demagInsurYn", chk_demagInsurYn.getValue());//손해보험여부
		dst_ClprErsrInfoVo.set("thrdInsurYn", chk_thrdInsurYn.getValue());//제3보험여부
		dst_ClprErsrInfoVo.set("clltFlagCd", sel_clltFlagCd.getValue());//소속구분
		dst_ClprErsrInfoVo.set("ersrCd", sel_ersrCd.getValue());//말소코드		
		dst_ClprErsrInfoVo.set("departCode", ib_departCode.getValue());//부서코드	    
	    dst_ClprErsrInfoVo.set("dlngStat","00");//상태 - 00: 등록, 01: 신청, 02:심사결과수신, 03:등록결과수신, 04:처리완료	    
	};

	//**********************************************************
	// 모집인정보 setting 
	//**********************************************************
	scwin.setClprErsrInfo = function() {

		btn_dataInit.setDisabled(false); //초기화버튼
		btn_dataSave.setDisabled(false); //저장버튼
		btn_dataDel.setDisabled(true);	//삭제버튼		

		ib_stndYmd.setValue(gsSysDate);//기준년월일
		//심사요청(01)이후에는 등록시 기준년월처리 확인필요 - Todo		

		ib_empNo.setValue(dst_ClprErsrInfoVo.get("empNo"));//사원번호
		ib_custNo.setValue(dst_ClprErsrInfoVo.get("custNo"));//사원주민번호
		ib_name.setValue(dst_ClprErsrInfoVo.get("name"));//사원이름		
		ib_demagInsurRgstNo.setValue(dst_ClprErsrInfoVo.get("demagInsurRgstNo"));//손해보험번호
		ib_thrdInsurRgstNo.setValue(dst_ClprErsrInfoVo.get("thrdInsurRgstNo"));//제3보험번호
		chk_demagInsurYn.setValue(dst_ClprErsrInfoVo.get("demagInsurYn"));//손해보험여부
		chk_thrdInsurYn.setValue(dst_ClprErsrInfoVo.get("thrdInsurYn"));//제3보험여부
		sel_clltFlagCd.setValue(dst_ClprErsrInfoVo.get("clltFlagCd"));//소속구분
		sel_ersrCd.setValue(dst_ClprErsrInfoVo.get("ersrCd"));//말소코드		
		ib_departCode.setValue(dst_ClprErsrInfoVo.get("departCode"));//부서코드
		ib_departNm.setValue(dst_ClprErsrInfoVo.get("departNm"));//부서명				
	};

	//**********************************************************
	// 사번으로 모집인 정보 조회 - /sales/support/CBSB0027.do
	//**********************************************************
	scwin.btn_searchEmpInfo_onclick = function() {

		if(util.isNull(ib_empNo.getValue())){			
        	wprom.alertF("사번을 입력해주세요.",{},ib_empNo);
        	return;
		}
		if(ib_empNo.getValue().length<7){			
        	wprom.alertF("사번은 7자리 입니다.",{},ib_empNo);
        	return;
		}

		dst_ClprErsrInfoVo.set("empNo" , ib_empNo.getValue());

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveClprErsrByEmpNo");        	// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0027.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprErsrInfoVo");					
		trans.targetDs("dst_ClprErsrInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	//**********************************************************
	// 사번 입력후 엔터시 조회
	//**********************************************************
	scwin.ib_empNo_onkeydown = function(e) {
		// 엔터클릭시 바로 조회 
		if(e.keyCode == 13) {
			scwin.btn_searchEmpInfo_onclick();
		}		
	};

	//**********************************************************
	// 초기화 버튼 클릭시
	//**********************************************************
	scwin.btn_dataInit_onclick = function() {

		btn_dataInit.setDisabled(true); //초기화버튼
		btn_dataSave.setDisabled(true); //저장버튼
		btn_dataDel.setDisabled(true);	//삭제버튼		

		ib_stndYmd.setValue("");//기준년월일		
		ib_empNo.setValue("");//사원번호
		ib_custNo.setValue("");//사원주민번호
		ib_name.setValue("");//사원이름		
		ib_demagInsurRgstNo.setValue("");//손해보험번호
		ib_thrdInsurRgstNo.setValue("");//제3보험번호
		chk_demagInsurYn.setValue("");//손해보험여부
		chk_thrdInsurYn.setValue("");//제3보험여부
		sel_clltFlagCd.setValue("");//소속구분
		sel_ersrCd.setValue("");//말소코드		
		ib_departCode.setValue("");//부서코드
		ib_departNm.setValue("");//부서명		
	};

	//**********************************************************
	// 저장 버튼시 - /sales/support/CBSB0025.do
	//**********************************************************
	scwin.btn_dataSave_onclick = function() {

		if(util.isNull(sel_ersrCd.getValue())){			
        	wprom.alertF("말소코드를 입력해 주세요.",{},sel_ersrCd);
        	return;
		}

		if(util.isNull(chk_demagInsurYn.getValue())
			&& util.isNull(chk_thrdInsurYn.getValue())){			
        	wprom.alertF("영업범위를 선택해 주세요.",{},chk_demagInsurYn);
        	return;
		}		

		if(util.isNull(ib_demagInsurRgstNo.getValue())
			&& util.isNull(ib_thrdInsurRgstNo.getValue())){			
        	wprom.alertF("등록번호를 확인해 주세요.",{},ib_demagInsurRgstNo);
        	return;
		}		

		if(!util.isNull(chk_demagInsurYn.getValue())){			
			if(util.isNull(ib_demagInsurRgstNo.getValue())){
        		wprom.alertF("손보 등록번호를 확인해 주세요.",{},ib_demagInsurRgstNo);
        		return;
			}
		}		

		if(!util.isNull(chk_thrdInsurYn.getValue())){			
			if(util.isNull(ib_thrdInsurRgstNo.getValue())){
        		wprom.alertF("제3보 등록번호를 확인해 주세요.",{},ib_demagInsurRgstNo);
        		return;
			}
		}	

		//저장데이터 setting
		scwin.setClprErsrRegisterInfo();

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("registerClprErsr");        		// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0025.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprErsrInfoVo");					
		trans.targetDs("dst_ClprErsrInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();

	};

	//**********************************************************
	// 삭제 버튼시 - /sales/support/CBSB0026.do
	//**********************************************************
	scwin.btn_dataDel_onclick = function() {

		//삭제데이터 setting
		scwin.setClprErsrRegisterInfo();

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("modifyClprErsrDelete");        	// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0026.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprErsrInfoVo");					
		trans.targetDs("dst_ClprErsrInfoVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	//**********************************************************
	// 모집인 리스트 조회 - /sales/support/CBSB0024.do
	//**********************************************************
	scwin.btn_Search_onclick = function() {

		//모집인 리스트 조회 키 setting
		scwin.setClprErsrSerchInparam();

		var trans = new transSub();                  	// transaction 보낼 객체 생성
		trans.setId("retrieveClprErsrList");        	// transaction 보낼 SubmissionID
		trans.setUrl("/sales/support/CBSB0024.do");   	// Controller 쪽으로 보낼 주소
		trans.setDs("dst_ClprErsrInfoVo");					
		trans.targetDs("dst_ClprErsrInfoListVo");		
		trans.callBack("scwin.subCallBackfunction");  	// 받을 Dataset을 처리시 콜백함수
		trans.send();
	};

	//**********************************************************
	// 모집인조회KEY정보 setting 
	//**********************************************************
	scwin.setClprErsrSerchInparam = function() {

		dst_ClprErsrInfoVo.set("dateFr",ic_dateFr.getValue());//조회시작일자
		dst_ClprErsrInfoVo.set("dateTo",ic_dateTo.getValue());//조회종료일자
		dst_ClprErsrInfoVo.set("clltFlagCd",sel_inputClltFlagCd.getValue());//소속구분
		dst_ClprErsrInfoVo.set("departCode",ib_inputDepartCode.getValue());//부서코드		
		if(!util.isNull(ib_inputCustNo.getValue())){
			var custKey = scwin.lfn_getCustNo(ib_inputCustNo.getValue());			
			dst_ClprErsrInfoVo.set("custNo", custKey);//주민등록번호
		}else{
			dst_ClprErsrInfoVo.set("custNo", ib_inputCustNo.getValue());//주민등록번호
		}		
		dst_ClprErsrInfoVo.set("name",ib_inputName.getValue());//이름



	};

	//**********************************************************
	// 그리드 정보로 업무 setting 
	//**********************************************************
	scwin.grd_dataList_oncellclick = function(row,col) {	

		var colId = grd_dataList.getColumnID(col);	

		//설계사/사용인 관리화면 오픈
		if(colId == "empNoErsrAvaYn"){			
			var empNoErsrAvaYn = grd_dataList.getCellData(row, "empNoErsrAvaYn");
			var clltFlagCd = grd_dataList.getCellData(row, "clltFlagCd");

			//채번가능상태인 경우
			if(empNoErsrAvaYn == "Y"){
				var promise = Promise.resolve();
			    promise = promise.then(function(rtnVal) {

			        //추후, 화면 오픈시 데이터 값 setting -- Todo
					//설계사 화면 오픈
					if(clltFlagCd == "M"){
						scwin.openCBSB014M(grd_dataList.getCellData(row, "empNo"));				

					//사용인 화면 오픈
					}else if(clltFlagCd == "S"){
						scwin.openCBSB010M(grd_dataList.getCellData(row, "empNo"));								
					}
				});				

				//등록정보 초기화
				scwin.btn_dataInit_onclick();
			}					

		//하단 데이터 setting 	
		}else{		
			ib_stndYmd.setValue(grd_dataList.getCellData(row,"stndYmd"));//기준년월일		
			ib_empNo.setValue(grd_dataList.getCellData(row,"empNo"));//사원번호
			ib_custNo.setValue(grd_dataList.getCellData(row,"custNo"));//사원주민번호
			ib_name.setValue(grd_dataList.getCellData(row,"name"));//사원이름		
			ib_demagInsurRgstNo.setValue(grd_dataList.getCellData(row,"demagInsurRgstNo"));//손해보험번호
			ib_thrdInsurRgstNo.setValue(grd_dataList.getCellData(row,"thrdInsurRgstNo"));//제3보험번호
			chk_demagInsurYn.setValue(grd_dataList.getCellData(row,"demagInsurYn"));//손해보험여부
			chk_thrdInsurYn.setValue(grd_dataList.getCellData(row,"thrdInsurYn"));//제3보험여부
			sel_clltFlagCd.setValue(grd_dataList.getCellData(row,"clltFlagCd"));//소속구분
			sel_ersrCd.setValue(grd_dataList.getCellData(row,"ersrCd"));//말소코드		
			ib_departCode.setValue(grd_dataList.getCellData(row,"departCode"));//부서코드
			ib_departNm.setValue(grd_dataList.getCellData(row,"departNm"));//부서명					

			//처리상태에 따라 초기화, 저장, 삭제버튼 활성/비활성 처리
			//상태 - 00: 등록, 01: 신청, 02:심사결과수신, 03:등록결과수신, 04:처리완료
			var dlngStat = grd_dataList.getCellData(row,"dlngStat");//처리상태

			btn_dataInit.setDisabled(false);//초기화버튼			

			//저장
			if(dlngStat == "00"){									
				btn_dataDel.setDisabled(false);//삭제버튼
				btn_dataSave.setDisabled(false);//저장버튼
				sel_ersrCd.setDisabled(false);//말소코드
			}else{
				btn_dataDel.setDisabled(true);//삭제버튼
				btn_dataSave.setDisabled(true);//저장버튼
				sel_ersrCd.setDisabled(true);//말소코드
			}
		}	
	};

