    //**********************************************************
	// 말소처리 위한 화면이동버튼 setting 
	//**********************************************************
	scwin.setErsrDlngScrnMoveYn = function(data, formattedData, rowIndex, colIndex) {
		var ersrDlngScrnMoveYn = grd_dataList.getCellData(rowIndex, "empNoErsrAvaYn");
		// 첨부파일명이 존재하면 이미지 표시
		if(ersrDlngScrnMoveYn == "Y"){
			formattedData = "<img src='/cm/images/content/detail_link_ico.png'>";			
		//말소된 경우에 처리완료 표시	
		}else if(ersrDlngScrnMoveYn == "S"){		
			formattedData = "말소";					
		// 첨부파일명이 널이면 '-'표시 
		}else {
			formattedData = "-";
		}	
		return formattedData;	
	};

