//---------------------------------------------------------------------------
// function 명 : lfn_getRealNo (기간계B370)
// 내용        : 실주민번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getRealNo = function(sCustKeyNo) {
		DsWs_CustNo.removeAll();

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectRealCustNo", 0, 0);
		MethodObj.setId("selectRealCustNo");
		MethodObj.setParam("sCustKeyNo", sCustKeyNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sRealCustNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sRealCustNo");
		}else{
			return sCustKeyNo;
		}
	};
//---------------------------------------------------------------------------
// function 명 : lfn_getCustNo
// 내용        : 고객번호 조회
//---------------------------------------------------------------------------
	scwin.lfn_getCustNo = function(sRealCustNo) {
		DsWs_CustNo.removeAll();		

		var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectCustKeyNo", 0, 0);
		MethodObj.setId("selectCustKeyNo");
		MethodObj.setParam("sRealCustNo", sRealCustNo);
		MethodObj.setUrl("/common/CBSO028MController.do");
		MethodObj.setXPath("//*/LTI0000");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();    

		DsWs_CustNo.setJSON(_tranSync.getDsJson("LTI0000"));

		if(DsWs_CustNo.getRowCount() > 0 && wconv.length(DsWs_CustNo.getCellData(0,"sCustKeyNo")) >  0) {
			return DsWs_CustNo.getCellData(0,"sCustKeyNo");
		}else{
			return "";
		}
	};
