var addr;  // 주소
var sYn; 
var sGb = "";			// 설계/조회 구분
var sCustNo = "";	// 설계/계약 구분(주민번호)
var sName = "";		// 성명
var sPlanNo = "";	 // 설계/조회 구분  
var nCrChngNo = "";		// 변경번호
var sStandDate = ""; //기준일자(직업코드찾기) 2014.04.01 제도개정
var sSaveFlg = "";		// 저장 모드
var sSaveFlgNm = "";		// 저장 모드
var medt_sCustNo_Event;	// 주민번호(InputBox) MaskEdit 정규식 입력

//---------------------------------------------------------------------------
// function 명 : B013_OnLoadCompleted
// 내용        : 화면로딩시 메인으로부터 파라메터값 담음
//---------------------------------------------------------------------------
scwin.init = function() {
	console.log("[/CBSC003P.xml] [init()] ==> [폼 로딩] [TEST_01]" );

	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sMyPlan)

		if (!util.isNull(inJSON.sGb)) 	sGb = inJSON.sGb;				// 설계/조회 구분 
		if (!util.isNull(inJSON.sCustNo)) 	sCustNo = inJSON.sCustNo;	 //설계/계약 구분(주민번호)   
		if (!util.isNull(inJSON.sName)) 	sName = inJSON.sName;	// 성명  
		if (!util.isNull(inJSON.sPlanNo)) 	sPlanNo = inJSON.sPlanNo;	 // 설계/조회
		if (!util.isNull(inJSON.nCrChngNo)) 	nCrChngNo = inJSON.nCrChngNo;	// 변경번호	

		// medt_sCustNo(InputBox) MaskEdit 정규식 입력  
		var maskRegExp = [ /^[0-9\s]{7}[A-Z0-9\s]{6}$/			// 01. 주민번호 이면("######-#ZZZZZZ")  : 13자리
									, /^[0-9\s]{3}^[0-9\s]{2}[A-Z0-9\s]{5}$/	   // 02. 법인번호 이면("###-##-#####")  : 10자리 
								 ]; 			// 07. 청약번호 이면 : 12자리
		var maxLength  = [13, 10];
		var chkIndex   = 0;
		medt_sCustNo_Event = new wMaskEditObj(medt_sCustNo, maskRegExp, maxLength, chkIndex);

		sStandDate = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// /기준일자(global 현재일자)

	 	// TEST 데이타: 2017.10.20, by 진태만
//		sGb = "1";	// 설계/조회 구분[설계(1),계약(2) 여부] 
//		sCustNo = "8808251A00003";	// 설계/계약 구분(주민번호)  
//		sName = "정한진";	// 성명  
//		sPlanNo = "M20170002245";	// 설계/계약 번호  
		//console.log("[/CBSC003P.xml] [init()] ==> [폼 로딩] [TEST_02] [sGb]"+ sGb +"[sCustNo]"+ sCustNo +"[sPlanNo]"+ sPlanNo +"[sName]"+ sName );		

		scwin.lfn_init();	  //화면초기화

		return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); // =================================================>
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	//pro = pro.catch(function(rj) {
		//util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	//});

	return pro;

   //console.log("[/CBSC003P.xml] [init()] ==> [폼 로딩] [TEST_end]" );
};


//---------------------------------------------------------------------------
// function 명 : lfn_init()
// 내용        : 화면초기화
//---------------------------------------------------------------------------
scwin.lfn_init = function() { 
	//console.log("[/CBSC003P.xml] [lfn_init()] ==> [화면초기화] [TEST_01]" );

	wconv.clear(Ds_LTIDC93);

    medt_sCustNo.setValue(sCustNo);		// 주민번호
    medt_sPlanNo.setValue(sPlanNo);		// 설계번호(청약번호)

    if ( sGb == "2") {  	 // 법인 이면
        Static0.setValue("계약번호"); 

    } else { 	 // 개인 이면
    	//console.log("[/CBSC003P.xml] [lfn_init()] ==> [화면초기화] [개인 이면]" );

    	cmb_sCorpType.hide();  // 직장명 콤보
		cmb_sCorpInfo.hide();  // 직업/업종 콤보	
    	Static7.hide(); 	// 홈페이지(이메일)주소 
    	edt_sWebAddr.hide(); 
    	Static32.setValue("직위");		// 직위
        Static32.show();       
  	} 
    //console.log("[/CBSC003P.xml] [init()] ==> [화면초기화] [TEST_02] [medt_sCustNo]"+ medt_sCustNo.getValue() +"[medt_sPlanNo]"+ medt_sPlanNo.getValue() );		

    scwin.lfn_Search();	 // 조회

 	scwin.cmb_sClass_Changed();  
};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : EDD,CDD  조회
//---------------------------------------------------------------------------
scwin.lfn_Search = function() {

 	//console.log("[/CBSC003P.xml] [lfn_Search()] ==> [조회 처리] [TEST_01]" );

	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtidc93");		// EDD고객 조회 Obj
	MethodObj.setId("getLtidc93");	// ID 셋팅 
	MethodObj.setParam("sPlanNo", sPlanNo);	// Param 셋팅(설계번호)  
	MethodObj.setParam("sGb", sGb);	// Param 셋팅(설계/조회 구분)  
	MethodObj.setParam("sCustNo", medt_sCustNo.getValue() );	// Param 셋팅(설계/계약 구분(주민번호))   
	MethodObj.setUrl("/contract/CBSC003PController.do");  			// 서버단 호출 URL 
	MethodObj.setXPath("//*/LTIDC93");					// XPath(Ds_LTIDC93) 
	MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
	MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.setXPathDs();
	MethodObj.send();		// 최종 전송
	//console.log("[/CBSC003P.xml] [lfn_Search()] ==> [01. EDD고객 조회, 전송_완료] [TEST_91]");

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_LTIDC93.setJSON(_tranSync.getDsJson("//*/LTIDC93"));				// 보험 기본사항 Ds 셋팅 
	DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)  
	//console.log("[/CBSC003P.xml] [lfn_Search()] ==> [Ds_LTIDA09,_갯수]"+ Ds_LTIDC93.getRowCount() +"[Ds_LTIDC93]"+ WebSquare.xml.serialize(Ds_LTIDC93.getAllXML()) );  

  	if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }

    if (Ds_LTIDC93.getRowCount() == 0) {
        //wprom.alert("청약사항 조회건이 없습니다.");

   		btn_input.setDisabled(false);
        btn_modify.setDisabled(true);
        btn_delete.setDisabled(true);

    } else {
    	//console.log("[/CBSC003P.xml] [lfn_Search()] ==> [조회 성공] [TEST_71]" );

  		Ds_LTIDC93.setRowPosition(0);	 // Row 위치 0번째로 지정 

     	edt_sTelNo1.setValue(wconv.substr(Ds_LTIDC93.getCellData(0, "sTelNo"), 0, 3));
        edt_sTelNo2.setValue(wconv.substr(Ds_LTIDC93.getCellData(0, "sTelNo"), 3, 4));
        edt_sTelNo3.setValue(wconv.substr(Ds_LTIDC93.getCellData(0, "sTelNo"), 7, 4));
        edt_sZip1.setValue(wconv.substr(Ds_LTIDC93.getCellData(0, "sZipNo"), 0, 3));
        edt_sZip2.setValue(wconv.substr(Ds_LTIDC93.getCellData(0, "sZipNo"), 3, 3));

		addr = Ds_LTIDC93.getCellData(0, "sAddr").split("^");	// split 처리

        edt_sAddr1.setValue(addr[0]);
        edt_sAddr2.setValue(addr[1]);
        cmb_sClass.setValue(Ds_LTIDC93.getCellData(0, "sClass"));

        btn_input.setDisabled(true);
        btn_modify.setDisabled(false);
        btn_delete.setDisabled(false);

        if (DsWs_LTIDC93.getCellData(0, "sCnFmYn") == "Y") {	//심사자가 확인한 경우는 수정,삭제 불가
            btn_modify.setDisabled(true);
            btn_delete.setDisabled(true);
        } 
    }

    if (wconv.trim(sYn) == "D" || wconv.trim(sGb) == "2") {	//심사 또는 계약조회화면에서 넘어올경우

        btn_input.setDisabled(true);
        btn_modify.setDisabled(true);
        btn_delete.setDisabled(true);
    }    

	//console.log("[/CBSC003P.xml] [lfn_Search()] ==> [TEST_end]" ); 
};

scwin.btn_input_Onclick = function(e) { 
 	sSaveFlg = "I";		// 저장 모드(I:입력, U:수정, D: 삭제)	
 	sSaveFlgNm = "입력";	
	scwin.lfn_Save();	
};

scwin.btn_modify_Onclick = function(e) {	
	sSaveFlg = "U";		// 저장 모드(I:입력, U:수정, D: 삭제)	
	sSaveFlgNm = "수정";	
	scwin.lfn_Save();	
};

scwin.btn_delete_Onclick = function(e) {
	sSaveFlg = "D";		// 저장 모드(I:입력, U:수정, D: 삭제)	
	sSaveFlgNm = "삭제";	
	scwin.lfn_Save();	
};

//---------------------------------------------------------------------------
// function 명 : lfn_Save
// 내용        : EDD,CDD  조회
//---------------------------------------------------------------------------
scwin.lfn_Save = function() {
	//console.log("[/CBSC003P.xml] [lfn_Save()] ==> [저장] [TEST_01]" );

    //개인,법인에 따른 항목 입력 값 초기화
    if (cmb_sClass.getValue() == "1") {	 //개인         
        cmb_sCorpType.setValue(""); 
        cmb_sCorpInfo.setValue("");       
        edt_sCorpBldDate.setValue("");
        edt_sWebAddr.setValue("");

    } else if (cmb_sClass.getValue() == "2") {	  //법인       
        edt_sWorkName.setValue("");
        edt_sJobCode.setValue("");
        edt_sJobName.setValue("");
        edt_sDept.setValue("");
        edt_sJobPos.setValue("");
    } 

    Ds_LTIDC93.setCellData(0, "sPlanNo", sPlanNo);
    Ds_LTIDC93.setCellData(0, "sCustNo", sCustNo);
    Ds_LTIDC93.setCellData(0, "nCrChngNo", nCrChngNo);
    Ds_LTIDC93.setCellData(0, "sClass", cmb_sClass.getValue());
    Ds_LTIDC93.setCellData(0, "sName", edt_sName.getValue());
    Ds_LTIDC93.setCellData(0, "sJobCode", edt_sJobCode.getValue());
    Ds_LTIDC93.setCellData(0, "sJobName", edt_sJobName.getValue());
    Ds_LTIDC93.setCellData(0, "sWorkName", edt_sWorkName.getValue());
    Ds_LTIDC93.setCellData(0, "sDept", edt_sDept.getValue());
    Ds_LTIDC93.setCellData(0, "sJobPos", edt_sJobPos.getValue());
    Ds_LTIDC93.setCellData(0, "sTelNo", edt_sTelNo1.getValue() + edt_sTelNo2.getValue() + edt_sTelNo3.getValue());
    Ds_LTIDC93.setCellData(0, "sZipNo", edt_sZip1.getValue() + edt_sZip2.getValue());
    Ds_LTIDC93.setCellData(0, "sAddr", edt_sAddr1.getValue() + "^" + edt_sAddr2.getValue());
    Ds_LTIDC93.setCellData(0, "sTrAim", txa_sTrAim.getValue());
    Ds_LTIDC93.setCellData(0, "sTrSource", txa_sTrSource.getValue());
    Ds_LTIDC93.setCellData(0, "sExtraCont", txa_sExtraCont.getValue());
    Ds_LTIDC93.setCellData(0, "gUserID", gds.gUserID);
    Ds_LTIDC93.setCellData(0, "gUserIP", gds.gUserIP);

	if (sSaveFlg == "I") {	 // 저장 모드(I:등록, U:수정, D: 삭제)
       Ds_LTIDC93.setCellData(0, "sProcGB", "I");
    } else if (sSaveFlg == "U") {	 
        Ds_LTIDC93.setCellData(0, "sProcGB", "U");
    } else if (sSaveFlg == "D") {	 
        Ds_LTIDC93.setCellData(0, "sProcGB", "D");
    }   
    //console.log("[/CBSC003P.xml] [lfn_Input()] ==> [Ds_LTIDC93,_갯수]"+ Ds_LTIDC93.getRowCount() +"[Ds_LTIDC93]"+ WebSquare.xml.serialize(Ds_LTIDC93.getAllXML()) );

    var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "saveLtidc93"); 	// 01. 저장 처리 
	MethodObj.setId("saveLtidc93");		// ID 셋팅 
	MethodObj.addSingleData("LTIDC93", "LTIDC93", "Ds_LTIDC93:A");  	// Param 셋팅(부담보정보 Ds): A(전체), I(등록), U(수정), R  
	MethodObj.setUrl("/contract/CBSC003PController.do");  			// 서버단 호출 URL  
	MethodObj.addXPath("//WARNING");	// XPath(No) 
	MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
	MethodObj.setHashTable();   	// 전문의 앞이 hashtable로 보낼 때  
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]  
	MethodObj.send();		// 최종 전송  		 
	//console.log("[/CBSC003P.xml] [lfn_Input()] ==> [02. 저장 처리_완료] [TEST_71]");  	

	util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

 	DsWs_Warning.setJSON(_tranSync.getDsJson("WARNING"));  		// Warning DS 셋팅(조회 결과) 
  	//console.log("[/CBSC003P.xml] [lfn_Input()] ==> [저장 처리] [DsWs_Warning.getRowCount()]"+ DsWs_Warning.getRowCount() +"[DsWs_Warning]"+ WebSquare.xml.serialize(DsWs_Warning.getAllXML()) ); 

    if (DsWs_Warning.getRowCount() != 0) {	 //에러 처리
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }

    wprom.alert("EDD/CDD 관리항목이 " + sSaveFlgNm + " 되었습니다.");

    if (sSaveFlg == "I") {	 // 저장 모드(I:등록, U:수정, D: 삭제)	
        btn_input.setDisabled(true);
        btn_modify.setDisabled(false);
        btn_delete.setDisabled(false);
    }

    if (sSaveFlg == "D") {	 // 저장 모드(I:등록, U:수정, D: 삭제)	
        wconv.clear(Ds_LTIDC93);
        edt_sTelNo1.setValue("");
        edt_sTelNo2.setValue("");
        edt_sTelNo3.setValue("");
        edt_sZip1.setValue("");
        edt_sZip2.setValue("");
        edt_sAddr1.setValue("");
        edt_sAddr2.setValue("");
        cmb_sClass.setValue("");

        btn_input.setDisabled(true);
        btn_modify.setDisabled(true);
        btn_delete.setDisabled(true);
        btn_search.setDisabled(true);
    } 
	//console.log("[/CBSC003P.xml] [lfn_Input()] ==> [저장] [TEST_end]");  
};

