scwin.Validation_Check = function() {};

//---------------------------------------------------------------------------
// function 명 : bt_search_onclick
// 내용        : 직업정보 조회 
//---------------------------------------------------------------------------
scwin.Btn_JobSearch_OnClick = function(obj) {
 	//console.log("[/CBSC069P.xml] [Btn_JobSearch_OnClick()] ==> [직업정보 조회] [TEST_01]" );

	var pro = Promise.resolve();

	pro = pro.then(function(rv) {
		var indata  = {	"flag" : "bt_search",
						"sStandDate" : sStandDate
					  };

		var popInfo = {
			"width"     : 845,
			"height"    : 565,  
			"indata"    : indata,
			"title"     : "직업코드 조회",
			"callback"  : scwin.popupCallback
		};

		return wprom.popupOpen("/ui/cbso/CBSO021P.xml", popInfo);		// 직업정보 팝업
	});

	pro = pro.then(function(rv) {
		var rtnValue = wprom.getReturnData();
			//console.log("[/CBSC069P.xml] [Btn_JobSearch_OnClick()] ==> [직업정보 팝업 결과값] [TEST_01]"+ rtnValue );

		if (rtnValue != "") {
			scwin.search_val(rtnValue);
		}
	});
};

//---------------------------------------------------------------------------
// function 명 : bt_search_onclick
// 내용        : 직업 검색한 ds 셋팅
//---------------------------------------------------------------------------
scwin.search_val = function(result) {
 	console.log("[/CBSC069P.xml] [search_val()] ==> [직업정보 팝업 결과값] [result]"+ result );

	var jobretArr = result.split("^");

	edt_sJobCode.setValue(jobretArr[1]); //직업코드 
	edt_sJobName.setValue(jobretArr[0]); //직업명  
};

