// function 명 : Btn_AddrSearch1_OnClick
// 내용        : 주소 검색
//---------------------------------------------------------------------------
scwin.Btn_AddrSearch_OnClick = function(obj) {
	//console.log("[/CBSC003P.xml] [Btn_AddrSearch_OnClick()] ==> [조회 처리] [TEST_01]" );

	var args = "Ds_Addr^0^sZip1^sZip2^sAdrs^sAdrsAdd^sAdrs1^sAdrs2^sAdrs3^dataTab.tab1.Edt_1_AdrsAdd^" + 
   				Ds_Addr.getCellData(0, "sZip1") + Ds_Addr.getCellData(0, "sZip2") + "^" + Ds_Addr.getCellData(0, "sAdrs") + 
   				"^sZipType" + "^sAddrMgtNo^nAddrStdSeq^sOrgZipType^sOrgZipCode^sOrgAddr";

	var indata  = {	"parentInfo" : args,
					"sTableNm" : "CUSAA01",
					"sAdrsType" : "1"
				}; 

   	scwin.zipPopup(indata); 
};

//---------------------------------------------------------------------------
// function 명 : zipPopup
// 내용        : 주소찾기창을 띄움 
//---------------------------------------------------------------------------
scwin.zipPopup = function(indata) { 
	//console.log("[/CBSC003P.xml] [zipPopup()] ==> [주소찾기창을 띄움] [ indata.parentInfo]"+ indata.parentInfo );
	//console.log("[/CBSC003P.xml] [zipPopup()] ==> [주소찾기창을 띄움]"+ Ds_Addr.getRowCount() +"[Ds_Addr]"+ WebSquare.xml.serialize(Ds_Addr.getAllXML()) );  

   	// 우편번호창 오픈 
   	var pro = Promise.resolve();

	pro = pro.then(function(rv) { 
		var pObj = {
				"Ds_Addr" : Ds_Addr 			// 피보험자 정보 Ds  
		} 	// 자식으로 보낼 부모 Ds OBJECT 리스트

		var popInfo = {
			"width"     : 780,
			"height"    : 786,   
			"indata"    : indata,	 			// 자식으로 보낼 부모 파라미터  리스트
		 	"parentObject"   : pObj,	 	// 자식으로 보낼 부모 Ds OBJECT 리스트 
			"title"     : "우편번호 찾기",
			"callback"  : scwin.popupCallback
		};

		pro = pro.then(function(rv) {
			//console.log("[/CBSC003P.xml] [zipPopup()] ==> [주소찾기_결과] [TEST_41]" );

			var rtnValue = wprom.getReturnData();
			 console.log("[/CBSC003P.xml] [zipPopup()] ==> [주소찾기_결과] [rtnValue]"+ rtnValue );

			if (rtnValue != "") {			 
			 	var sAddrAll =  rtnValue.split("^");		// split 처리 
			 	//console.log("[/CBSC003P.xml] [zipPopup()] ==> [주소찾기_결과] [sAddrAll]"+ sAddrAll );

		 	 	edt_sZip1.setValue(sAddrAll[0]);		// 우편번호
		        edt_sZip2.setValue(sAddrAll[1]);
		        edt_sAddr1.setValue(sAddrAll[2]);	// 주소
		        edt_sAddr2.setValue(sAddrAll[3]);  
				//  [/CBSC003P.xml] [zipPopup()] ==> [주소찾기_결과] [rtnValue]074 ^20 ^서울 영등포구 도림천로13길  ^6, 2222호 (대림동) ^ ^서울 ^영등포구 ^도림천로13길  ^ ^1156013300110600035037866 ^1700112771 ^1 ^150825 ^서울 영등포구 도림천로13길  6, 2222 (대림동) ^1156013300
         	} 
		}); 

		return wprom.popupOpen("/ui/cbso/CBSO019P.xml", popInfo);		// 우편번호창 팝업
	});
};

 //---------------------------------------------------------------------------
// function 명 : search_val1
// 내용        : 주소검색결과 세팅
