scwin.Set_ZipAddr = function(result) {

    var ZipretArr = wconv.split(result, "^");
    edt_sZip1.setValue(ZipretArr[0]); //우편번호1
    edt_sZip2.setValue(ZipretArr[1]); //우편번호2
    edt_sAddr1.setValue(ZipretArr[2]); //우편번호2
    edt_sAddr2.setValue(ZipretArr[3]); //우편번호2
};

