scwin.edt_sTelNo1_OnKillFocus = function(obj) {
    scwin.check_telno(wconv.trim(edt_sTelNo1.getValue()), 3);
};

scwin.edt_sTelNo2_OnKillFocus = function(obj) {
    scwin.check_telno(wconv.trim(edt_sTelNo2.getValue()), 4);
};

scwin.edt_sTelNo3_OnKillFocus = function(obj) {
    scwin.check_telno(wconv.trim(edt_sTelNo3.getValue()), 4);
};

scwin.check_telno = function(telno, len) {
    if (wconv.length(telno) < len) {
        wprom.alert("연락처 번호의 자리수를 확인하시기 바랍니다.");
        return false;
    }
};

/**
 * @function	phoneCheck
 * @desc		전화번호 체크
 * @param      
 * 				tel1Cpt : 전화번호 컴포넌트
 * 				tel2Cpt : 전화번호 컴포넌트
 * 				tel3Cpt : 전화번호 컴포넌트
 * 				errStr  : 에러 메세지
 * @return		true/false
 **/
scwin.phoneCheck = function(tel1Cpt, tel2Cpt, tel3Cpt, errStr) {
    if (wconv.length(wconv.trim(tel1Cpt.Text)) > 0)
        or;
    wconv.length(wconv.trim(tel2Cpt.Text)) > 0;
    or;
    wconv.length(wconv.trim(tel3Cpt.Text)) > 0; {
        if (wconv.length(wconv.trim(tel1Cpt.Text)) < 2) {
            wprom.alert(errStr + " 바르게 입력해 주세요."); 
			util.focus(tel1Cpt);	// 포커스 설정 
            return false;
        }
        if (wconv.length(wconv.trim(tel2Cpt.Text)) < 3) {
            wprom.alert(errStr + " 바르게 입력해 주세요."); 
			util.focus(tel2Cpt);	// 포커스 설정 
            return false;
        }
        if (wconv.length(wconv.trim(tel3Cpt.Text)) != 4) {
            wprom.alert(errStr + " 바르게 입력해 주세요."); 
			util.focus(tel3Cpt);	// 포커스 설정 
            return false;
        }
    }
    if (wconv.length(tel1Cpt + tel2Cpt + tel3Cpt) > 0) {
        if (isValidPhone(tel1Cpt + tel2Cpt + tel3Cpt)) {
            return true;
        } else {
            //alert(errStr + " 무효한 번호입니다. \n재입력해 주세요");
            return false; //alert("무효");
        }
    }
    return true;
};

