scwin.init = function() {
	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {}

    //	Static20.Text = "고객님께서 " + sCrtorName + "고객님의 " +sGdCdName + "의 보험료 " + nApplPrem + "원을 납입하시는데\n동의 하십니까?";
    Static20.setValue("본 계약 체결을 위한 보험료를 납입하시는데 동의하십니까?");
    //	Static22.Text = sDpsrName + " 고객님께서 청약하신 보험계약의 정상적인 체결과 유지를 위해 고객님의 주민등록번호와 \n보험료 입금 등의개인(신용)정보를 계약 유지기간 동안 수집ㆍ이용 하거나 신용정보집중기관 \n등에 제공하는데 동의 하십니까?";
    Static22.setValue("보험계약의 정상적인 체결과 유지를 위해 고객님의 주민등록번호와 계좌번호 \n"+
                            "또는 신용카드 정보를 계약 유지기간 동안 수집.이용하고 신용정보집중기관을\n"+
