    Edt_CustNo.setValue(fv_args.sDpsrCd);
    Edt_Name.setValue(fv_args.sDpsrName);
    scwin.lfn_Btn_enable();
    scwin.lfn_selectAgreYn();
};

scwin.lfn_Close = function(e) {
    //goo parent.descrFlag = false;

    if (DsWs_LTIDA50_2.getRowcount() > 0) {
        wprom.close(1);
    } else {
        wprom.close(0);
    }
};

scwin.Btn_save_OnClick = function(e) {
    // Validation Check	
    if (Ds_LTIDA50.getCellData(0, "s32AgreYn") != "Y") {
        wprom.alert("보험료 납입 동의를 확인해 주세요.");
        return;
    }
    if (Ds_LTIDA50.getCellData(0, "s33AgreYn") != "Y") {
        wprom.alert("개인(신용)정보 수집ㆍ이용ㆍ제공 동의를 확인해 주세요.");
        return;
    }
    scwin.btn_RecEnd_OnClick();
    Ds_LTIDA50.setCellData(0, "sPlanNoOri", fv_args.sPlanNoOri);
    Ds_LTIDA50.setCellData(0, "sDpsrName", fv_args.sDpsrName);
    Ds_LTIDA50.setCellData(0, "sDpsrCd", fv_args.sDpsrCd);
    Ds_LTIDA50.setCellData(0, "s32AgreYn", Ds_LTIDA50.getCellData(0, "s32AgreYn"));
    Ds_LTIDA50.setCellData(0, "s33AgreYn", Ds_LTIDA50.getCellData(0, "s33AgreYn"));
    Ds_LTIDA50.setCellData(0, "sRecvCrtorRelCd", fv_args.sRecvCrtorRelCd);
    Ds_LTIDA50.setCellData(0, "sRecvCrtorTelNo", fv_args.sRecvCrtorTelNo);
    Ds_LTIDA50.setCellData(0, "sCmMetd", fv_args.sCmMetd);
    Ds_LTIDA50.setCellData(0, "sMdfcDler", gds.gUserID);
    Ds_LTIDA50.setCellData(0, "sMdfcDlerIP", gds.gUserIP);
    Ds_LTIDA50.setCellData(0, "sRecStartDate", sStrtDate);
    Ds_LTIDA50.setCellData(0, "sRecStartTime", sStrtTime);
    Ds_LTIDA50.setCellData(0, "sRecEndDate", sEndDate);
    Ds_LTIDA50.setCellData(0, "sRecEndTime", sEndTime);
    Ds_LTIDA50.setCellData(0, "nCallSerial", gds.gCallSerial);
    /*
    SOBJ_save.WSDL = gds.gWsdlBasis;
    var MethodObj = SOBJ_save.CreateMethodObj("perform");
    setHeader("hashtable", "com.inswave.longterm.dpt.rec.task.FAF400Task", "insertRecvAgreYn");
    addSingleData("LTIDA50", "LTIDA50", Ds_LTIDA50);
    setTail("hashtable");
    SOBJ_save.CreateObj("in0");
    MethodObj.in0 = insertStr;
    gfn_CallService(SOBJ_save, DOBJ_save, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF400Task", "insertRecvAgreYn");
	MethodObj.setId("insertRecvAgreYn");
    MethodObj.addSingleData("LTIDA50", "LTIDA50", "Ds_LTIDA50");
	MethodObj.setUrl("/contract/CBSC011PController.do");  	// 서버단 호출 URL (수납 확정)    
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.setHashTable();   	//  전문의 앞이 hashtable로 보낼 때
	MethodObj.sync();					// 동기 처리[기본(async:비동기)]
	MethodObj.send(); 					// 최종 전송 		// TEST @@@ ===>
	DsWs_SaveWarning.setJSON( _tranSync.getDsJson("WARNING"));

    //에러처리
    if (DsWs_SaveWarning.getRowCount() != 0) {
        wprom.alert(DsWs_SaveWarning.getCellData(0, "msg"));
        return;
    }
    wprom.alert("개인(신용)정보 동의가 저장 되었습니다.");
    //goo parent.descrFlag = true;
    wprom.close(1);
};

//---------------------------------------------------------------------------
// function 명 : lfn_selectAgreYn
// 내용        : 조회
//---------------------------------------------------------------------------
scwin.lfn_selectAgreYn = function() {
    wconv.clear(DsWs_LTIDA50); //리스트 내용삭제
    wconv.clear(Ds_LTIDA50);//.removeAll();
    if (Edt_CustNo.getValue() == "") {
        return;
    }
    /*
    SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.dpt.rec.task.FAF400Task", "selectRecvAgreYn", "", "");
    if (sDpsrCd == "") {
        sDpsrCd = Edt_CustNo.getValue();
    }
    gfn_SetParam(MethodObj, "sPlanNo", sPlanNoOri);
    gfn_SetParam(MethodObj, "sCustNo", sDpsrCd);
    gfn_SetParam(MethodObj, "sCmMetd", sCmMetd);
    var result = gfn_CallService(SOBJ_select, DOBJ_select2, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF400Task", "selectRecvAgreYn");
	MethodObj.setId("selectRecvAgreYn");

	//MethodObj.setParam("sAgentCode", gUserID);
    if (sDpsrCd == "") {
        fv_args.sDpsrCd = Edt_CustNo.getValue();
    }
    MethodObj.setParam( "sPlanNo", fv_args.sPlanNoOri);
    MethodObj.setParam( "sCustNo", fv_args.sDpsrCd);
    MethodObj.setParam( "sCmMetd", fv_args.sCmMetd);

	MethodObj.setUrl("/contract/CBSC011PController.do");
	MethodObj.setXPath("//*/CUSAA35");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("CUSAA35"), "DsWs_LTIDA50_2");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_selectWarning");
	//util.setCompBindRefresh("DsWs_LTIDA50_2");
	//util.setRowPosition(DsWs_LTIDA50, 0);

    //trace(replace(gfn_XmlEncoder(DOBJ_select2.Value), "><", ">\n<"));
    if (DsWs_selectWarning.getRowCount() != 0) {
        wprom.alert(DsWs_selectWarning.getCellData(0, "msg"));
        return;
    }
    if (DsWs_LTIDA50_2.getRowCount() > 0 && DsWs_LTIDA50_2.getCellData(0, "result") > 0) {
        util.appendRow(Ds_LTIDA50);
        if (DsWs_LTIDA50_2.getCellData(0, "sAgmYn") == "Y") {
            Ds_LTIDA50.setCellData(0, "s32AgreYn", DsWs_LTIDA50_2.getCellData(0, "sAgmYn"));
            chk_agree1.setDisabled(true);
        }
        if (DsWs_LTIDA50_2.getCellData(0, "sAgmYn") == "Y") {
            Ds_LTIDA50.setCellData(0, "s33AgreYn", DsWs_LTIDA50_2.getCellData(0, "sAgmYn"));
            chk_agree2.setDisabled(true);
        }
        Ds_LTIDA50.setCellData(0, "sInptDler", DsWs_LTIDA50_2.getCellData(0, "sAgmUser"));
        Ds_LTIDA50.setCellData(0, "sInptDate", DsWs_LTIDA50_2.getCellData(0, "sAgmDt"));
        Btn_save.setDisabled(true);
        if (DsWs_LTIDA50_2.getCellData(0, "sAgmYn") == "Y" && DsWs_LTIDA50_2.getCellData(0, "sAgmYn") == "Y") {
            btn_RecStrt.setDisabled(true);
        }
    } else {
        util.appendRow(Ds_LTIDA50);
        Ds_LTIDA50.setCellData(0, "sPlanNoOri", fv_args.sPlanNoOri);
        Ds_LTIDA50.setCellData(0, "sDpsrName", fv_args.sDpsrName);
        Ds_LTIDA50.setCellData(0, "sDpsrCd", fv_args.sDpsrCd);
        //Ds_LTIDA50.SetColumn(0,"s32AgreYn","Y");
        //Ds_LTIDA50.SetColumn(0,"s33AgreYn","Y");
        Ds_LTIDA50.setCellData(0, "sInptDler", util.getGdsValue("gUserID"));
        Ds_LTIDA50.setCellData(0, "sInptDlerIP", util.getGdsValue("gUserIP") );
        gfn_getServerTime();
        sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간	
        Ds_LTIDA50.setCellData(0, "sInptDate", wconv.mid(sysDate, 0, 8));
        //Edt_sReqID.Value = gUserID;
        //Edt_sUserName.Value  = gUserEmpName;	
        //wconv.trace("test");
    }
};

//---------------------------------------------------------------------------
// function 명 : btn_RecStrt_OnClick
// 내용        : 녹취 정보 콜.
//---------------------------------------------------------------------------
scwin.btn_RecStrt_OnClick = function(obj) {
    // 서버시간 조회
    gfn_getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간	
    sStrtDate = wconv.mid(sysDate, 0, 8);
    sStrtTime = wconv.mid(sysDate, 8, 6);
    Btn_save.setDisabled(false);
};

//---------------------------------------------------------------------------
// function 명 : btn_RecEnd_OnClick
// 내용        : 녹취 정보 콜.
//---------------------------------------------------------------------------
scwin.btn_RecEnd_OnClick = function(obj) {
    // 서버시간 조회
    gfn_getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간	
    sEndDate = wconv.mid(sysDate, 0, 8);
    sEndTime = wconv.mid(sysDate, 8, 6);
};

//---------------------------------------------------------------------------
// function 명 : lfn_Btn_enable
// 내용        : 계약상태에 따른 버튼 비활성화
//---------------------------------------------------------------------------
scwin.lfn_Btn_enable = function() {
    Btn_save.setDisabled(true);
};
