var sCmMetd = "";//수금방법
var sCrtorRelnFlag = "";// 수금 계약자 관계(계약자,피보험자,기타)
var sScanObjtYn = "N";//비대면 계약여부 
var sRlnmCnfmFlag = false;//실명확인 플래그 false 경우 실명 확인 입력란 unvisible, true 경우 visible
var sGb = "1";//설계(1),계약(2) 여부 : EDD고객정보 및 FATCA본인확인에서 사용
var sRlnmCnfmInfoRvMatt1 = "";
var sRlnmCnfmInfoRvMatt2 = "";
var sFlag = "";
var sFATCAFlag = "";

//cbs 추가
