//---------------------------------------------------------------------------
// function 명 : lfn_Init
// 내용        : 화면오픈시 처리
//---------------------------------------------------------------------------
scwin.lfn_Init = function() {
    if (wconv.substr(gfn_GetSysTime(), 0, 8) >= "20160101") {
        btn_fatcaConfirm.setValue("CRS 본인확인");
    } else {
        btn_fatcaConfirm.setValue("FATCA 본인확인");
    }

    //단체취급계약(피보험자)  
    if (parent.Ds_LTIDA00.getCellData(0, "sPsnGroupFlgcd") == "20") {
        //Ds_Subang.Filter("ToNumber(id_cd) == 6");
        wconv.filter(Ds_Subang, "ToNumber(id_cd) < 6",true);
    } else {
        //저축상품은 카드입금 금지
        if (parent.DsWs_PrdAttr.getCellData(0, "GdFlgcd") == "2") {
            wconv.filter(Ds_Subang, "wconv.toNumber(id_cd) == 5",true); //개인계약
        } else {
            wconv.filter(Ds_Subang, "wconv.toNumber(id_cd) < 6",true); //개인계약 
        }
    }


    //07 직접수금 계약자가 개인이 아닌 경우(사업자번호 입력시)에만 직접수금 선택 가능
    if (wconv.length(parent.Ds_LTIDA03.getCellData(0, "sCrtorCd")) != 13) {
        var aRow = util.appendRow(Ds_Subang);
        Ds_Subang.setCellData(aRow, "id_cd", "07");
        Ds_Subang.setCellData(aRow, "id_nm", "직접수금");
    }


	//cbs 추가
	edt_join.setValue("개인형");

    //공통코드호출
    scwin.lfn_commCodeSetting("RlnmCnfmRgcr RlnmCnfmInfoRvMatt1 RlnmCnfmInfoRvMatt2");
//    gfn_getCommonCodes( "DsWs_RlnmCnfmRgcr DsWs_RlnmCnfmInfoRvMatt1 DsWs_RlnmCnfmInfoRvMatt2"
//                                    , "RlnmCnfmRgcr RlnmCnfmInfoRvMatt1 RlnmCnfmInfoRvMatt2"
//                                    , "cmb_RlnmCnfmRgcr cmb_RvMatt1 cmb_RvMatt2"
//                                    , "S");
    //DsWs_RlnmCnfmRgcr.index = 1;
    cmb_RlnmCnfmRgcr.setSelectedIndex(0);

    //trace(Ds_Subang.SaveXml());
    // 대면계약일때 계약체결 이행동의 버튼 NO VISI, 비교 안내 확인 처리. 
    var cnt = scwin.lfn_LapsCnclCrSearch(parent.Ds_Pibo.getCellData(0, "sInrpsCd"));
    if (parent.Ds_LTIDA00.getCellData(0, "sScanObjtYn") == "Y") {
        sScanObjtYn = "Y";
        //승환계약 체크 
        if (cnt > 0 && DsWs_LapsCnclCrList.getCellData(0, "sGdNamePast") != "") {
            wconv.copy(Ds_CmprCrNotc, "DsWs_CmprCrNotc");
            if (Ds_CmprCrNotc.getRowCount() > 0) {
                if (Ds_CmprCrNotc.getCellData(0, "sCmprCrNotc") == "Y") {
                    chk_save.setDisabled(true);
                    btn_Cfn.setDisabled(true);
                    chk_save.setValue("Y");
                }
            } else {
                chk_save.setDisabled(false);
                util.insertColumn(Ds_CmprCrNotc, [["sCmprCrNotc","sCmprCrNotc","text"]]);
                util.appendRow(Ds_CmprCrNotc);
                chk_save.setValue("N");
            }
        } else {
            chk_save.setDisabled(true);
            btn_Cfn.setDisabled(true);
        }

        btn_CrConcAgree.hide();
//        Static19.show();
//        chk_save.setDisabled(false);
//        btn_Cfn.setDisabled(false);
    } else {
        //Static19.hide();
        chk_save.setDisabled(true);
        btn_Cfn.setDisabled(true);
    }


    //승환계약일경우 비교안내건만 안내문발송버튼 확성화
    //승환계약 체크 
    if (cnt > 0 && DsWs_LapsCnclCrList.getCellData(0, "sGdNamePast") != "") {
        if (DsWs_CmprCrNotc.getCellData(0, "sCmprCrNotc") != "Y") {
            btn_PrintInfo.setDisabled(true);
        } else {
            btn_PrintInfo.setDisabled(false);
        }
    } else {
        btn_PrintInfo.setDisabled(false);
    }


    //2014.09.23 추가 By SDJ (N1409-00052 )
    //EDD화면 팝업 버튼은 고액계약(1.월납이면서 5백만원초과, 2.일시납(비월납포함)이면서 2억초과)일경우만 활성화
    if ((parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") != "12" && 
         wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "nBussPrem")) > 200000000) || 
         (parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") == "12" && 
         wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "nBussPrem")) > 5000000)) {
        btn_EddCnfm.show();
    } else {
        btn_EddCnfm.hide();
    }

    //END
    DsWs_PaymCycl.setJSON(parent.DsWs_PaymCycl.getAllJSON()); //납입주기코드(공통)
    /*
    SOBJ_reqMain.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_reqMain, "com.inswave.longterm.ssr.pln.task.DBB330Task", "getInsPlan", "", "");
    DsWs_Warning.DataObject = "DOBJ_reqMain";
    gfn_SetParam(MethodObj, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo")); //설계번호
    var result = gfn_CallService(SOBJ_reqMain, DOBJ_reqMain, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB330Task","getInsPlan", "", "");;
	MethodObj.setId("getInsPlan");
	MethodObj.setParam("sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo")); //설계번호
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA00Doc']/LTIDA00");
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIEA10Doc']/LTIEA10");
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA03Doc']/LTIDA03");	
	MethodObj.addXPath("//hashtable/data[@hashkey='paymCyclCdList']/vector/data/multi");
	MethodObj.addXPath("//hashtable/data[@hashkey='lTIDA07ListDoc']/vector/data/LTIDA07");			
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_LTIDA00.setJSON(_tranSync.getDsJson("LTIDA00"));
	DsWs_LTIEA10.setJSON(_tranSync.getDsJson("LTIEA10"));
	DsWs_LTIDA03.setJSON(_tranSync.getDsJson("LTIDA03"));
	DsWs_PaymCycl.setJSON(_tranSync.getDsJson("multi"));
	DsWs_LTIDA07.setJSON(_tranSync.getDsJson("LTIDA07"));
	util.setRowPosition(DsWs_LTIDA00, 0);
	util.setRowPosition(DsWs_LTIEA10, 0);
	util.setRowPosition(DsWs_LTIDA03, 0);
	util.setRowPosition(DsWs_LTIDA07, 0);

    //trace(replace(gfn_XmlEncoder(DOBJ_reqMain.Value), "><", ">\n<"));
    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    } else {

        /*
        //단체취급계약(피보험자)  
        if (parent.Ds_LTIDA00.GetColumn(0, "sPsnGroupFlgcd") == "20"){
        	Ds_Subang.Filter("ToNumber(id_cd) == 6");
        }else{
        	Ds_Subang.Filter("ToNumber(id_cd) < 6");//개인계약
        }*/
        //피보험자주소 
        edt_RvadAddr.setValue("(" + DsWs_LTIDA03.getCellData(0, "sMailRvadAddrFlagName") + ") " + edt_RvadAddr.getValue());
        //납입주기 세팅
        edt_sPaymCyclCd.setValue(cmb_codeHidden.getText());
        wconv.copy(Ds_LTIEA10, DsWs_LTIEA10);
        Ds_LTIEA10.setRowPosition(0, false);
        var nGroupDcrt = wconv.toNumber(Ds_LTIEA10.getCellData(0, "nGroupDcrt")) * 100; //단체할인율
        Ds_LTIEA10.setCellData(0, "nGroupDcrt", wconv.toString(nGroupDcrt));
        DsWs_LTIEA10.setCellData(0, "nGroupDcrt", wconv.toNumber(DsWs_LTIEA10.getCellData(0, "nGroupDcrt")) * 100);

        //납입주기가 일시납일때처리
        if (parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") == "99") {
            cmb_sCmMetd.setDisabled(true);
            Div.hide();
            Div1.hide();
            cmb_AutoLoan.setDisabled(true);
            cmb_sCmMetd.setValue("");
        } else {
            cmb_sCmMetd.setDisabled(false);
            if (parent.Ds_LTIDA00.getCellData(0, "sCmMetd") == "") {
                //cmb_sCmMetd.Value = '05';
                cmb_sCmMetd.setSelectedIndex(0);
            } else {
                cmb_sCmMetd.setValue(parent.Ds_LTIDA00.getCellData(0, "sCmMetd"));
            }
            scwin.Div_OnLoadCompleted();
        }
    }

    //자동대출비활성화
    if (parent.DsWs_PrdAttr.getCellData(0, "AutoLoanYn") == "N") {
        cmb_AutoLoan.setDisabled(true);
    }

    //계약자주민번호 "*"처리
    //대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
    if (parent.sAgncYn == "Y") {
        gfn_obj_convertMask(medt_sCrtorCd, DsWs_LTIDA03.getCellData(0, "sCrtorCd"), "######-#******");
    }
    btn_ChkReal.show();
    //실명확인 flag seting
    //lfn_RlnmCnfmSetting();

    // 청약사항입력(탭), 입금안내(탭) 예금주관계 활성화 
    //3자 입력이 되도록 재수정(N1405-00217)
    //if (parent.sSimpleYn = "N" &&
    //parent.DsWs_PrdAttr.GetColumn(0, "BunsMetdDocuClsfCd") == "7") {	// 업무보고서분류코드-7:연금
    //Div.cmb_sCrtorRelnFlag.Enable = false;  // 청약사항입력.예금주구분
    //}
    scwin.lfn_B180_CallBack();

    //cbs 추가
    if (wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "sPlanProgRankCd")) == 57 || 
    	wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "sPlanProgRankCd")) == 0 || 
    	wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "sPlanProgRankCd")) == 90) {
        parent.scwin.lfn_ComponentEnable(parent.Tab_Req_Step3, "", false);
    }
};

//---------------------------------------------------------------------------
// function 명 : fn_hwangup
// 내용        : 예상환급금조회
//---------------------------------------------------------------------------
scwin.fn_hwangup = function(obj) {
    var promise = Promise.resolve();
    //var result = Dialog("ssr_sst::B200_Pop.xml",  800, 550, "TaskBar=true Resize=true");	// 장기보험고도화
    var hwangup = "/ui/cbsc/CBSC131P.xml";
    if (parent.DsWs_PrdAttr.getCellData(0, "BunsMetdDocuClsfCd") == "7") {
        // 업무보고서분류코드-7:연금
        hwangup = "/ui/cbsc/CBSC132P.xml";
    }
    promise = promise.then(function(rtnVal) {
    	var pObj = {	"Ds_LTIDA00" : DsWs_LTIDA00,
	    				"Ds_Pibo" : parent.Ds_Pibo,
	    				"Ds_Trty" : parent.Ds_Trty
	    			};
    	var popInfo = {
				title  : "해지환급금 조회",
    			width  : 870,
    			height : 620,
    			parentObject : pObj
    	};
        return wprom.popupOpen(hwangup, popInfo);//800, 550, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
    });
    return promise;
};

//---------------------------------------------------------------------------
// function 명 : fn_summary
// 내용        : 보험료요약
//---------------------------------------------------------------------------
scwin.fn_summary = function(obj) {
    var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
        //개인일경우는 sPsnGroupFlgcd  피보험자단체구분코드
        //var result = Dialog("ssr_pln::B050_Pop.xml", , 800, 550, "TaskBar=true Resize=true");	
	    var indata = {};
	    indata.sPlanNo=parent.Ds_LTIDA00.getCellData(0, "sPlanNo"); //설계번호
	    indata.sPsnGroupFlgcd=parent.Ds_LTIDA00.getCellData(0, "sPsnGroupFlgcd");
	    var pObj = {	"Ds_LTIDA00" : DsWs_LTIDA00,
	    				"Ds_Pibo" : parent.Ds_Pibo,
	    				"Ds_LTIEA10" : DsWs_LTIEA10
	    			};
	    var popInfo = {
	   			title  : "보험료 요약",
				width  : 825,
				height : 550,
				indata : indata,
				parentObject : pObj
	    };
        return wprom.popupOpen("/ui/cbsc/CBSC058P.xml", popInfo);//820, 600, "TaskBar=true AutoSize=false Resize=false"); /*wprom.popupOpen(가이드참조)*/
    });
    return promise;
};

/*-----------------------------------
// function 명 : fn_fatca_OnClick
// 내용        : FATCA 본인확인조회
//2015.03.17 추가 By SDJ(N1503-00064)
-----------------------------------*/
scwin.fn_FatcaYNConfirm_OnClick = function(obj) {
    var promise = Promise.resolve();
    //var arg = "";
    //arg += " sPlanNo=" + wconv.quote(parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    //arg += " sGb=" + wconv.quote(sGb);
    var indata = {};
    indata.sPlanNo=parent.Ds_LTIDA00.getCellData(0, "sPlanNo");
    indata.sGb=sGb;    
    if (wconv.substr(gfn_GetSysTime(), 0, 8) >= "20160101") {
        if (wconv.length(parent.Ds_LTIDA03.getCellData(0, "sCrtorCd")) != 13) {
            promise = promise.then(function(rtnVal) {
                //단체(사업자)
                var popInfo = {
		       			title : "CRS 본인확인(법인)",
						width : 775,
						height : 690,
                		indata : indata
                };
                return wprom.popupOpen("/ui/cbsc/CBSC002P.xml", popInfo);//arg, 744, 776, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
            });
        } else {
            promise = promise.then(function(rtnVal) {
                var popInfo = {
		       			title : "CRS 본인확인",
						width : 800,
						height : 690,
                		indata : indata
                };
                return wprom.popupOpen("/ui/cbsc/CBSC001P.xml", popInfo);//arg, 744, 736, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
            });
        }
    } else {
        promise = promise.then(function(rtnVal) {
             var popInfo = {
		       			title : "FATACA 본인정보 확인",
						width : 815,
						height : 730,
	             		indata : indata
             };
            return wprom.popupOpen("/ui/cbsc/CBSC006P.xml", popInfo);//arg, 550, 850, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
        });
    }
    return promise;
};

scwin.btn_ChkReal_OnClick = function(obj) {
    var promise = Promise.resolve();
    if (btn_fatcaConfirm.getDisabled() == true) {
        sFATCAFlag = 1;
    } else {
        sFATCAFlag = 0;
    }
    //var arg = "";
    //arg += " sPlanNo=" + wconv.quote(parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    //arg += " sGb=" + wconv.quote(sGb);
    //arg += " sFATCAFlag=" + wconv.quote(sFATCAFlag);
    var indata = {};
    indata.sPlanNo=parent.Ds_LTIDA00.getCellData(0, "sPlanNo");
    indata.sGb=sGb;
    indata.sFATCAFlag=sFATCAFlag;

    //cbs 추가
    indata.sPaymCyclCd=parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd");
    indata.nBussPrem=parent.Ds_LTIDA00.getCellData(0, "nBussPrem");
    indata.sBunsMetdDocuClsfCd=parent.Ds_LTIDA00.getCellData(0,"sBunsMetdDocuClsfCd");

    promise = promise.then(function(rtnVal) {
    	var popInfo = {
    					"width"     : 750,
						"height"    : 290,  
						"indata"    : indata,
						"title"     : "실소유자 및 실명확인"
    	};
        return wprom.popupOpen("/ui/cbsc/CBSC071P.xml", popInfo);//arg, 550, 850, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
    });
    return promise;
};

