//---------------------------------------------------------------------------
// function 명 : lfn_FatcaYNSearch
// 내용        : FATCA 본인확인여부 조회
// 2015.03.19 추가 By SDJ(N1503-00064)
//---------------------------------------------------------------------------
scwin.lfn_FatcaYNConfirmSearch = function() {
    wconv.clear(DsWs_FatcaYNConfirm);
    /*
    SOBJ_select.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida00FatcaYN");
    gfn_SetParam(MethodObj, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    gfn_SetParam(MethodObj, "sGb", sGb);
    gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida00FatcaYN");
	MethodObj.setId("getLtida00FatcaYN");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam("sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    MethodObj.setParam("sGb", sGb);	
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//*/LTIDA00");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_FatcaYNConfirm");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_selectWarning");

    if (DsWs_selectWarning.getRowCount() != 0) {
        wprom.alert(DsWs_selectWarning.getCellData(0, "msg"));
        return 0;
    }
    if (wconv.substr(gfn_GetSysTime(), 0, 8) >= "20160101") {
        return DsWs_FatcaYNConfirm.getCellData(0, "sCrsYn");
    } else {
        return DsWs_FatcaYNConfirm.getCellData(0, "sFatcaYN");
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_RealOwnerYN
// 내용        : 실명확인여부 조회
//---------------------------------------------------------------------------
scwin.lfn_RealOwnerYN = function() {
    wconv.clear(DsWs_FatcaYNConfirm);
    /*
    SOBJ_select.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida00FatcaYN");
    gfn_SetParam(MethodObj, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    gfn_SetParam(MethodObj, "sGb", sGb);
    gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtida00FatcaYN");
	MethodObj.setId("getLtida00FatcaYN");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam("sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    MethodObj.setParam("sGb", sGb);	
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//*/LTIDA00");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_FatcaYNConfirm");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_selectWarning");    
    if (DsWs_selectWarning.getRowCount() != 0) {
        wprom.alert(DsWs_selectWarning.getCellData(0, "msg"));
        return 0;
    }
    //wconv.trace(DsWs_FatcaYNConfirm.getCellData(0, "sRealOwnerYn"));
    return DsWs_FatcaYNConfirm.getCellData(0, "sRealOwnerYn");
};


