scwin.fn_eddConfirm = function(obj) {
    var promise = Promise.resolve();
    var indata = {};
    indata.sPlanNo=parent.Ds_LTIDA00.getCellData(0, "sPlanNo");
    indata.sCustNo=parent.Ds_LTIDA03.getCellData(0, "sCrtorCd");
    indata.sName=parent.Ds_LTIDA03.getCellData(0, "sCrtorName");
    indata.sGb=sGb;
    indata.nCrChngNo=parent.Ds_LTIDA00.getCellData(0, "nCrChngNo");

    promise = promise.then(function(rtnVal) {
        //var result = Dialog("ssr_pln::B013_Pop.xml", arg, -1, -1);
        var popInfo = {
       			title : "EDD고객확인",
				width : 805,
				height : 600,        
        	indata : indata
        };
        return wprom.popupOpen("/ui/cbsc/CBSC003P.xml", popInfo);//arg, 670, 830, "TaskBar=true AutoSize=false Resize=false"); /*wprom.popupOpen(가이드참조)*/
    });
    return promise;
};

