                parent.scwin.lfn_ComponentEnable(parent.Tab_Req_Step1, "", false);
                parent.scwin.lfn_ComponentEnable(parent.Tab_Req_Step2, "", false);
            }
        });
    } else {
    	//alert("처리할 수 없는 상태입니다.");
    	wprom.alert("비대면 채널로 설계한 청약입니다. 상담원시스템으로 로그인하세요.");
