//---------------------------------------------------------------------------
// function 명 : fn_next
// 내용        : 다음버튼
//---------------------------------------------------------------------------
scwin.fn_next = function(obj) {
    //	lfn_setDiv();
    //고지사항 입력 누락 확인(재물제외)
    if (DsWs_LTIDA07.getRowCount() <= 0 && 
        DsWs_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd") != "3" && 
        ((DsWs_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd") != "5" || (DsWs_LTIDA00.getCellData(0, "sBunsMetdClsfCd") != "1"))) && 
        DsWs_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd") != "7" && 
        DsWs_LTIDA00.getCellData(0, "sGdCd") != "2900" && 
        DsWs_LTIDA00.getCellData(0, "sGdCd") != "2901" && 
        DsWs_LTIDA00.getCellData(0, "sGdCd") != "2902" &&	// 2018 상품개정
        DsWs_LTIDA00.getCellData(0, "sGdCd") != "2121" &&
        DsWs_LTIDA00.getCellData(0, "sGdCd") != "2127") {
        wprom.alert("고지사항 미입력 계약입니다.");
        return false;
    }

    //일시납이 아닐때만, 수금방법check
    if (parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") != "99") {
        if (cmb_sCmMetd.getValue().length == 0) {
            wprom.alert("수금방법이 없습니다.");
            return false;
        }
    }

    //자금세탁 방지에 따른 필수값 입력 여부 체크
    /*  if(sRlnmCnfmFlag){

		if(cmb_RlnmCnfmRgcr.Value = "" || edt_RvMatt1.Text = "" ){
		   alert("고객 실명 확인사항을 입력하세요");
		   edt_RvMatt1.SetFocus();
		   return true;
		}else if(cmb_RlnmCnfmRgcr.Value > "01" && edt_RvMatt2.Text = "" && rtrim(Stat_RvMatt2.Text) > ""){
		   alert("고객 실명 확인사항을 입력하세요");
		   edt_RvMatt2.SetFocus();
		   return true;
		}
    }*/
    if (scwin.lfn_RealOwnerYN() != "N" && scwin.lfn_RealOwnerYN() != "Y") {
        wprom.alert("고객 실명 확인사항을 입력하세요");
        scwin.btn_ChkReal_OnClick();
        return;
    }

    //2014.10.07 By SDJ(N1410-00035)
    //사업자 등록번호 4~5번째 자리가 82인 경우 확인창 띄워줌
    if (wconv.substr(parent.Ds_LTIDA03.getCellData(0, "sCrtorCd"), 3, 2) == "82") {
        wprom.alert("비영리법인의 경우 설립목적 등에 대한 검증을 위해 정관 등 \n검증가능한 별도 서류를 제출하여야 합니다.");
    }

    //END
    //입금자 모집인동일여부체크
    if (scwin.lfn_selectRecvClprSameYn() == false) {
        wprom.alert("모집인과 보험료 입금자는 동일할 수 없습니다.");
        return false;
    }
    if (wconv.substr(gfn_GetSysTime(), 0, 8) >= "20160101") {
        //if(length(parent.Ds_LTIDA03.GetColumn(0, "sCrtorCd")) == 13){
        if (scwin.lfn_FatcaYNConfirmSearch() != "N" && scwin.lfn_FatcaYNConfirmSearch() != "Y") {
            wprom.alert(" CRS본인확인 내용을 전산 화면에 등록하고 대면 채널의 경우  확인 서류를 스캔하시기바랍니다.");
            return false;
        }
    } else {
        if (wconv.length(parent.Ds_LTIDA03.getCellData(0, "sCrtorCd")) == 13) {
            if (scwin.lfn_FatcaYNConfirmSearch() != "N" && scwin.lfn_FatcaYNConfirmSearch() != "Y") {
                wprom.alert(" FATCA본인확인 내용을 전산 화면에 등록하고 대면 채널의 경우  확인 서류를 스캔하시기바랍니다.");
                return false;
            }
        }
    }

    //지급계좌등록여부확인
    if (scwin.lfn_selectPymAcctRgstYn() == false) {
        wprom.alert("환급금계좌를 등록하시기바랍니다.");
        return false;
    }

    //N1409-00195:대면 영업 대리점 서류심사 프로세스 변경(A000271 대리점 체크 제외 처리)
    if ((parent.Ds_LTIDA00.getCellData(0, "sDoer") == "A000271" && 
         parent.Ds_LTIDA00.getCellData(0, "sSsrtDate") < "20150501") || //2015.07.23 (N1507-00312) : 대면 시스템 선수납 프로세스 적용 요청
        //2015.07.23 : 보장성보험(2805, 2703, 2704 외 전상품)에 대해 20150727부터 20150731까지 작용
        //2015.07.23 (N1507-00312) : 대면 시스템 선수납 프로세스 적용 요청
        //2015.07.23 : 보장성보험(2805, 2703, 2704 외 전상품)에 대해 20150727부터 20150731까지 작용
        (parent.Ds_LTIDA00.getCellData(0, "sScanObjtYn") == "Y" && 
        ((parent.Ds_LTIDA00.getCellData(0, "sSsrtDate") <= "20150731" && 
        parent.Ds_LTIDA00.getCellData(0, "sSsrtDate") >= "20150727")) &&
        ((parent.Ds_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd") != "5" && 
        parent.Ds_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd") != "7"))) || //2015.07.23 : 전상품에 대해 20150801부터 20151031까지 적용 
        //2015.07.23 : 전상품에 대해 20150801부터 20151031까지 적용 
        (parent.Ds_LTIDA00.getCellData(0, "sScanObjtYn") == "Y" && 
        ((parent.Ds_LTIDA00.getCellData(0, "sSsrtDate") <= "20151031" && 
        parent.Ds_LTIDA00.getCellData(0, "sSsrtDate") >= "20150801")))) {
        //null;
        //wconv.trace("A0000271 예외처리" + parent.Ds_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd"));
    } else {
        //if( parent.Ds_LTIDA00.GetColumn(0, "sDoer") != "A000271" ){
        //계약체결 동의가 되었는지 확인.(상품소개 포함(04만 체크))
        if (scwin.lfn_selectPriInfoAgreeList()) {
            return false;
        }

        // 납입동의가 되었는지 확인. 
        if (DsWs_LTIDA00.getCellData(0, "sCmMetd") == "05" || DsWs_LTIDA00.getCellData(0, "sCmMetd") == "03") {
            scwin.lfn_selectAgreYn();
            if (Ds_LTIDA50.getRowCount() == 0 && sCrtorRelnFlag == "40") {
                wprom.alert("카드소유자 및 예금주의 개인신용정보 동의를 진행하시기 바랍니다.");
                return;
            }
        }
    }

    /*
		//대면계약의 경우 이미지 심사 완료 여부 확인 (ldh)
		var ImagJugmYn = false;
		if(sScanObjtYn == "Y"){
			ImagJugmYn = lfn_selectImageJugm();
			if(!ImagJugmYn){
				//	alert("이미지 심사처리가 완료되지 않았습니다");
				return;
			}
		}
	*/

    //자금세탁 방지 위험평가 등급 체크
    var rtn = scwin.lfn_scoreAMLRisk();
    if (rtn <= "3") {
        wprom.alert("자금세탁 위험평가 " + rtn + " 등급입니다.");
    } else if (rtn >= "4") {
        wprom.alert("자금세탁 위험평가 " + rtn + " 등급입니다. EDD 입력 대상입니다.");
    }

    //2014.09.23 추가 By SDJ (N1409-00052)
    //EDD고객정보 입력여부 확인
    var EddYn = false;
    if ((parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") != "12" && wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "nBussPrem")) > 200000000) || // 일시납(비월납포함)이면서 보험료가 2억초과
        (parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd") == "12" && wconv.toNumber(parent.Ds_LTIDA00.getCellData(0, "nBussPrem")) > 5000000) || // 월납이면서 보험료가 5백초과
        (rtn >= "4")) {
        // 위험평가등급이 4,5등급일때
        EddYn = scwin.lfn_getEddInfo();
        if (EddYn == false) {
            return Promise.reject(false);
        }
    }

    var pro = Promise.resolve();

    //if(parent.DsWs_PrdAttr.GetColumn(0,"GdFlgcd") != "2"){ //저축청보험도 비교대상 안내이므로 주석처리
    var cnt = scwin.lfn_LapsCnclCrSearch(parent.Ds_Pibo.getCellData(0, "sInrpsCd"));
    if (wconv.toNumber(cnt) > 0) {
    	if (sScanObjtYn == "N") {
        	if (DsWs_LapsCnclCrList.getCellData(0, "sGdNamePast") != "") {
            	var indata = {};
                indata.sInrpsCd=parent.Ds_Pibo.getCellData(0, "sInrpsCd");
                indata.sSsrtScrnYn="Y";
                indata.sPlanNo=parent.Ds_Pibo.getCellData(0, "sPlanNo");
                indata.sCrtorName=DsWs_LTIDA03.getCellData(0, "sCrtorName");
                indata.sDoer=parent.Ds_LTIDA00.getCellData(0, "sDoer");
                indata.sGdTypeFlagCd=parent.DsWs_PrdAttr.getCellData(0, "GdTypeFlagCd");

                pro = pro.then(function(rv) {
                	var popInfo = {
					       			title : "비교계약안내",
									width : 649,
									height : 600,
					             	indata : indata
					};
                    return wprom.popupOpen("/ui/cbsc/CBSC062P.xml", popInfo);//arg, 10, 5, "Closeflag=false TaskBar=true Resize=true");
                });

                pro = pro.then(function(rv) {
                	var rtnValue = wprom.getReturnData();

                    if (rtnValue == "1") {
                        return;
                    } else {
                        //비교안내후 안내문발송 버튼활성화
                        btn_PrintInfo.setDisabled(false);
                    }
                });
            }
        } else {
            if (DsWs_LapsCnclCrList.getCellData(0, "sGdNamePast") != "") {
                //alert(DsWs_LapsCnclCrCnt.GetColumn(0, "sGdNamePast"));
                //alert(DsWs_LapsCnclCrCnt.GetCount());
                //trace(DsWs_LapsCnclCrCnt.savexml());
                if (DsWs_CmprCrNotc.getCellData(0, "sCmprCrNotc") != "Y" && cnt > 0) {
                    wprom.alert("보험계약 비교안내를 확인하세요");
                    return false;
                }
            }
        }
    }

    pro = pro.then(function(rv) {
 		return util.winTopShowModalPromise(rv);
	});

    pro = pro.then(function(rv) {
        //자금세탁 방지 데이터 셋팅.
        /*if(sRlnmCnfmFlag){
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfmDate", parent.sToday);
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfm", gDoer);
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfmPstnFlgcd", "01");//본인'01'
	    	//미성년자 체크 
	    	if(getAge(DsWs_LTIDA03.GetColumn(0, "sCrtorCd"), parent.sToday) < 20 ){
	    		DsWs_LTIDA03.SetColumn(0, "sRlnmCnfmPstnFlgcd", "02"); //법정대리인'02'
	    	}
	    }else{
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfmDate", "");
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfm", "");
	    	DsWs_LTIDA03.SetColumn(0, "sRlnmCnfmPstnFlgcd", "");
	    }
    	*/
    	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB040Task", "setCmMetd", 0, 0);
		MethodObj.setId("setCmMetd");
		MethodObj.setUrl("/contract/CBSC015MController.do");
		MethodObj.setHashTable("perform");   // 저장할 때

        //계약기본사항을 담는다
        Div.Ds_Args.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
        Div.Ds_Args.setCellData(0, "sUserIP", util.getGdsValue("gUserIP"));
        util.insertColumn(DsWs_LTIDA00, [["sAutoLoanYn", "sAutoLoanYn", "text"],["sAutoLoanRqstDate", "sAutoLoanRqstDate", "text"]]);
		util.setCompBindRefresh(DsWs_LTIDA00.getID());
    	DsWs_LTIDA00.setRowPosition(0, false);

        DsWs_LTIDA00.setCellData(0, "sAutoLoanYn", cmb_AutoLoan.getValue());
        if (cmb_AutoLoan.getValue() == "Y") {
            DsWs_LTIDA00.setCellData(0, "sAutoLoanRqstDate", parent.sToday);
        } else {
            DsWs_LTIDA00.setCellData("sAutoLoanRqstDate", "");
        }
        MethodObj.addSingleData("LTIDA00", "LTIDA00", "DsWs_LTIDA00:A"); // 계약기본사항정보
        MethodObj.addSingleData("LTIDA03", "LTIDA03", "DsWs_LTIDA03:A"); // 계약자정보 사항(실명확인 정보 업데이트 처리)
        if (cmb_sCmMetd.getValue() == "05") {
            //자동이체계좌 저장입력값 체크
            if (Div.scwin.lfn_reqChkValue() == false) {
                return;
            }
            util.dataListCloneCopy(Div.Ds_Args, "pDs_Args", "0");
            util.dataListCloneCopy(Div.Ds_LTIFA03, "pDs_LTIFA03", "0");
            MethodObj.addVectorData("Args", "Args", "pDs_Args:A");
            MethodObj.addVectorData("LTIFA03", "LTIFA03", "pDs_LTIFA03:A");
        } else if (cmb_sCmMetd.getValue() == "03") {
            //카드이체 저장입력값 체크
            if (Div1.scwin.lfn_reqChkValue() == false) {
                return;
            }
            util.dataListCloneCopy(Div1.Ds_Args, "pDs_Args", "0");
            util.dataListCloneCopy(Div1.Ds_LTIFA03, "pDs_LTIFA03", "0");
            MethodObj.addVectorData("Args", "Args", "pDs_Args:A");
            MethodObj.addVectorData("LTIFA03", "LTIFA03", "pDs_LTIFA03:A");
        } else if (cmb_sCmMetd.getValue() == "07") {}
        //trace("DsWs_LTIDA00.SaveXML() ===> " + DsWs_LTIDA00.SaveXML()); 
        setTail("hashtable");

		MethodObj.setXPath("//*/LTIDA17");
	    MethodObj.addXPath("//*/LTIDC93");
		MethodObj.addXPath("//*/WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
	    if (DsWs_Warning.getRowCount() != 0) {
	        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
	        return;
        } else {
            //상위 계약정보에 상태 수정
            parent.Ds_LTIDA00.setCellData(0, "sPlanProgRankCd", "60"); //설계진행단계코드 60 계약요약
            parent.Ds_LTIDA00.setCellData(0, "sPlanProgTbNo", "03"); //설계진행탭번호
            parent.Ds_LTIDA00.setCellData(0, "sCmMetd", cmb_sCmMetd.getValue()); //수금방법
            var flag = parent.scwin.lfn_TabChanging(2, 3);
            if (flag) {
            	parent.scwin.lfn_Tab_Focus(3);
            }
        }
    });

    pro = pro.then(function(rv) {
 		util.winTopHideModal();
	});

    return pro;
};

//---------------------------------------------------------------------------
// function 명 : cmb_queryType_OnChanged
// 내용        : 수금방법변경시 처리
//---------------------------------------------------------------------------
scwin.cmb_sCmMetd_OnChanged = function() {
    //lfn_setDiv();
    var strCode = cmb_sCmMetd.getValue();

    switch (strCode) {
        case "05":
            //자동이체	
            Div_CmMetd05.setStyle("display", "");
            Div_CmMetd03.setStyle("display", "none");
            //cbs 임시 - div.Url = "dpt_rec::F150_Div1.xml";
            Div.Ds_Args.setCellData(0, "sPrcCat", "1");
            // 처리구분 (필수): 1(청약),2(기타),3(변경) 
            Div.Ds_Args.setCellData(0, "sApplCat", "1");
            // 신청구분 (필수): 1(신규),3(계좌변경),5(해지)
            Div.Ds_Args.setCellData(0, "sCrNo", "");
            // 계약번호 (필수)
            Div.Ds_Args.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
            // 설계번호 (청약 필수, 변경 옵션)
            Div.Ds_Args.setCellData(0, "sAcctFlag", "1");
            // 계좌구분 (필수)
            Div.Ds_Args.setCellData(0, "nCrChngNo", "0");
            // 변경번호 (필수, 청약입력시: 0)
            Div.Ds_Args.setCellData(0, "sCrtorCd", DsWs_LTIDA03.getCellData(0, "sCrtorCd"));
            Div.Ds_Args.setCellData(0, "sCrtorName", edt_sCrtorName.getValue());
            Div.Ds_Args.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
            Div.Ds_Args.setCellData(0, "sUserIP", util.getGdsValue("gUserIP"));
            Div.Ds_Args.setCellData(0, "sInrpsCd", parent.Ds_Pibo.getCellData(0, "sInrpsCd"));
            Div.Ds_Args.setCellData(0, "sInrpsName", parent.Ds_Pibo.getCellData(0, "sInrpsName"));
            Div.Ds_Args.setCellData(0, "sInsurStrtdate", parent.Ds_LTIDA00.getCellData(0, "sInsurStrtdate"));
            Div.scwin.lfn_initF150_Div1();
            break;
        case "03":
            //카드입금
            Div_CmMetd05.setStyle("display", "none");
            Div_CmMetd03.setStyle("display", "");
            //Div1.Url = "dpt_rec::F170_Div1.xml";
            //초기 조회 및 처리용 데이터
            Div1.Ds_Args.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
            // 설계번호 (청약 필수, 변경 옵션)
            Div1.Ds_Args.setCellData(0, "sCrNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
            // 계약번호 (필수)
            Div1.Ds_Args.setCellData(0, "sPrcCat", "1");
            // 처리구분 (필수): 1(청약),2(기타),3(변경) 
            Div1.Ds_Args.setCellData(0, "sCmMetdBefo", "");
            // 변경전 수금방법
            Div1.Ds_Args.setCellData(0, "nCrChngNo", "0");
            // 변경번호 (필수, 청약: 0 )
            Div1.Ds_Args.setCellData(0, "sCrtorCd", DsWs_LTIDA03.getCellData(0, "sCrtorCd"));
            Div1.Ds_Args.setCellData(0, "sCrtorName", edt_sCrtorName.getValue());
            Div1.Ds_Args.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
            Div1.Ds_Args.setCellData(0, "sUserIP", util.getGdsValue("gUserIP"));
            Div1.Ds_Args.setCellData(0, "sInrpsCd", parent.Ds_Pibo.getCellData(0, "sInrpsCd"));
            Div1.Ds_Args.setCellData(0, "sInrpsName", parent.Ds_Pibo.getCellData(0, "sInrpsName"));
            Div1.scwin.lfn_initF170_Div1();
            break;
        default:
            Div.setStyle("display", "none");
            Div1.setStyle("display", "none");
            break;
    }

    //cbs 추가
    parent.scwin.lfn_set_Tab_Info("S3", "T1");
};

//---------------------------------------------------------------------------
// function 명 : lfn_setDiv
// 내용        : dIV내부채우기
//---------------------------------------------------------------------------
scwin.lfn_setDiv = function() {
    //Div.lfn_reqChkValue());
    Div.Ds_Args.setCellData(0, "sPrcCat", "1"); // 처리구분 (필수): 1(청약),2(기타),3(변경) 
    Div.Ds_Args.setCellData(0, "sApplCat", "1"); // 신청구분 (필수): 1(신규),3(계좌변경),5(해지)
    Div.Ds_Args.setCellData(0, "sCrNo", ""); // 계약번호 (필수)
    Div.Ds_Args.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo")); // 설계번호 (청약 필수, 변경 옵션)
    Div.Ds_Args.setCellData(0, "sAcctFlag", "1"); // 계좌구분 (필수)
    Div.Ds_Args.setCellData(0, "nCrChngNo", "0"); // 변경번호 (필수, 청약입력시: 0)
    Div.Ds_Args.setCellData(0, "sCrtorCd", DsWs_LTIDA03.getCellData(0, "sCrtorCd")); // 계약자 코드 (필수)
    Div.Ds_Args.setCellData(0, "sCrtorName", DsWs_LTIDA03.getCellData(0, "sCrtorCdName")); // 계약자명 (필수)}
    Div.Ds_Args.setCellData(0, "sUserID", util.getGdsValue("gUserID"));
    Div.Ds_Args.setCellData(0, "sUserIP", util.getGdsValue("gUserIP"));
};

//---------------------------------------------------------------------------
// function 명 : openPrint()
// 내용        : 가입설계서
//---------------------------------------------------------------------------
scwin.openPrint = function(obj) {
    //해약환급처리
    parent.scwin.lfn_RtnAmt();
    var sArg = "";
    var sPlanNo = parent.medt_PlanNo.getValue();
    var sPsnGroupFlgcd = parent.Ds_LTIDA00.getCellData(0, "sPsnGroupFlgcd");

//    if (sScanObjtYn == "Y") {
//        sArg += " sOdiId='B621_Rep'";
//    } else {
//        sArg += " sOdiId='B400_Rep'";
//    }
//    sArg += " sPlanNo=" + sPlanNo;
//    sArg += " sPsnGroupFlgcd=" + sPsnGroupFlgcd;
//    sArg += " sGdCd=" + parent.edt_sGdCd.getValue();

	var reportInfo  = {}
    if (sScanObjtYn == "Y") {
		reportInfo.rptName = "longterm/B621_Rep";
	}else{
		reportInfo.rptName = "longterm/B400_Rep";
	}	
	reportInfo.dataType       = "xml";
	reportInfo.taskValue      = "com.inswave.longterm.psv.ens.task.EBE200Task";
	reportInfo.actionValue    = "printB621_RepV01";
	reportInfo.sInterfaceType = "Y";
	reportInfo.gUserID        = util.getGdsValue("gUserID");
	reportInfo.setParam       = [ "sPlanNo|"+ sPlanNo
	                             ,"sPsnGroupFlgcd|"+sPsnGroupFlgcd
	                             ,"sGdCd|"+parent.edt_sGdCd.getValue()];
	reportInfo.title          = "가입설계서";

	wprom.reportOpen(reportInfo);

    //return wprom.popupOpen("PROJECT_PATH_TEMP/ssr_out/CBSC108P.xml", sArg, 928, 688); /*wprom.popupOpen(가이드참조)*/
};

