scwin.lfn_selectAgreYn = function() {
    wconv.clear(DsWs_LTIDA50); //리스트 내용삭제
    wconv.clear(Ds_LTIDA50);
    var sDpsrCd;
    var sCmMetd;

console.log("=== scwin.lfn_selectAgreYn : "+ cmb_sCmMetd.getValue()); 
    if (cmb_sCmMetd.getValue() == "05") {
        sDpsrCd = Div.Ds_LTIFA03.getCellData(0, "sDpsrCd");
        sCrtorRelnFlag = Div.Ds_LTIFA03.getCellData(0, "sCrtorRelnFlag");
    } else if (cmb_sCmMetd.getValue() == "03") {
        sDpsrCd = Div1.Ds_LTIFA03.getCellData(0, "sDpsrCd");
        sCrtorRelnFlag = Div1.Ds_LTIFA03.getCellData(0, "sCrtorRelnFlag");
    }
    if (sCrtorRelnFlag == "40") {
		/*
	    SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
	    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.dpt.rec.task.FAF400Task", "selectRecvAgreYn", "", "");
	    gfn_SetParam(MethodObj, "sPlanNo", parent.medt_PlanNo.Value);
	    gfn_SetParam(MethodObj, "sCustNo", sDpsrCd);
	    gfn_SetParam(MethodObj, "sCmMetd", cmb_sCmMetd.getValue());
	        //2014.09.17 By SDJ (N1408-00240)
	        //대면일경우는 카드이체도 자동이체로 조회해야함 (※ 제3자동의서 심사 시 납입방법을 자동이체로 세팅하기때문 )
	        if (sScanObjtYn == "Y" && cmb_sCmMetd.getValue() == "03") {
	            gfn_SetParam(MethodObj, "sCmMetd", "05");
	        }

	        //END
	        var result = gfn_CallService(SOBJ_select, DOBJ_select2, MethodObj);
	   */
		var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF400Task", "selectRecvAgreYn");
		MethodObj.setId("selectRecvAgreYn");

		//MethodObj.setParam("sAgentCode", gUserID);//
	    MethodObj.setParam("sPlanNo", parent.medt_PlanNo.getValue());
	    MethodObj.setParam("sCustNo", sDpsrCd);
	    MethodObj.setParam("sCmMetd", cmb_sCmMetd.getValue());
        if (sScanObjtYn == "Y" && cmb_sCmMetd.getValue() == "03") {
            MethodObj.setParam("sCmMetd", "05");
        }		
		MethodObj.setUrl("/contract/CBSC015MController.do");
		MethodObj.setXPath("//*/CUSAA35");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();
		//util.interfaceKey(_tranSync.result.serviceDs);
		//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
		util.jSonDsCreate(_tranSync.getDsJson("CUSAA35"), "DsWs_LTIDA50");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "WARNING");
		util.setCompBindRefresh("DsWs_LTIDA50");
		util.setRowPosition(DsWs_LTIDA50, 0);

        if (DsWs_LTIDA50.getCellData(0, "sAgmYn") == "Y") {
			util.jSonDsCreate(_tranSync.getDsJson("CUSAA35"), "Ds_LTIDA50");
            //wconv.copy(Ds_LTIDA50, "DsWs_LTIDA50");
        }
    } else {
        util.appendRow(Ds_LTIDA50);
    }
};

