scwin.lfn_CrConcAgree_OnClick = function(obj) {
    var promise = Promise.resolve();
    /*
    //계약체결 동의 대상자 db에 insert 처리.
    SOBJ_SAVE.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
    //var sPlanNo = parent.Ds_LTIDA03.getColumn(0,"sPlanNo");
    var sPlanNo = parent.medt_PlanNo.value;
    var MethodObj = gfn_CreateMethodObj(SOBJ_SAVE, "com.inswave.longterm.dpt.rec.task.FAF400Task", "insertPriInfoAgreeLong", 0, 0); //  	
    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo);
    gfn_SetParam(MethodObj, "sApplyNo", sPlanNo);
    gfn_SetParam(MethodObj, "sAgmGvs", "10");
    gfn_SetParam(MethodObj, "sMdfcDler", util.getGdsValue("gUserID"));
    gfn_SetParam(MethodObj, "sMdfcDlerIP", util.getGdsValue("gUserIP"));
    var result = gfn_CallService(SOBJ_SAVE, DOBJ_SAVE, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF400Task", "insertPriInfoAgreeLong");
	MethodObj.setId("insertPriInfoAgreeLong");

    var sPlanNo = parent.medt_PlanNo.getValue();
    MethodObj.setParam( "sPlanNo", sPlanNo);
    MethodObj.setParam( "sApplyNo", sPlanNo);
    MethodObj.setParam( "sAgmGvs", "10");
    MethodObj.setParam( "sMdfcDler", util.getGdsValue("gUserID"));
    MethodObj.setParam( "sMdfcDlerIP", util.getGdsValue("gUserIP"));	
	MethodObj.setUrl("/contract/CBSC015MController.do");	
	MethodObj.setXPath("//*/LTIDA17");
	MethodObj.setXPath("//*/LTIDC93");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "ds_Wanning");

    //trace(replace(gfn_XmlEncoder(DOBJ_SAVE.Value), "><", ">\n<"));
    //에러처리
    if (ds_Wanning.getRowCount() != 0) {
        wprom.alert(ds_Wanning.getCellData(0, "msg"));
        return;
    } else {
        promise = promise.then(function(rtnVal) {
	        var indata = {};
	        indata.sApplyNo=sPlanNo;
	        indata.sAgmGvs="10";
        	var popInfo = {
				title  : "계약 체결 이행 동의",
    			width  : 595,
    			height : 356,
    			indata : indata
	    	};
	        return wprom.popupOpen("/ui/cbsc/CBSC026P.xml", popInfo);	//PriInfoAgree_Pop
        });
    }
    return promise;
};

