//---------------------------------------------------------------------------
// function 명 : lfn_selectPriInfoAgreeList
// 내용        : 계약체결이행동의 대상자 리스트 
// sCustNo(주민번호), sApplyNo = sPlanNo
//---------------------------------------------------------------------------
scwin.lfn_selectPriInfoAgreeList = function() {
    if (sScanObjtYn == "Y") {
    	return false;
        //wconv.trace("대면은 입금안내에서 체결이행 체크");
    } else {
        wconv.clear(ds_Wanning);
        wconv.clear(DsWs_CUSAA35);
        /*
        SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
        var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.customer.task.CustInfoTask", "checkCUSAA35CUSAA04Info", 0, 0); //  
        gfn_SetParam(MethodObj, "sApplyNo", parent.Ds_LTIDA03.getCellData(0, "sPlanNo"));
        gfn_SetParam(MethodObj, "sAgmGvs", "10");
        //trace("sPlanNo:" + parent.Ds_LTIDA03.getColumn(0,"sPlanNo") );
        var result = gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);
		*/
		var MethodObj = new callService("com.inswave.customer.task.CustInfoTask", "checkCUSAA35CUSAA04Info");
		MethodObj.setId("checkCUSAA35CUSAA04Info");
        MethodObj.setParam("sApplyNo", parent.Ds_LTIDA03.getCellData(0, "sPlanNo"));
        MethodObj.setParam("sAgmGvs", "10");
		MethodObj.setUrl("/contract/CBSC015MController.do");
		MethodObj.setXPath("//*/CUSAA35");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();

		util.jSonDsCreate(_tranSync.getDsJson("CUSAA35"), "DsWs_CUSAA35");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "ds_Wanning");

        //trace(replace(gfn_XmlEncoder(DOBJ_select.Value), "><", ">\n<"));
        //trace(DsWs_CUSAA35.SaveXML());
        //에러처리
        if (ds_Wanning.getRowCount() != 0) {
            wprom.alert(ds_Wanning.getCellData(0, "msg"));
            //return "Y"; // needto 동의 
            return true;
        }
        if (DsWs_CUSAA35.getRowCount() > 0) {
            //Y값은 반드시 있고 N값은 전혀 없어야 함.
            if (wconv.findRow(DsWs_CUSAA35, "sCUSAA04AgmReq", "N") > -1 && 
                wconv.findRow(DsWs_CUSAA35, "sCUSAA35AgmReq", "N") > -1 && 
                wconv.findRow(DsWs_CUSAA35, "sCUSAA41AgmReq", "N") > -1) {
                //return "N";
                return false;
            } else {
                wprom.alert("계약체결 이행동의를 확인하세요");
                //return "Y"; // needto 동의 
                return true;
            }
        }
    }
    return false;
};

//---------------------------------------------------------------------------
// function 명 : lfn_LapsCnclCrSearch
// 내용        : 승환계약 비교안내 대상 체크 
// sInrpsCd(피보험자 주민번호)
//---------------------------------------------------------------------------
scwin.lfn_LapsCnclCrSearch = function(sInrpsCd) {
    //피보험자가 없는 경우 승환계약 비교안함.(재물보험)
    if (parent.Ds_Pibo.getRowCount() < 1) {
        return 0;
    }
    wconv.clear(DsWs_LapsCnclCrCnt);
    wconv.clear(DsWs_CmprCrNotc);
    wconv.clear(DsWs_LapsCnclCrList);
    /*
    SOBJ_Search.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.psv.ens.task.EAD071Task", "selLapsCnclCrList", "", "");
    gfn_SetParam(MethodObj, "sInrpsCd", sInrpsCd);
    gfn_SetParam(MethodObj, "sSsrtScrnYn", "Y");
    gfn_SetParam(MethodObj, "sPlanNo", parent.Ds_Pibo.getCellData(0, "sPlanNo"));
    gfn_SetParam(MethodObj, "sGdTypeFlagCd", parent.DsWs_PrdAttr.getCellData(0, "GdTypeFlagCd"));
    gfn_CallService(SOBJ_Search, DOBJ_LapsCnclCrSearch, MethodObj);
	*/
	var MethodObj = new callService("com.inswave.customer.task.CustInfoTask", "checkCUSAA35CUSAA04Info", 0, 0);
	MethodObj.setId("checkCUSAA35CUSAA04Info");
    MethodObj.setParam("sInrpsCd", sInrpsCd);
    MethodObj.setParam("sSsrtScrnYn", "Y");
    MethodObj.setParam("sPlanNo", parent.Ds_Pibo.getCellData(0, "sPlanNo"));
    MethodObj.setParam("sGdTypeFlagCd", parent.DsWs_PrdAttr.getCellData(0, "GdTypeFlagCd"));
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//hashtable/data[@hashkey='LapsCnclCrCnt']/LTIEA00Cnt");	
	MethodObj.addXPath("//hashtable/data[@hashkey='CmprCrNotc']/CmprCrNotc");
	MethodObj.addXPath("//*/LTIEA00");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	//util.interfaceKey(_tranSync.result.serviceDs);
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00Cnt"), "DsWs_LapsCnclCrCnt");
	util.jSonDsCreate(_tranSync.getDsJson("CmprCrNotc"), "DsWs_CmprCrNotc");
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LapsCnclCrList");

    //trace(replace(gfn_XmlEncoder(DOBJ_LapsCnclCrSearch.Contents), "><", ">\n<")); 
    // trace(DsWs_LapsCnclCrCnt.SaveXML());
    //trace(DsWs_LapsCnclCrList.savexml());
    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), warning_LapsCnclCrCnt.getID());
    if (warning_LapsCnclCrCnt.getRowCount() != 0) {
        wprom.alert(warning_LapsCnclCrCnt.getCellData(0, "msg"));
        return 0;
    }

    /*
    if(DsWs_CmprCrNotc.GetRowCount() > 0){
    	Ds_CmprCrNotc.Copy("DsWs_CmprCrNotc");
    	if(Ds_CmprCrNotc.GetColumn(0,"sCmprCrNotc") == "Y"){
    		chk_save.Enable = false;
    		btn_Cfn.Enable = false;
    	}else{
    	//chk_save.Enable = true;
    	//btn_Cfn.Enable = true;
    	//Ds_CmprCrNotc.AddRow();
    	}
    }else{
    	//chk_save.Enable = true;
    	Ds_CmprCrNotc.AddColumn("sCmprCrNotc");
    	Ds_CmprCrNotc.AddRow();
    	Ds_CmprCrNotc.SetColumn(0,"sCmprCrNotc","N");		
    }
    */
    if (DsWs_LapsCnclCrList.getCellData(0, "sGdNamePast") == "") {
        return 0;
    } else {
        return DsWs_LapsCnclCrList.getRowCount();
    }
};

