        //trace(replace(gfn_XmlEncoder(DOBJ_Save.Contents), "><", ">\n<")); 
        //trace(ds_Wanning.SaveXML());
        if (ds_Wanning.getCellData(0, "result") == "1") {
            wprom.alert("저장되었습니다");
            //비교안내후 안내문발송 버튼활성화
            btn_PrintInfo.setDisabled(false);
            return;
        }
    } else {
        wprom.alert("비교안내 체크박스를 선택하세요");
        return;
    }
};

