//---------------------------------------------------------------------------
// function 명 : lfn_RlnmCnfmSetting
// 내용        :  실명확인 기준 셋팅.
//---------------------------------------------------------------------------
scwin.lfn_RlnmCnfmSetting = function() {
    //월납보험료 25만원초과, 연납 300만원초과, 일시납 500만원 초과시 고객정보 사항 입력필수, 미입력시 팝업안내
    //          안내문구 : “고객 실명확인사항을 입력하세요”
    //변경 : 저축/연금 가입 전건, 보장성 월납 25만원 이상 or 일시납 1천만원 이상[N1410-00071] -> 월납25만원이상제외
    sRlnmCnfmFlag = false;
    var sPaymCyclCd = parent.Ds_LTIDA00.getCellData(0, "sPaymCyclCd"); //납입주기 "99"일시납 
    var nBussPrem = parent.Ds_LTIDA00.getCellData(0, "nBussPrem"); //납입 보험료 
    var sRlnmCnfmRgcr = DsWs_LTIDA03.getCellData(0, "sRlnmCnfmRgcr"); //실명확인 증표 코드
    var sBunsMetdDocuClsfCd = parent.Ds_LTIDA00.getCellData(0, "sBunsMetdDocuClsfCd");

    //trace("B010_Div3 sBunsMetdDocuClsfCd : " + sBunsMetdDocuClsfCd);
    if (sBunsMetdDocuClsfCd == "5" || sBunsMetdDocuClsfCd == "7") {
        //저축,연금
        sRlnmCnfmFlag = true;
    } else if (sPaymCyclCd == "99" && wconv.toNumber(nBussPrem) >= 10000000) {
        //일시납 1천만원이상
        sRlnmCnfmFlag = true; //}else if((sPaymCyclCd <> "99") &&((ToNumber(nBussPrem) / 12 * ToNumber(sPaymCyclCd)) >= 250000)){ //월납25만이상
        //   sRlnmCnfmFlag = true;
    }
    if (sRlnmCnfmFlag) {
    	//cbs 변경
    	id_RlnmCnfmRgcr.setStyle("display", "");
//        Static23.show();
//        cmb_RlnmCnfmRgcr.show();
//        Stat_RvMatt1.show();
//        Stat_RvMatt2.show();
//        edt_RvMatt1.show();
//        edt_RvMatt2.show();
        if (wconv.length(sRlnmCnfmRgcr) > 0) {
            scwin.lfn_RlnmCnfmRgcr("cmb_Flag", sRlnmCnfmRgcr, "", "", "");
        }
    } else {
    	//cbs 변경
    	id_RlnmCnfmRgcr.setStyle("display", "");
//        Static23.hide();
//        cmb_RlnmCnfmRgcr.hide();
//        Stat_RvMatt1.hide();
//        Stat_RvMatt2.hide();
//        edt_RvMatt1.hide();
//        edt_RvMatt2.hide();
        edt_RvMatt1.setValue("");
        edt_RvMatt2.setValue("");
        cmb_RlnmCnfmRgcr.setValue("");
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_RlnmCnfmRgcr
// 내용        :  (실명확인증표 선택에 따른 증빙 대상건 조회)
//---------------------------------------------------------------------------
scwin.lfn_RlnmCnfmRgcr = function(info) {
console.log("lfn_RlnmCnfmRgcr :"+this.getID());
    if (wconv.length(info.newValue) < 1) {
        Stat_RvMatt1.setValue("실명확인정보1");
        Stat_RvMatt2.setValue("실명확인정보2"); //edt_RvMatt1.Enable = false;
        //edt_RvMatt2.Enable = false;
    } else {
        Stat_RvMatt1.setTitle(DsWs_RlnmCnfmInfoRvMatt1.getCellData(wconv.findRow(DsWs_RlnmCnfmInfoRvMatt1, "sCd", info.newValue), "sHnglCdName"));
        Stat_RvMatt2.setTitle(DsWs_RlnmCnfmInfoRvMatt2.getCellData(wconv.findRow(DsWs_RlnmCnfmInfoRvMatt2, "sCd", info.newValue), "sHnglCdName"));

        //edt_RvMatt1.Enable = true;
        edt_RvMatt1.focus();
        if (this.getID() != "cmb_Flag") {
            edt_RvMatt1.setValue("");
            edt_RvMatt2.setValue("");
        }
        if (wconv.length(Stat_RvMatt2.getTitle()) < 1) {
            edt_RvMatt2.setDisabled(true);
        } else {
            edt_RvMatt2.setDisabled(false);
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_setALMRisk
// 내용        : 자금세탁방지(위험평가 등급 산정)
//---------------------------------------------------------------------------
scwin.lfn_scoreAMLRisk = function() {
    var sGrade = 0;
    wconv.clear(Ds_LTIDA00Risk);
	/*
    SOBJ_Save.WSDL = gds.gWsdlBasis; //기본 웹서비스
    var MethodObj = SOBJ_Save.CreateMethodObj("perform");
    setHeader("hashtable", "com.inswave.longterm.ssr.pln.task.DBB024Task", "scoreAMLRisk");
    */
    //계약기본사항을 담는다
    util.dataListCloneCopy(DsWs_LTIDA00, "Ds_LTIDA00Risk", "0");
    util.insertColumn(Ds_LTIDA00Risk, [["gUserID", "gUserID", "text"]]);
    util.insertColumn(Ds_LTIDA00Risk, [["gUserIP", "gUserIP", "text"]]);
    Ds_LTIDA00Risk.setCellData(0, "gUserID", util.getGdsValue("gUserID"));
    Ds_LTIDA00Risk.setCellData(0, "gUserIP", util.getGdsValue("gUserIP"));
    /*
    addSingleData("LTIDA00", "LTIDA00", Ds_LTIDA00Risk, "LTIDA00"); // 계약기본사항정보
    addSingleData("LTIDA03", "LTIDA03", DsWs_LTIDA03, "LTIDA03"); // 계약자정보 사항(실명확인 정보 업데이트 처리)
    //피보험자사항 담는다
    addVectorData("vLTIDA01", "vLTIDA01", parent.Ds_Pibo); //피보험자사항
    if (cmb_sCmMetd.getValue() == "05") {
        addSingleData("LTIFA03", "LTIFA03", div.Ds_LTIFA03, "LTIFA03");
    } else if (cmb_sCmMetd.getValue() == "03") {
        addSingleData("LTIFA03", "LTIFA03", div1.Ds_LTIFA03, "LTIFA03");
    }
    setTail("hashtable");
    DsWs_Warning.DataObject = "DOBJ_Save";
    DsWs_LTIDA17.DataObject = "DOBJ_Save";
    SOBJ_Save.CreateObj("in0");
    MethodObj.in0 = insertStr;
    var result = gfn_CallService(SOBJ_Save, DOBJ_Save, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB024Task", "scoreAMLRisk");
	MethodObj.setId("scoreAMLRisk");

    MethodObj.addSingleData("LTIDA00", "LTIDA00", "Ds_LTIDA00Risk"); // 계약기본사항정보
    MethodObj.addSingleData("LTIDA03", "LTIDA03", "DsWs_LTIDA03"); // 계약자정보 사항(실명확인 정보 업데이트 처리)
    //피보험자사항 담는다
    util.dataListCloneCopy(parent.Ds_Pibo, "pDs_Pibo", "0");
    MethodObj.addVectorData("vLTIDA01", "vLTIDA01", "pDs_Pibo:A"); //피보험자사항
    if (cmb_sCmMetd.getValue() == "05") {
    	util.dataListCloneCopy(Div.Ds_LTIFA03, "dDs_LTIFA03", "0");
        MethodObj.addSingleData("LTIFA03", "LTIFA03", "dDs_LTIFA03:A");//, "LTIFA03");
    } else if (cmb_sCmMetd.getValue() == "03") {
    	util.dataListCloneCopy(Div1.Ds_LTIFA03, "dDs_LTIFA03", "0");
        MethodObj.addSingleData("LTIFA03", "LTIFA03", "dDs_LTIFA03:A");//, "LTIFA03");
    }
	MethodObj.setUrl("/contract/CBSC015MController.do");  	// 서버단 호출 URL (수납 확정)    
	MethodObj.setXPath("//*/LTIDA17");	// XPath
	MethodObj.addXPath("//WARNING");	// XPath
	MethodObj.setHashTable();   		// 전문의 앞이 hashtable로 보낼 때
	MethodObj.sync();					// 동기 처리[기본(async:비동기)]
	MethodObj.send(); 					// 최종 전송

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA17"), "DsWs_LTIDA17");

	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");	
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    } else {
        sGrade = DsWs_LTIDA17.getCellData(0, "sEvalGrad");
        return sGrade;
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_getEddInfo
// 내용        : 자금세탁방지(EDD입력확인여부)
//---------------------------------------------------------------------------
scwin.lfn_getEddInfo = function() {
    //DsWs_Warning.DataObject = "DOBJ_Save";
    //DsWs_LTIDC93.DataObject = "DOBJ_Save";
    wconv.clear(DsWs_LTIDC93);
    /*
    SOBJ_Save.WSDL = gds.gWsdlBasis; //기본웹서비스
    MethodObj = gfn_CreateMethodObj(SOBJ_Save, "com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtidc93", "", "");
    gfn_SetParam(MethodObj, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    gfn_SetParam(MethodObj, "sCustNo", parent.Ds_LTIDA03.getCellData(0, "sCrtorCd"));
    gfn_CallService(SOBJ_Save, DOBJ_Save, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB013Task", "getLtidc93");
	MethodObj.setId("getLtidc93");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam("sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
    MethodObj.setParam("sCustNo", parent.Ds_LTIDA03.getCellData(0, "sCrtorCd"));	
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//*/LTIDC93");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("LTIDC93"), "DsWs_LTIDC93");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

    if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return false;
    } else {
        if (wconv.length(DsWs_LTIDC93.getCellData(0, "sPlanNo")) == 0) {
            wprom.alert("EDD 고객정보를 입력하시기 바랍니다.");
            return false;
        } else if (wconv.findRow(DsWs_LTIDC93, "sCnfmYn", "") > -1 || wconv.findRow(DsWs_LTIDC93, "sCnfmYn", "N") > -1) {
            wprom.alert("EDD 고객정보 확인 대상입니다.");
            return false;
        } else {
            return true;
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_selectRecvClprSameYn
// 내용        : 입금자 모집인동일여부체크
//---------------------------------------------------------------------------
scwin.lfn_selectRecvClprSameYn = function() {
    //wconv.trace("lfn_selectRecvClprSameYn");
    wconv.clear(DsWs_RecvClprSameYn);
    wconv.clear(DsWs_Warning);
    //DsWs_Warning.DataObject = "DOBJ_select";
    var sDpsrCd;
    var sCmMetd;
    /*
    SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.ssr.pln.task.DBB040Task", "selectRecvClprSameYn", "", "");
    if (cmb_sCmMetd.getValue() == "05") {
        //자동이체
        sDpsrCd = s_LTIFA03.GetColumn(0, "sDpsrCd");
    } else if (cmb_sCmMetd.getValue() == "03") {
        //카드입금
        sDpsrCd = Ds_LTIFA03.getCellData(0, "sDpsrCd");
    }
    gfn_SetParam(MethodObj, "sDpsrCd", sDpsrCd);
    gfn_SetParam(MethodObj, "sPlanNo", parent.medt_PlanNo.Value);
    gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB040Task", "selectRecvClprSameYn");
	MethodObj.setId("selectRecvClprSameYn");
	//MethodObj.setParam("sAgentCode", gUserID);
    if (cmb_sCmMetd.getValue() == "05") {
        //자동이체
        sDpsrCd = Div.Ds_LTIFA03.getCellData(0, "sDpsrCd");
    } else if (cmb_sCmMetd.getValue() == "03") {
        //카드입금
        sDpsrCd = Div1.Ds_LTIFA03.getCellData(0, "sDpsrCd");
    }
    MethodObj.setParam("sDpsrCd", sDpsrCd);
    MethodObj.setParam("sPlanNo", parent.medt_PlanNo.getValue());	
	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//*/LTIDA00");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_RecvClprSameYn");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

    if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return false;
    } else {
        //wconv.trace("sRecvClprSameYn : " + DsWs_RecvClprSameYn.getCellData(0, "sRecvClprSameYn"));
        if (DsWs_RecvClprSameYn.getRowCount() > 0 && DsWs_RecvClprSameYn.getCellData(0, "sRecvClprSameYn") == "Y") {
            return false;
        } else {
            return true;
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_selectPymAcctRgstYn
// 내용        : 환급금계좌등록여부확인
//---------------------------------------------------------------------------
scwin.lfn_selectPymAcctRgstYn = function() {
    wconv.clear(DsWs_PymAcctRgstYn);
    //DsWs_Warning.DataObject = "DOBJ_select";
    var sDpsrCd;
    var sCmMetd;
    /*
    SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.ssr.pln.task.DBB040Task", "selectPymAcctRgstYn", "", "");
    gfn_SetParam(MethodObj, "sPlanNo", parent.medt_PlanNo.Value);
    gfn_SetParam(MethodObj, "sAcctFlag", "3");
    gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB040Task", "selectPymAcctRgstYn");
	MethodObj.setId("selectPymAcctRgstYn");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam("sPlanNo", parent.medt_PlanNo.getValue());
    MethodObj.setParam("sAcctFlag", "3");

	MethodObj.setUrl("/contract/CBSC015MController.do");
	MethodObj.setXPath("//*/LTIFA33");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIFA33"), "DsWs_PymAcctRgstYn");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");

    if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return false;
    } else {
        //wconv.trace("sPymAcctRgstYn : " + DsWs_PymAcctRgstYn.getCellData(0, "sPymAcctRgstYn"));
        if (DsWs_PymAcctRgstYn.getRowCount() > 0 && DsWs_PymAcctRgstYn.getCellData(0, "sPymAcctRgstYn") == "Y") {
            return true;
        } else {
            return false;
        }
    }
};

