    var resultArr = edt_sB180PopValue.getValue().split("^");
    sFlag = resultArr[2];
};

//---------------------------------------------------------------------------
// function 명 : btn_PymAcct_OnClick
// 내용        : 지급계좌 등록
//---------------------------------------------------------------------------
scwin.btn_PymAcct_OnClick = function(obj) {
    var promise = Promise.resolve();
    promise = promise.then(function(rtnVal) {
	    var indata = {};
	    indata.sPlanNo=parent.Ds_LTIDA00.getCellData(0, "sPlanNo");
	    indata.sCrtorCd=parent.Ds_LTIDA03.getCellData(0, "sCrtorCd");
	    indata.sCrtorName=parent.Ds_LTIDA03.getCellData(0, "sCrtorName");
	    indata.sInrpsCd=parent.Ds_Pibo.getCellData(0, "sInrpsCd");
	    indata.sInrpsName=parent.Ds_Pibo.getCellData(0, "sInrpsName");
	    indata.sPeriMwayWdraYn=parent.Ds_LTIDA00.getCellData(0, "sPeriMwayWdraYn");
	    indata.sCmMetd=cmb_sCmMetd.getValue();
	    indata.sGdCd=DsWs_LTIDA00.getCellData(0, "sGdCd");
	    if (cmb_sCmMetd.getValue() == "05" && Div.cmb_sCrtorRelnFlag.getValue() == "10") {
	        indata.sBankCd=Div.cmb_BankCd.getValue();
	        indata.sAcctNo=Div.edt_sAcctNo.getValue();
	    } else {
	        indata.sBankCd="";
	        indata.sAcctNo="";
	    }
	    var popInfo = {
       			title : "환급금계좌정보",
				width : 774,
				height : 175,
				indata : indata
	    };
        return wprom.popupOpen("/ui/cbsc/CBSC133P.xml", popInfo);//arg, 550, 850, "TaskBar=true Resize=true"); /*wprom.popupOpen(가이드참조)*/
    });
    return promise;
};

//Div_CmMetd05 load 완료시 호출
scwin.Div_CmMetd05_oniframeload = function() {
	bLoadDivCmMetd05 = true;
	Div = Div_CmMetd05.getWindow();
	//scwin.Div_OnLoadCompleted();
	Div_CmMetd03.setSrc("/ui/cbsc/CBSC053S.xml");
};

//Div_CmMetd03 load 완료시 호출
scwin.Div_CmMetd03_oniframeload = function() {
	bLoadDivCmMetd03 = true;
	Div1 = Div_CmMetd03.getWindow();
	scwin.Div_OnLoadCompleted();
};

scwin.Div_OnLoadCompleted = function() {
	if (bLoadDivCmMetd05 && bLoadDivCmMetd03) {
		if (parent.sYoyakTab == "") {
        	scwin.cmb_sCmMetd_OnChanged("", cmb_sCmMetd.getValue(), cmb_sCmMetd.getValue(), 0, 0);
        } else if (parent.Ds_LTIDA00.getCellData(0, "sCmMetd") == "") {
            scwin.cmb_sCmMetd_OnChanged("", cmb_sCmMetd.getValue(), cmb_sCmMetd.getValue(), 0, 0);
        } else {
            switch (cmb_sCmMetd.getValue()) {
                case "05":
                    //자동이체
                    Div.Ds_Args.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
                    // 설계번호 (청약 필수, 변경 옵션)
                    Div.Ds_LTIFA03.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
                    // 설계번호 (청약 필수, 변경 옵션)
                    break;
                case "03":
                    //카드입금
                    Div1.Ds_Args.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
                    // 설계번호 (청약 필수, 변경 옵션)
                    Div1.Ds_LTIFA03.setCellData(0, "sPlanNo", parent.Ds_LTIDA00.getCellData(0, "sPlanNo"));
                    // 설계번호 (청약 필수, 변경 옵션)
                    break;
                default:
                    break;
            }
        }
    }
};

/*-----------------------------------
// function 명 : scwin.lfn_commCodeSetting
// 내용        : 공통 코드호출
-----------------------------------*/
scwin.lfn_commCodeSetting = function(strCommCode) {
	//json 데이터를 가져와서 DataSet에 바인딩 해준다
    var json = gfn_getJsonCommonCode(strCommCode);
    if(json != null){
    	//alert(JSON.stringify(json));
    	DsWs_CmCd.setJSON(json, false);
    }
};
