scwin.init = function() {

	var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();



pro = pro.then(function(rv) {
    return util.winTopShowModalPromise();
});



pro = pro.then(function(rv) {

     //init 부분 함수 입력하는곳
	var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{4}[0-9\s]{7}$/];
	var maxLength  = [12];
	var chkIndex   = 0;
	msk_sCrNo_Event = new wMaskEditObj(msk_sCrNo, maskRegExp, maxLength, chkIndex);
	msk_sCrNo_Event.setIndex(0);
 	msk_sCrNo.setPlaceholder( "_-____-_______" );
    //-----서버 시간 받기
//    transaction("getServerTime", "NewGen::getServerTime.jsp", "", "gDs_ServerTime=output", "", "tr_callback");

    // 버튼 처리    (조회, 입력,  수정,  삭제, 결재,   취소,  인쇄,  엑셀,  닫기)
//    gfn_ButtonEnable(true, false, false, false, false, false, false, false, true);

	gfn_btnEnable( false, false, false, false, false, false, false, true);
    msk_sCrNo.setValue(gds.gDs_LinkArg.getCellData(0, "sCrNo"));

    if (wconv.length(msk_sCrNo.getValue()) == 12) {
        //sAutoInqrYn = "Y"; 
        scwin.lfn_Search();
    }

	return;
    //init 함수를 종료하고자 할때 사용한다.
//    return Promise.reject("오류관련처리");
});


pro = pro.then(function(rv) {
    util.winTopHideModal();
});



//pro = pro.catch(function(rj) {
//    util.winTopHideModal();
//    alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
//});



return pro;
};

scwin.tr_callback = function(ServiceId, errorCd, errorMsg) {
    if (ServiceId == "getServerTime") {
        if (errorCd != 0) {
            wprom.alert("errorCd:::[" + errorCd + "] errorMsg:::[" + errorMsg + "]");
        } else {
            gsSysDate = gds.gDs_ServerTime.getCellData(0, "sToday").substr(0, 8);
        }
    }
};


