scwin.getDptCrData = function() {
    // 입력값 chk

    if (!scwin.lfn_inputChk()) {
        return false;
    }
    wconv.clear(warning);
    wconv.clear(Ds_refInfo);
    util.insertColumn(Ds_refInfo, [
        [
            "sGdCd",
            "sGdCd",
            "text"
        ]
    ]);
    util.insertColumn(Ds_refInfo, [
        [
            "sGdCdName",
            "sGdCdName",
            "text"
        ]
    ]);
    util.appendRow(Ds_refInfo);

    wconv.clear(Ds_LTIEA00);
    wconv.clear(DsWs_LTIEA00);
    wconv.clear(Ds_PremCalcSpec);
    wconv.clear(DsWs_PremCalcSpec);
    wconv.clear(DsWs_PaymPremSumInfo);
//    SOBJ_Search.WSDL = gds.gWsdlBasis;
//    warning.DataObject = DOBJ_DataSearch.ID;
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.psv.ens.task.EAD510Task", "getCtnPremInfo", "", "");
    //dpt.rec.task.FAF020Task psv.ens.task.EAD510Task															
//    gfn_SetParam(MethodObj, "sCrNo", msk_sCrNo.getValue());
//    gfn_SetParam(MethodObj, "sScrnId", "F020");
//    gfn_CallService(SOBJ_Search, DOBJ_DataSearch, MethodObj);

//조회데이터
var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD510Task", "getCtnPremInfo", pageSize, pageNo);

	MethodObj.setId("getCtnPremInfo");
	MethodObj.setParam( "sCrNo", msk_sCrNo.getValue()); 
	MethodObj.setParam( "sScrnId", "F020");

	MethodObj.setUrl("/contract/CBSC020MController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/LTIEA00");//(dsWS의 XPath)
	MethodObj.addXPath("//*/PremCalcSpec");//(dsWS의 XPath)
	MethodObj.addXPath("//*/hashtable/data/PaymPremSumInfo");//(dsWS의 XPath)
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LTIEA00");
	util.jSonDsCreate(_tranSync.getDsJson("PremCalcSpec"), "Ds_PremCalcSpec");
	util.jSonDsCreate(_tranSync.getDsJson("PaymPremSumInfo"), "DsWs_PaymPremSumInfo");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_DataWarning");

	util.setCompBindRefresh("DsWs_LTIEA00");//바인딩을 해줌
	util.setCompBindRefresh("Ds_PremCalcSpec");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_PaymPremSumInfo");//바인딩을 해줌
	util.setCompBindRefresh("warning");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_Warning");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_DataWarning");//바인딩을 해줌
	util.setRowPosition(DsWs_LTIEA00, 0);

    // 오류처리
    if (warning.getRowCount() != 0) {
        //alert(warning.GetColumn(0, "msg"));
        scwin._getDptCrData2();
        return false;
    }
    scwin._getDptCrData();

    return true;
};

//---------------------------------------------------------------------------
// function 명 : _getDptCrData
// 내용        : 계약조회후 상태에 따른 화면 컨트롤
//---------------------------------------------------------------------------
scwin._getDptCrData = function() {
    // 출력 data move
//    wconv.copy(Ds_LTIEA00, "DsWs_LTIEA00");
    util.copyM(Ds_LTIEA00, DsWs_LTIEA00.getAllJSON());
	util.setCompBindRefresh("Ds_LTIEA00");//바인딩을 해줌
	util.setRowPosition(Ds_LTIEA00, 0);
//    wconv.copy(Ds_PremCalcSpec, "DsWs_PremCalcSpec");
    util.copyM(Ds_PremCalcSpec, DsWs_PremCalcSpec.getAllJSON());
	util.setCompBindRefresh("Ds_PremCalcSpec");//바인딩을 해줌
	util.setRowPosition(Ds_PremCalcSpec, 0);
    // 화면 DataSet에 Move(조회부분)	
    Ds_refInfo.setCellData(0, "sGdCd", Ds_LTIEA00.getCellData(0, "sGdCd"));
    Ds_refInfo.setCellData(0, "sGdCdName", Ds_LTIEA00.getCellData(0, "sGdCdName"));
    msk_sCrtorName1.setValue(gfn_changeMask(Ds_LTIEA00.getCellData(0, "sCrtorName"), "4"));
    msk_sCrtorCd1.setValue(gfn_changeMask(Ds_LTIEA00.getCellData(0, "sCrtorCd"), "1"));
};

