//---------------------------------------------------------------------------
// function 명 : lfn_inputChk
// 내용        : 입력값체크
//---------------------------------------------------------------------------
scwin.lfn_inputChk = function(obj) {
    // 계약번호
    if (msk_sCrNo.getValue() == "") {
        wprom.alert("계약번호를 입력하세요.");
        return false;
    } else if (wconv.length(msk_sCrNo.getValue()) != 12) {
        wprom.alert("계약번호를 잘못 입력하였습니다. 확인해주십시요");
        return false;
    }
//    msk_sCrNo.setValue(toUpperCase(msk_sCrNo.getValue()));
	msk_sCrNo.setValue(msk_sCrNo.getValue().toUpperCase());
    return true;
};

//---------------------------------------------------------------------------
// function 명 : msk_sCrNo_OnCharChanged
// 내용        : 계약번호 변경시
//---------------------------------------------------------------------------
scwin.msk_sCrNo_OnCharChanged = function(obj, strPreText, strPostText) {
    //sAutoInqrYn = "Y";
    wconv.clear(Ds_LTIEA00);
    wconv.clear(Ds_LTIEA01);
    wconv.clear(Ds_LTIEA02);
    wconv.clear(Ds_PremCalcSpec);
    msk_sCrtorCd1.setValue("");
    msk_sCrtorName1.setValue("");
    wconv.clear(Ds_refInfo);
    util.insertColumn(Ds_refInfo, [
        [
            "sGdCd",
            "sGdCd",
            "text"
        ]
    ]);
    util.insertColumn(Ds_refInfo, [
        [
            "sGdCdName",
            "sGdCdName",
            "text"
        ]
    ]);
    util.appendRow(Ds_refInfo);
};

scwin.lfn_SearchData = function() {

    Ds_LTIEA01.removeAll();
//    SOBJ_Get.WSDL = gds.gWsdlBasis;
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Get, "com.inswave.longterm.psv.ens.task.EAD520Task", "selectRenwlTrtyInfo", "", "");
//    gfn_SetParam(MethodObj, "sCrNo", msk_sCrNo.getValue());
//    gfn_CallService(SOBJ_Get, DOBJ_Get, MethodObj);


	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD520Task", "selectRenwlTrtyInfo", pageSize, pageNo);
	MethodObj.setId("selectRenwlTrtyInfo");
	MethodObj.setParam( "sCrNo",  msk_sCrNo.getValue()); 

	MethodObj.setUrl("/contract/CBSC020MController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/hashtable/data/vector/data/LTIEA01");//(dsWS의 XPath)
	MethodObj.addXPath("//*/hashtable/data/vector/data/LTIEA02");//(dsWS의 XPath)	
	MethodObj.addXPath("//*/WARNING"); 
	MethodObj.sync();
	MethodObj.send();

	util.jSonDsCreate(_tranSync.getDsJson("LTIEA01"), "DsWs_LTIEA01");
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA01"), "Ds_LTIEA01");
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA02"), "DsWs_LTIEA02");

	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");

	util.setCompBindRefresh("DsWs_LTIEA01");//바인딩을 해줌
	util.setRowPosition(DsWs_LTIEA01, 0);

//    wconv.copy(Ds_LTIEA01, "DsWs_LTIEA01");
	//util.copyM(Ds_LTIEA01, DsWs_LTIEA01.getAllJSON());
	util.setCompBindRefresh("Ds_LTIEA01");//바인딩을 해줌
	util.setRowPosition(Ds_LTIEA01, 0);

    if (DsWs_DataWarning.getRowCount() != 0) {
        wprom.alert(DsWs_DataWarning.getCellData(0, "msg"));
        return;
    }

    if (Ds_LTIEA01.getRowCount() == 0) {
        // 조회 값이 없으면
        wconv.clear(DsWs_LTIEA01); //리스트 내용삭제
        Ds_LTIEA01.removeAll();
        wprom.alert("조회결과가 없습니다.");
        return;
    }
};

