var pageSize = "20";
var pageCount = "20";
var sDate;
//현재일
var sTime;
//현재시간
var slevel;

var msk_sInqrKeyCd_Event;

//---------------------------------------------------------------------------
// function 명 : lfn_OnLoadCompleted
// 내용        : 화면초기화
//---------------------------------------------------------------------------
scwin.init = function() { //완료
    //현재일 조회
    scwin.lfn_crntDate();

    //Mask Edit 정규식 입력
	//"######-#ZZZZZZ" , "A-####-#######", "@@@@@@@"
	var maskRegExp = [/^[0-9\s]{7}[A-Z0-9\s]{6}$/, /^[A-Z\s]{1}[0-9\s]{11}$/, /^[^ㄱ-ㅎ^ㅏ-ㅣ^가-힣]{7}$/ ];
	var maxLength  = [13, 12, 7];
	var chkIndex   = 0;
	msk_sInqrKeyCd_Event = new wMaskEditObj(msk_sInqrKeyCd, maskRegExp, maxLength, chkIndex);

    // 버튼 처리    (조회, 입력,  수정,  삭제, 결재,   취소,  인쇄,  엑셀,  닫기)
    // 2014.03.27 N1403-00288 대리점지원시스템 프로그램 수정 요청
    gfn_btnEnable(false, false, false, false, false, false, true, true);
    btn_ObjtSerch.setDisabled(true);

	//조회구분값변경 세팅
	scwin.cmb_sInqrFlagCd_OnChanged();

    /** 링크 콤보 데이터를 가저온다. 
     반드시 링크 데이터셋 ID = Ds_LinkList, 링크콤보 ID = cmb_linkMenu !!!!!
     링크 버튼 ID = btn_linkMenu !!!!!*/
    //gfn_GetLinkList("F010");
};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회
//---------------------------------------------------------------------------
//비동기호출 
scwin.lfn_Search_Async = function() {
	util.eventAsyncCall(this, scwin.lfn_Search, arguments);
};

scwin.lfn_Search = function() { //완료
    // 입력값 체크
    if (scwin.lfn_InptChek() == false) {
        return false;
    }
    wconv.clear(Ds_LTIFA01CNT);
    wconv.clear(DsWs_LTIFA01CNT);
    wconv.clear(Ds_search);
    wconv.clear(DsWs_search);
    scwin.lfn_SearchData(1);
    if (Ds_search.getRowCount() > 0) {
    	gfn_MakePagingData(Ds_Page, Ds_LTIFA01CNT.getCellData(0, "nCount"), pageSize, pageCount);
		Cmb_Page.setValue("1");
    } else {}
};

//---------------------------------------------------------------------------
// function 명 : lfn_InptChek
// 내용        : 입력값 체크
//---------------------------------------------------------------------------
scwin.lfn_InptChek = function() {
    var sInqrFlagCd = cmb_sInqrFlagCd.getValue(); //조회구분 01계약자 02피보험자 03계약번호 04상담원
    var sInqrFlagValue = msk_sInqrKeyCd; // 조회코드
    var sCrStatCd = cmb_sCrStat; // 계약상태코드
    var sProgStat = cmb_sProgStat; // 처리구분
    if (sInqrFlagCd == "01") {
        if (msk_sInqrKeyCd.getValue() == null || msk_sInqrKeyCd.getValue() == "") {
            msk_sInqrKeyCd.focus();
            wprom.alertF("계약자코드가 입력되지 않았습니다.",{},msk_sInqrKeyCd);
            return false;
        }
    } else if (sInqrFlagCd == "02") {
        if (msk_sInqrKeyCd.getValue() == null || msk_sInqrKeyCd.getValue() == "") {
            msk_sInqrKeyCd.focus();
            wprom.alertF("피보험자코드가 입력되지 않았습니다.",{},msk_sInqrKeyCd);
            return false;
        }
    } else if (sInqrFlagCd == "03") {
        if (msk_sInqrKeyCd.getValue() == null || msk_sInqrKeyCd.getValue() == "") {
            msk_sInqrKeyCd.focus();
            wprom.alertF("계약번호가 입력되지 않았습니다.",{},msk_sInqrKeyCd);
            return false;
        } else if (wconv.length(msk_sInqrKeyCd.getValue()) != 12) {
            msk_sInqrKeyCd.focus();
            wprom.alertF("계약번호가 잘 못 입력되었습니다.",{},msk_sInqrKeyCd);
            return false;
        }
    } else if (sInqrFlagCd == "04") {
        if (msk_sInqrKeyCd.getValue() == null || msk_sInqrKeyCd.getValue() == "") {
            msk_sInqrKeyCd.focus();
            wprom.alertF("상담원코드가 입력되지 않았습니다.",{},msk_sInqrKeyCd);
            return false;
        } else if (wconv.length(msk_sInqrKeyCd.getValue()) != 7) {
            msk_sInqrKeyCd.focus();
            wprom.alertF("상담원코드가 잘 못 입력되었습니다.",{},msk_sInqrKeyCd);
            return false;
        }
    } else if (sInqrFlagCd == "05") {
        if (msk_sInqrKeyCd.getValue() == null || msk_sInqrKeyCd.getValue() == "") {
            msk_sInqrKeyCd.focus();
            wprom.alertF("사용인코드가 입력되지 않았습니다.",{},msk_sInqrKeyCd);
            return false;
        } else if (wconv.length(msk_sInqrKeyCd.getValue()) != 7) {
            msk_sInqrKeyCd.focus();
            wprom.alertF("사용인코드가 잘 못 입력되었습니다.",{},msk_sInqrKeyCd);
            return false;
        }
    }
    return true;
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchData
// 내용        : 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchData = function(pageNo) {
    // 조회구분에 따른 입력값 형식 변경
    var inqrFlagCd = cmb_sInqrFlagCd.getValue();
    // 조회구분 키값
    var inqrFlagValue = msk_sInqrKeyCd.getValue();
    var sInqrKeyValue = edt_sInqrKeyValue.getValue();
    if (wconv.length(sInqrKeyValue) == 0 && inqrFlagCd == "01") {
        if (isBizRegNo(inqrFlagValue)) {
            //계약자명조회
            scwin.lfn_SearchCstName(2, inqrFlagValue, edt_sInqrKeyValue, "계약자");
        } else {
            scwin.lfn_SearchCstName(1, inqrFlagValue, edt_sInqrKeyValue, "계약자");
        }
    } else if (wconv.length(sInqrKeyValue) == 0 && inqrFlagCd == "02") {
        // 피보험자명 조회
        scwin.lfn_SearchCstName(1, inqrFlagValue, edt_sInqrKeyValue, "피보험자");
    } else if (wconv.length(sInqrKeyValue) == 0 && inqrFlagCd == "03") {
        //상품명 조회
        scwin.lfn_SearchPrd();
    } else if (wconv.length(sInqrKeyValue) == 0 && inqrFlagCd == "04") {
        //사원명
        scwin.lfn_SearchEmpName(inqrFlagValue, edt_sInqrKeyValue);
    }

    if (inqrFlagCd == "03" && (gfn_chekCrInqrAuth("DwWs_chekCrInqrAuth", "2", inqrFlagValue) == false)) {
        wprom.alert("조회권한이 없습니다.");
        return false;
    } else if (((inqrFlagCd == "04" || inqrFlagCd == "05")) && (gfn_chekDoerInqrAuth("DwWs_chekCrInqrAuth", inqrFlagValue) == false)) {
        wprom.alert("조회권한이 없습니다.");
        return false;
    }


    var transObj = new callService("com.inswave.longterm.com.com.task.AXR930Task", "getAgentList", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("getAgentList");
	transObj.setParam("sAgentCode", util.getGdsValue("gUserID")); //조직구조
	transObj.setXPath("//*/ADMAC03");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(Ds_TEAM);
	util.jSonDsCreate(_tranSync.getDsJson("ADMAC03"), "DsWs_TEAM");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAC03"), "Ds_TEAM");
    var sTeamCd = DsWs_TEAM.getCellData(0, "sTeamCode");


    var transObj = new callService("com.inswave.longterm.dpt.com.task.FAF000Task", "getEmpName", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("getEmpName");
	transObj.setParam("sEmpNo", util.getGdsValue("gUserID")); //조직구조
	transObj.setXPath("//*/ADMAB01");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(Ds_JobCode);
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_JobCode");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "Ds_JobCode");
    var sJobCode = DsWs_JobCode.getCellData(0, "sJobCode");


    //======================================================================================================================
    //직영 TM실 실장들 개별 조건
    var transObj = new callService("com.inswave.longterm.pay.pdg.task.GAG021Task", "selectForAuthCheck", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("selectForAuthCheck");
	transObj.setParam("sUserID", util.getGdsValue("gUserID")); //조직구조
	transObj.setParam("nAuthType", "1");
	transObj.setParam("nAuthID", "200196");
	transObj.setXPath("//*/GAG021");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

    wconv.clear(Ds_TMAuth);
    util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_TMAuth");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "Ds_TMAuth");
    wconv.copy(Ds_TMAuth, "DsWs_TMAuth");
    var sAuthFlag = DsWs_TMAuth.getCellData(0, "sAuthFlag");
    //======================================================================================================================



    var transObj = new callService("com.inswave.longterm.dpt.rec.task.FAF010Task", "selCtnPremRecvObjt", pageSize, pageNo);
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("selectForAuthCheck");
	transObj.setParam("sInqrFlagCd", cmb_sInqrFlagCd.getValue()); //조회구분 00계약자주민번호, 01계약자사업자번호  02피보험자 03계약번호 04상담원
	transObj.setParam("sInqrFlagValue", msk_sInqrKeyCd.getValue()); //조회구분 키값
	transObj.setParam("sCrStatCd", cmb_sCrStat.getValue()); //계약상태[99 전체, 00정상, 10 실효]
	transObj.setParam("sProgStat", cmb_sProgStat.getValue()); //진행상태[00 전체, 01 수납대기, 02 청구중, (삭제)03 완료]
	transObj.setParam("sRecvObjtYearMnth", wconv.mid(sDate, 0, 6)); //대상년월   
	transObj.setParam("slevel", slevel);
	transObj.setParam("sDuty", util.getGdsValue("gUserDuty"));

	if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "34009") && (sAuthFlag == "Y")) {
		transObj.setParam("sFrstDoerTeamCd", sTeamCd);
    }
	//설계자 여부 (48:DPM, 49:DP, 50:재정전문위원)
    if (util.getGdsValue("gUserDuty") == "48" || (util.getGdsValue("gUserDuty") == "49" && sAuthFlag == "N") || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64") {
    	transObj.setParam("sKeyDoer", util.getGdsValue("gUserID"));
    }
    if (util.getGdsValue("gUserDuty") == "66" && inqrFlagCd == "06") {
    	transObj.setParam("sFrstDoerTeamCd", sTeamCd);
    } else if (util.getGdsValue("gUserDuty") == "66") {
    	transObj.setParam("sUserID", util.getGdsValue("gUserID"));
    	transObj.setParam("sEmpNo", msk_sInqrKeyCd.getValue());
    }
	transObj.setXPath("//*/vector/data/LTIFA01");
	transObj.addXPath("//*/hashtable/data/LTIFA01CNT");
	transObj.addXPath("//*/ADMAB01");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	util.interfaceKey(_tranSync.result.serviceDs);

    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");
    if (warning.getRowCount() != 0) {
        // 오류처리
        wprom.alert(warning.getCellData(0, "msg"));
        return;
    } else {
    	util.jSonDsCreate(_tranSync.getDsJson("LTIFA01CNT"), "DsWs_LTIFA01CNT");
    	util.jSonDsCreate(_tranSync.getDsJson("LTIFA01CNT"), "Ds_LTIFA01CNT");

    	wconv.clear(DsWs_search);
		DsWs_search.setJSON(_tranSync.getDsJson("LTIFA01"));
    	util.setRowPosition(DsWs_search, 0);

    	wconv.clear(Ds_search);
		Ds_search.setJSON(_tranSync.getDsJson("LTIFA01"));
    	util.setRowPosition(Ds_search, 0);

    	gfn_LogCreditInfoVt(Ds_search, "", "S", "sCrtorCd");
        gfn_LogCreditInfoVt(Ds_search, "", "S", "sInrpsCd");
    }
};

