scwin.cm_btnExDown = function() {
	scwin.lfn_Exel();
}
scwin.lfn_Exel = function() {

	var inqrFlagCd = cmb_sInqrFlagCd.getValue();

	var transObj = new callService("com.inswave.longterm.com.com.task.AXR930Task", "getAgentList", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("getAgentList");
	transObj.setParam("sAgentCode", util.getGdsValue("gUserID")); //조직구조
	transObj.setXPath("//*/ADMAC03");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(Ds_TEAM);
	util.jSonDsCreate(_tranSync.getDsJson("ADMAC03"), "DsWs_TEAM");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAC03"), "Ds_TEAM");
    var sTeamCd = DsWs_TEAM.getCellData(0, "sTeamCode");


    var transObj = new callService("com.inswave.longterm.dpt.com.task.FAF000Task", "getEmpName", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("getEmpName");
	transObj.setParam("sEmpNo", util.getGdsValue("gUserID")); //조직구조
	transObj.setXPath("//*/ADMAB01");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(Ds_JobCode);
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_JobCode");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "Ds_JobCode");
    var sJobCode = DsWs_JobCode.getCellData(0, "sJobCode");

	//======================================================================================================================
    //직영 TM실 실장들 개별 조건
	var transObj = new callService("com.inswave.longterm.pay.pdg.task.GAG021Task", "selectForAuthCheck", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("selectForAuthCheck");
	transObj.setParam("sUserID", util.getGdsValue("gUserID")); //조직구조
	transObj.setParam("nAuthType", "1");
	transObj.setParam("nAuthID", "200196");
	transObj.setXPath("//*/GAG021");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

    wconv.clear(Ds_TMAuth);
    util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_TMAuth");
	util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "Ds_TMAuth");
    wconv.copy(Ds_TMAuth, "DsWs_TMAuth");
    var sAuthFlag = DsWs_TMAuth.getCellData(0, "sAuthFlag");
	//======================================================================================================================

	var nTotalExcelCnt = 0;
	var nTotalPage = wconv.toNumber(Edit_TotalPage.getValue());

	for (var i = 1; i <= nTotalPage; i++) {
		var transObj = new callService("com.inswave.longterm.dpt.rec.task.FAF010Task", "selCtnPremRecvObjt", pageSize, i);
		transObj.setUrl("/contract/CBSC025MController.do");
		transObj.setId("selectForAuthCheck");
		transObj.setParam("sInqrFlagCd", cmb_sInqrFlagCd.getValue()); //조회구분 00계약자주민번호, 01계약자사업자번호  02피보험자 03계약번호 04상담원
		transObj.setParam("sInqrFlagValue", msk_sInqrKeyCd.getValue()); //조회구분 키값
		transObj.setParam("sCrStatCd", cmb_sCrStat.getValue()); //계약상태[99 전체, 00정상, 10 실효]
		transObj.setParam("sProgStat", cmb_sProgStat.getValue()); //진행상태[00 전체, 01 수납대기, 02 청구중, (삭제)03 완료]
		transObj.setParam("sRecvObjtYearMnth", wconv.mid(sDate, 0, 6)); //대상년월   
		transObj.setParam("slevel", slevel);
		transObj.setParam("sDuty", util.getGdsValue("gUserDuty"));

		if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "34009") && (sAuthFlag == "Y")) {
			transObj.setParam("sFrstDoerTeamCd", sTeamCd);
	    }
		//설계자 여부 (48:DPM, 49:DP, 50:재정전문위원)
	    if (util.getGdsValue("gUserDuty") == "48" || (util.getGdsValue("gUserDuty") == "49" && sAuthFlag == "N") || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64") {
	    	transObj.setParam("sKeyDoer", util.getGdsValue("gUserID"));
	    }
	    if (util.getGdsValue("gUserDuty") == "66" && inqrFlagCd == "06") {
	    	transObj.setParam("sFrstDoerTeamCd", sTeamCd);
	    } else if (util.getGdsValue("gUserDuty") == "66") {
	    	transObj.setParam("sUserID", util.getGdsValue("gUserID"));
	    	transObj.setParam("sEmpNo", msk_sInqrKeyCd.getValue());
	    }

	    transObj.setXPath("//hashtable/data[@hashkey='LTIFA01']/vector/data/LTIFA01");
		transObj.addXPath("//*/WARNING");
		//transObj.callBack("scwin.subCallBackfunction");
		transObj.sync();
		transObj.send();
		util.interfaceKey(_tranSync.result.serviceDs);

	    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_ExcelWarning");
	    //에러처리
        if (DsWs_ExcelWarning.getRowCount() != 0) {
            wprom.alert(DsWs_ExcelWarning.getCellData(0, "msg"));
            return;
        }

        if (i == 1) {
	        wconv.clear(DsWs_ExcelDown);
			DsWs_ExcelDown.setJSON(_tranSync.getDsJson("LTIFA01"));
	    	util.setRowPosition(DsWs_ExcelDown, 0);

	    	wconv.clear(Ds_ExcelLoad);
			Ds_ExcelLoad.setJSON(_tranSync.getDsJson("LTIFA01"));
	    	util.setRowPosition(Ds_ExcelLoad, 0);
            //wconv.clear(Ds_ExcelLoad);
            //wconv.copy(Ds_ExcelLoad, "DsWs_ExcelDown");
            nTotalExcelCnt = Ds_ExcelLoad.getRowCount();
        } else {
        	DsWs_ExcelDown.setJSON(_tranSync.getDsJson("LTIFA01"), true);
        	Ds_ExcelLoad.setJSON(_tranSync.getDsJson("LTIFA01"), true);
        }
	}
	var option={"fileName": "대리점지원시스템계속보험료입금대상조회"};
	var infoArr = {};
	common.downLoadExcel("Grd_excel", option, infoArr);
	//Grd_excel.SaveExcel(FileDialog0.FilePath + "\\" + FileDialog0.FileName, "대리점지원시스템 계속보험료 입금대상조회"); //trace("Grd_excel--->" + Grd_excel.SaveXML());
};

//---------------------------------------------------------------------------
// function 명 : btn_ObjtSerch_OnClick
