scwin.btn_ObjtSerch_OnClick = function(obj) {
	var sInqrFlagCd = cmb_sInqrFlagCd.getValue();
    var sArg = "";
    var rst = "";

	/* 조회구분에 따른 조회코드명조회*/
    if (sInqrFlagCd == "" || sInqrFlagCd == null) {
        wprom.alert("조회구분을 선택하세요.");
        return false;
    } else if (sInqrFlagCd == "01" || sInqrFlagCd == "02") {
    	wprom.alert("추후개발예정");
    	return false;
    	/*
