            //검색단어
            return wprom.promise.popupOpen("PROJECT_PATH_TEMP/cst_cin/CBSO004P.xml", sArg, 640, 543);
        });
        promise = promise.then(function(rst) {
            if (wconv.length(rst) != 0 && rst != 0) {
                var cusInfo = wconv.split(rst, "^");
                if (isBizRegNo(msk_obj.getValue())) {
                    msk_obj.setDisplayFormat("######-#ZZZZZZ");
                } else {
                    msk_obj.setDisplayFormat("######-#ZZZZZZ");
                }
                msk_obj.setValue(cusInfo[0]);
                edt_obj.setValue(cusInfo[1]);
            }
        });
        */
    } else if (sInqrFlagCd == "03") {
    	var msk_obj = msk_sInqrKeyCd;
        var edt_obj = edt_sInqrKeyValue;
        var promise = Promise.resolve();
        promise = promise.then(function(rtnVal) {
            //계약번호
            //return wprom.promise.popupOpen("PROJECT_PATH_TEMP/psv_ens/CBSC031P.xml", 704, 552);
