            if (wconv.length(result) != 0) {
                var groupRetArr = result.split("^");
                msk_obj.setValue(groupRetArr[0]); //계약번호
                edt_obj.setValue(groupRetArr[1]); //상품명
                //msk_XXXXXXXXXX.Value = groupRetArr [0];//계약번호
                //msk_XXXXXXXXXX.Value = groupRetArr [1];//상품명
                //msk_XXXXXXXXXX.Value = groupRetArr [2];//계약자명
                //msk_XXXXXXXXXX.Value = groupRetArr [3];//피보험자명
                //msk_XXXXXXXXXX.Value = groupRetArr [4];//계약상태
            }
        });
	} else if (sInqrFlagCd == "04") {
		var msk_obj = msk_sInqrKeyCd;
        var edt_obj = edt_sInqrKeyValue;
		var promise = Promise.resolve();
	    promise = promise.then(function(rtnVal) {
	        //상담원
            //return wprom.promise.popupOpen("PROJECT_PATH_TEMP/popup/CBSO015P.xml", sArg, 640, 336);
	        //var indata  = {"id" : "ddd", "name" : "ddd"};
			var popInfo = {
				"width"     : 654,
				"height"    : 435,
				//"top"       : 100,
				//"left"      : 100,
				"indata"    : "", //indata
