                msk_obj.setValue(arrRst[0]);
                edt_obj.setValue(arrRst[1]);
	        }
	    });
	}else if(sInqrFlagCd == "05"){		
		//var msk_obj = msk_sInqrKeyCd.getValue();
		//var edt_obj = edt_sInqrKeyValue.getValue();
		var promise = Promise.resolve();
		promise = promise.then(function(rtnVal) {
			var popInfo = {
					"width"     : 657,
					"height"    : 437,  
					title          : "사원찾기"
			};
			//상담원
			//rst = Dialog("popup::R960_Pop.xml", sArg, 640, 336);
			return wprom.popupOpen("/ui/cbso/CBSO015P.xml",popInfo);
		});
