		transObj.setXPath("//*/GAG021");
		transObj.addXPath("//*/WARNING");
		//transObj.callBack("scwin.subCallBackfunction");
		transObj.sync();
		transObj.send();
		//util.interfaceKey(_tranSync.result.serviceDs);

	    wconv.clear(Ds_TMAuth);
	    util.jSonDsCreate(_tranSync.getDsJson("GAG021"), "DsWs_TMAuth");
		util.jSonDsCreate(_tranSync.getDsJson("GAG021"), "Ds_TMAuth");
	    var sAuthFlag = DsWs_TMAuth.getCellData(0, "sAuthFlag");

        var promise = Promise.resolve();	  
	    promise = promise.then(function(result) {
	    	//return wprom.promise.popupOpen("PROJECT_PATH_TEMP/com_pop/0120.xml", "", 594, 321);
		    var sArg = {};
			var popInfo = {
					"width"     : 730,
					"height"    : 500, 
				//	"indata"    : sArg,
					"callback"  : "",
					"title"     : "조직 찾기 [0120]",
			};
			return wprom.popupOpen("/ui/cbso/CBSO020P.xml", popInfo);		// 조직 찾기 팝업 처리
	    });
	    promise = promise.then(function(result) { 
			if(result.length > 0 ) {
				var arrRst = result.split("^"); 
				if (wconv.length(result) != 0) {
	                if (wconv.toNumber(arrRst[2]) <= 2) {
	                    wprom.alert("조직은 팀/센터이후부터 선택하세요");
	                } else if (util.getGdsValue("gUserDuty") == "66" && (Ds_TEAM.getCellData(0, "sTeamCode") == arrRst[0])) {
	                    msk_sInqrKeyCd.setValue(arrRst[0]);
	                    edt_sInqrKeyValue.setValue(arrRst[3]);
	                    slevel = arrRst[2];
	                } else if (util.getGdsValue("gUserDuty") == "66" && (Ds_TEAM.getCellData(0, "sTeamCode") != arrRst[0])) {
	                    wprom.alert("대면총무인 경우, 본인 소속 지점을 선택해주세요.");
	                    msk_sInqrKeyCd.setValue("");
	                    edt_sInqrKeyValue.setValue("");
	                    slevel = "";
	                } else if (sAuthFlag == "Y" && (Ds_TEAM.getCellData(0, "sTeamCode") != arrRst[0])) {
	                    wprom.alert("직영TM 실장의 경우, 본인 소속 TM실을 선택해주세요.");
	                    msk_sInqrKeyCd.setValue("");
	                    edt_sInqrKeyValue.setValue("");
	                    slevel = "";
	                } else {
	                    msk_sInqrKeyCd.setValue(arrRst[0]);
	                    edt_sInqrKeyValue.setValue(arrRst[3]);
	                    slevel = arrRst[2];
	                }
	            }
			}
	    });
	}
};

//---------------------------------------------------------------------------
// function 명 : cmb_sInqrFlagCd_OnChanged
// 내용        : 조회구분값 변경시
//---------------------------------------------------------------------------
scwin.cmb_sInqrFlagCd_OnChanged = function(info) {
    //조회데이터 초기화
    wconv.clear(Ds_search);
    wconv.clear(DsWs_search);

    //조회구분 01계약자코드  02피보험자코드 03계약번호 04상담원
    var sInqrFlagCd = cmb_sInqrFlagCd.getValue();

    // 기존값 삭제
    msk_sInqrKeyCd.setValue("");
    edt_sInqrKeyValue.setValue("");
    msk_sInqrKeyCd.setDisabled(false);

    //마스크 변경
    if (sInqrFlagCd == "01") {
    	msk_sInqrKeyCd_Event.setIndex(0);
        //msk_sInqrKeyCd.setDisplayFormat("######-#ZZZZZZ");
          msk_sInqrKeyCd.setDisplayFormat("######-#######");
            msk_sInqrKeyCd.setPlaceholder("______-_______");
        btn_ObjtSerch.setDisabled(true);
    } else if (sInqrFlagCd == "02") {
    	msk_sInqrKeyCd_Event.setIndex(0);
        //msk_sInqrKeyCd.setDisplayFormat("######-#ZZZZZZ");
          msk_sInqrKeyCd.setDisplayFormat("######-#######");
            msk_sInqrKeyCd.setPlaceholder("______-_______");
        btn_ObjtSerch.setDisabled(true);
    } else if (sInqrFlagCd == "03") {
    	msk_sInqrKeyCd_Event.setIndex(1);
        //msk_sInqrKeyCd.setDisplayFormat("A-####-#######");
          msk_sInqrKeyCd.setDisplayFormat("#-####-#######");
            msk_sInqrKeyCd.setPlaceholder("_-____-_______");
        btn_ObjtSerch.setDisabled(false);
    } else if (sInqrFlagCd == "04") {
        if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35") {
            msk_sInqrKeyCd.setDisabled(true);
        }
        btn_ObjtSerch.setDisabled(false);
        msk_sInqrKeyCd_Event.setIndex(2);
        //msk_sInqrKeyCd.setDisplayFormat("@@@@@@@");
          msk_sInqrKeyCd.setDisplayFormat("#######");
            msk_sInqrKeyCd.setPlaceholder("_______");
        msk_sInqrKeyCd.setValue(util.getGdsValue("gDoer"));
        //scwin.lfn_msk_sInqrKeyCd_OnCharChanged(msk_sInqrKeyCd, msk_sInqrKeyCd.getValue(), msk_sInqrKeyCd.getValue());
        scwin.lfn_msk_sInqrKeyCd_OnCharChanged();
    } else if (sInqrFlagCd == "05") {
        btn_ObjtSerch.setDisabled(false);
        msk_sInqrKeyCd_Event.setIndex(2);
        //msk_sInqrKeyCd.setDisplayFormat("@@@@@@@");
          msk_sInqrKeyCd.setDisplayFormat("#######");
            msk_sInqrKeyCd.setPlaceholder("_______");
        msk_sInqrKeyCd.setValue(util.getGdsValue("gDoerUser"));
        //scwin.lfn_msk_sInqrKeyCd_OnCharChanged(msk_sInqrKeyCd, msk_sInqrKeyCd.getValue(), msk_sInqrKeyCd.getValue());
        scwin.lfn_msk_sInqrKeyCd_OnCharChanged();
    } else {
        btn_ObjtSerch.setDisabled(false);
        msk_sInqrKeyCd_Event.setIndex(2);
        //msk_sInqrKeyCd.setDisplayFormat("@@@@@@@");
          msk_sInqrKeyCd.setDisplayFormat("#######");
            msk_sInqrKeyCd.setPlaceholder("_______");
        msk_sInqrKeyCd.setValue("");
    }

    //기존 데이터 삭제
    //Ds_search.Clear();
    wconv.clear(Ds_LTIFA01CNT);
    msk_sInqrKeyCd.focus();
};

//---------------------------------------------------------------------------
// function 명 : cmb_sCrStat_OnChanged
// 내용        : 계약상태 변경시
//---------------------------------------------------------------------------
scwin.cmb_sCrStat_OnChanged = function(info) {
    //계약상태[99 전체, 00정상, 10 실효]
    if (info.newValue == "99") {
        //계약상태 : 전체 [진행상태 : 비활성화]
        cmb_sProgStat.setSelectedIndex(0);
        cmb_sProgStat.setDisabled(true);
    } else if (info.newValue == "10") {
        cmb_sProgStat.setSelectedIndex(0);
        cmb_sProgStat.setDisabled(true);
    } else if (info.newValue == "07") {
        cmb_sProgStat.setSelectedIndex(0);
        cmb_sProgStat.setDisabled(true);
    } else {
        cmb_sProgStat.setSelectedIndex(0);
        cmb_sProgStat.setDisabled(false);
    }
    //기존 데이터 삭제
    wconv.clear(Ds_search);
    wconv.clear(Ds_LTIFA01CNT);
};

//---------------------------------------------------------------------------
// function 명 : btn_ReviDlng_OnClick
// 내용        : 버튼클릭 : 부활처리 
//---------------------------------------------------------------------------
scwin.btn_ReviDlng_OnClick = function(obj) {
	wpron.alert("추후개발예정");
	return;
    wconv.copy(Ds_argsTemp, Ds_search);
    wconv.filter(Ds_argsTemp, "sSelYn=='1'");
    gds.gDs_LinkArg.removeAll();
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    /*수작업 변환 개발자 가이드(테스트 중)
    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sCrNo",
            "sCrNo",
            "text"
        ]
    ]);
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sScrnId",
            "sScrnId",
            "text"
        ]
    ]);
    gds.gDs_LinkArg.setCellData(0, "sCrNo", Ds_argsTemp.getCellData(0, "sCrNo"));
    gds.gDs_LinkArg.setCellData(0, "sScrnId", "D020");
    gfn_OpenLinkPage("E110");
};

//---------------------------------------------------------------------------
// function 명 : lfn_crntDate
// 내용        : 서버날짜 가져오기
//---------------------------------------------------------------------------
scwin.lfn_crntDate = function() {
    // 현재일
    gfn_getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
    sDate = wconv.mid(sysDate, 0, 8);
    sTime = wconv.mid(sysDate, 8, 6);
};

//---------------------------------------------------------------------------
// function 명 : lfn_OnCellClick
// 내용        : 그리드 클릭시 체크박스, 처리버튼 셋팅
//---------------------------------------------------------------------------
scwin.lfn_OnCellClick = function(nRow, nCell) {
    if (nCell == 0) {
        scwin.lfn_ChekBoxClick(nRow, nCell);
    } else if (nCell == 6) {
        scwin.lfn_CrNoClick(nRow, nCell);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_CrNoClick
// 내용        : 그리드의 계약번호 클릭 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_CrNoClick = function(nRow, nCell) {
    var sCrNo = Ds_search.getCellData(nRow, "sCrNo");

    //2015.01.23 (N1501-00121)
    if (gfn_chekAcesCntrAuth("DsWs_chekCrInqrAuth", "2", sCrNo) == false) {
        wprom.alert("접근 권한이 없습니다.");
        return;
    }

    //END (N1501-00121)
    if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", sCrNo) == false) {
        wprom.alert("조회권한이 없습니다.");
        return false;
    }

    var oDsJson=[{"sCrNo" : sCrNo, "sScrnId" : "F010"}];
	window.top.util.jSonDsCreate(oDsJson, "gDs_LinkArg");
	gfn_OpenLinkPage("CBSC028M");
    /*
    gds.gDs_LinkArg.removeAll();
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sCrNo",
            "sCrNo",
            "text"
        ]
    ]);
    util.insertColumn(gds.gDs_LinkArg, [
        [
            "sScrnId",
            "sScrnId",
            "text"
        ]
    ]);
    gds.gDs_LinkArg.setCellData(0, "sCrNo", sCrNo);
    gds.gDs_LinkArg.setCellData(0, "sScrnId", "F010");
    gfn_OpenLinkPage("D020");
    */
};

//---------------------------------------------------------------------------
// function 명 : lfn_chekBoxClick
// 내용        : 그리드의 선택 체크박스 클릭 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_ChekBoxClick = function(nRow, nCell) {
    //기존 체크박스 상태
    var sSelYnBefor = Ds_search.getCellData(nRow, "sSelYn");

    // 처리버튼 및 체크박스 초기화
    btn_ReviDlng.setDisabled(true);
    btn_CtnPremRecvDlng.setDisabled(true);
    var rowCnt = Ds_search.getTotalRow();
    for (i = 0; i < rowCnt; i++) {
        Ds_search.setCellData(i, "sSelYn", "0");
    }
    var sCrNo = Ds_search.getCellData(nRow, "sCrNo");
    if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", sCrNo) == false) {
        wprom.alert("조회권한이 없습니다.");
        return false;
    }
    if (nCell == 0 && ((sSelYnBefor == "0" || sSelYnBefor == null))) {
        // sCrStatCd 계약상태[00정상, 10 실효]
        var sCrStatCd = Ds_search.getCellData(nRow, "sCrStatCd");
        var sRecvProgStat = Ds_search.getCellData(nRow, "sRecvProgStat");
        if (sCrStatCd == "10") {
            //미납해지 부활처리 버튼 활성화
            Ds_search.setCellData(nRow, "sSelYn", "1");
            btn_ReviDlng.setDisabled(false);
            btn_CtnPremRecvDlng.setDisabled(true);
        } else if (sRecvProgStat == "3") {
            //수납대기
            wprom.alert("현재 수납대기 상태입니다.");
            btn_ReviDlng.setDisabled(true);
            btn_CtnPremRecvDlng.setDisabled(true);
        } else if (sRecvProgStat == "1" || sRecvProgStat == "4") {
            //청구중
            Ds_search.setCellData(nRow, "sSelYn", "1");
            btn_ReviDlng.setDisabled(true);
            btn_CtnPremRecvDlng.setDisabled(false);
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_msk_sInqrKeyCd_OnCharChanged
// 내용        : msk_sInqrKeyCd OnCharChanged 이벤트시
//---------------------------------------------------------------------------
scwin.lfn_msk_sInqrKeyCd_OnCharChanged = function() { //완료
    // 조회구분에 따른 입력값 형식 변경
    var inqrFlagCd = cmb_sInqrFlagCd.getValue();
    // 조회구분 키값
    var inqrFlagValue = msk_sInqrKeyCd.getValue();

    // 계약자코드
    if (inqrFlagCd == "01") {
        if (isBizRegNo(inqrFlagValue)) {
        	msk_sInqrKeyCd_Event.setIndex(0);
            //msk_sInqrKeyCd.setDisplayFormat("######-#ZZZZZZ");
              msk_sInqrKeyCd.setDisplayFormat("######-#######");
			    msk_sInqrKeyCd.setPlaceholder("______-_______");
            //계약자명조회
            scwin.lfn_SearchCstName(2, inqrFlagValue, edt_sInqrKeyValue, "계약자");
        } else {
        	msk_sInqrKeyCd_Event.setIndex(0);
            //msk_sInqrKeyCd.setDisplayFormat("######-#ZZZZZZ");
              msk_sInqrKeyCd.setDisplayFormat("######-#######");
			    msk_sInqrKeyCd.setPlaceholder("______-_______");
            //계약자명조회
            scwin.lfn_SearchCstName(1, inqrFlagValue, edt_sInqrKeyValue, "계약자");
        }
        return;
    } else if (inqrFlagCd == "02") {
        // 피보험자명 조회
        scwin.lfn_SearchCstName(1, inqrFlagValue, edt_sInqrKeyValue, "피보험자");
        return;
    } else if (inqrFlagCd == "03") {
        if (wconv.length(msk_sInqrKeyCd.getValue()) == 12) {
            //상품명 조회
            scwin.lfn_SearchPrd();
            return;
        } else {
            edt_sInqrKeyValue.setValue("");
        }
        return;
    } else if (inqrFlagCd == "04" || inqrFlagCd == "05") {
        //사원명
        //scwin.lfn_SearchEmpName(inqrFlagValue, edt_sInqrKeyValue.getValue());
        scwin.lfn_SearchEmpName(inqrFlagValue, edt_sInqrKeyValue);
    }
};

